import CIBER_MULTICALENDAR:MultiCalendar;

Function IScript_MultiCalendar()

   Local string &js, &css, &json, &template;
   Local string &parmStr = %Request.GetParameter("PAGELETID");

   &js = GetHTMLText(HTML.ANGULAR_BOOTSTRAP_CALENDAR_JS);
   &css = GetHTMLText(HTML.ANGULAR_BOOTSTRAP_CALENDAR_CSS);
   &json = GenerateScriptContentURL(%Portal, %Node, Record.WEBLIB_CIBERCAL, Field.ISCRIPT1, "FieldFormula", "IScript_MultiCalendar_JSON?PAGELETID=" | &parmStr | "&CBRDT=");
   &template = Clean(GetHTMLText(HTML.CIBERCAL_MODAL_HTML));
   %Response.Write(GetHTMLText(HTML.CIBERCAL_HTML, &js, &css, &json, &template));

End-Function;

Function IScript_MultiCalendar_JSON()

   Local string &parmStr = %Request.GetParameter("PAGELETID");
   Local CIBER_MULTICALENDAR:MultiCalendar &objMC = create CIBER_MULTICALENDAR:MultiCalendar(&parmStr);
   %Response.Write(&objMC.GetJSON());

End-Function;

