instance.peoplecode_text:
import CIBER_PTPPB_PAGELET:XMLDataSource:*;
import CIBER_PTPPB_PAGELET:Utility:JSON:*;

class MultiCalendar
   method MultiCalendar(&id_param As string);
   method GetJSON() Returns string;
   method GetXML() Returns string; /* for testing */

private
   instance string &pageletId;
   instance XmlDoc &XMLDoc;

   /* for use with angular js calendar */
   instance CIBER_PTPPB_PAGELET:Utility:JSON:JSON &Events;
   instance CIBER_PTPPB_PAGELET:Utility:JSON:JSONObject &OneEvent;

   /* for style sheet element name */
   instance number &X;
   Constant &TYPE = "typeColor";

   method addEvent();
   method addEventItem(&strKey As string, &strValue As string);
   method GetJSONfromQuery();
   method GetJSONfromConsCal();
   method GetNextType() Returns string;
   method FormatDateTimeText(&strDtTm As string) Returns string;

end-class;

method MultiCalendar
   /+ &id_param as String +/
   &pageletId = &id_param;
   &X = 1;

   Local CIBER_PTPPB_PAGELET:XMLDataSource:PageletWizardXML &pwObj = create CIBER_PTPPB_PAGELET:XMLDataSource:PageletWizardXML(&pageletId);
   &XMLDoc = &pwObj.GetXMLDoc();

end-method;

method GetXML
   /+ Returns String +/
   Return &XMLDoc.GenFormattedXmlString();
end-method;

method GetJSON
   /+ Returns String +/

   /*
	example angular js calendar event format JSON:
	[{
	"title": "Ajax event",
	"type": "warning",
	"startsAt": "2016-01-03T12:00:00.000",
	"endsAt": "2016-01-03T16:00:00.000"
	},
	{
	"title": "2nd Ajax event",
	"type": "information",
	"startsAt": "2016-02-03T12:00:00.000",
	"endsAt": "2016-02-03T16:00:00.000"
	}]
*/

   /* create json structures for calendar events */
   &Events = create CIBER_PTPPB_PAGELET:Utility:JSON:JSON("A");
   %This.GetJSONfromQuery();
   %This.GetJSONfromConsCal();
   Return &Events.getFinalString();

end-method;

method GetJSONfromQuery

   Local string &jsontype;
   Local number &i, &j, &k, &y;
   Local XmlNode &thisnode, &thatnode;
   Local array of string &aryKeyFieldNames, &aryKeyNames;
   Local array of XmlNode &ary_queryresult, &ary_queryrows_row, &ary_queryfields_field, &ary_querydata;

   &ary_queryresult = &XMLDoc.GetElementsByTagName("queryresult"); /* go thru all query pagelets */
   For &i = 1 To &ary_queryresult.Len
      &jsontype = %This.GetNextType();
      &ary_queryrows_row = &ary_queryresult [&i].FindNodes("queryrows/row");
      If &ary_queryrows_row.Len > 0 Then /* make sure query has rows */

         /* get key field name & key field */
         &ary_queryfields_field = &ary_queryresult [&i].FindNodes("queryfields/field");
         &aryKeyFieldNames = CreateArrayRept("", &ary_queryfields_field.Len);
         &aryKeyNames = CreateArrayRept("", &ary_queryfields_field.Len);
         For &j = 1 To &ary_queryfields_field.Len
            &aryKeyFieldNames [&j] = &ary_queryfields_field [&j].GetAttributeValue(1);
            &aryKeyNames [&j] = &ary_queryfields_field [&j].FindNode("headingtext").NodeValue;
         End-For;

         /* get actual key/value pair data */

         For &j = 1 To &ary_queryrows_row.Len
            For &k = 1 To &aryKeyFieldNames.Len
               %This.addEventItem(&aryKeyNames [&k], &ary_queryrows_row [&j].FindNode("querydata[@fieldname='" | &aryKeyFieldNames [&k] | "']/text").NodeValue);
            End-For;
            %This.addEventItem("type", &jsontype);
            %This.addEvent();
         End-For;
      Else
         %This.addEventItem("title", "placeholder");
         %This.addEventItem("type", &jsontype);
         %This.addEventItem("detail", "");
         %This.addEventItem("startsAt", "1980-01-01T12:00:00.000");
         %This.addEventItem("endsAt", "1980-01-01T13:00:00.000");
         %This.addEvent();
      End-If;
   End-For;

end-method;

method GetJSONfromConsCal

   /* 
	example calendar event xml format:
		<event allday="N" id="2101">
			<title>Test Event</title>
			<startdttm>2016-01-14-02.00.00.000000</startdttm>
			<enddttm>2016-01-14-03.00.00.000000</enddttm>
			<detail>Location: Tully Student Center</detail>
		</event>
*/
   Local string &jsontype;
   Local number &i, &j;
   Local array of XmlNode &ary_comm_cal_results, &ary_event;

   &ary_comm_cal_results = &XMLDoc.GetElementsByTagName("community-calendars-result"); /* go thru all calendar pagelets */
   For &i = 1 To &ary_comm_cal_results.Len
      &jsontype = %This.GetNextType();
      &ary_event = &ary_comm_cal_results [&i].GetElementsByTagName("event");
      If &ary_event.Len = 0 Then
         %This.addEventItem("title", "placeholder");
         %This.addEventItem("type", &jsontype);
         %This.addEventItem("detail", "");
         %This.addEventItem("startsAt", "1980-01-01T12:00:00.000");
         %This.addEventItem("endsAt", "1980-01-01T13:00:00.000");
         %This.addEvent();
      Else
         For &j = 1 To &ary_event.Len
            %This.addEventItem("title", &ary_event [&j].FindNode("title").NodeValue);
            %This.addEventItem("type", &jsontype);
            %This.addEventItem("detail", &ary_event [&j].FindNode("detail").NodeValue);
            %This.addEventItem("startsAt", %This.FormatDateTimeText(&ary_event [&j].FindNode("startdttm").NodeValue));
            %This.addEventItem("endsAt", %This.FormatDateTimeText(&ary_event [&j].FindNode("enddttm").NodeValue));
            %This.addEvent();
         End-For;
      End-If;
   End-For;

end-method;

method addEvent

   /*
	add event for angular js calendar
	*/

   Local CIBER_PTPPB_PAGELET:Utility:JSON:Value &tmpJSONVal = create CIBER_PTPPB_PAGELET:Utility:JSON:Value("O");
   &tmpJSONVal.JSONObject = &OneEvent;
   Local CIBER_PTPPB_PAGELET:Utility:JSON:Elements &tmpJSONElems = create CIBER_PTPPB_PAGELET:Utility:JSON:Elements(&tmpJSONVal);

   If &Events.BaseJSONArray.JSONElements <> Null Then
      &Events.BaseJSONArray.JSONElements.addElements(&tmpJSONElems);
   Else
      &Events.BaseJSONArray.JSONElements = &tmpJSONElems;
   End-If;

   &OneEvent = Null;
end-method;

method addEventItem
   /+ &strKey as String, +/
   /+ &strValue as String +/

   /*	add event item for angular js calendar */

   Local CIBER_PTPPB_PAGELET:Utility:JSON:Value &tmpValue = create CIBER_PTPPB_PAGELET:Utility:JSON:Value("S");
   &tmpValue.JSONString = &strValue;

   Local CIBER_PTPPB_PAGELET:Utility:JSON:Pair &tmpPair = create CIBER_PTPPB_PAGELET:Utility:JSON:Pair(&strKey, &tmpValue);
   Local CIBER_PTPPB_PAGELET:Utility:JSON:Members &tmpMembers = create CIBER_PTPPB_PAGELET:Utility:JSON:Members(&tmpPair);

   If &OneEvent = Null Then
      &OneEvent = create CIBER_PTPPB_PAGELET:Utility:JSON:JSONObject();
      &OneEvent.JSONMembers = &tmpMembers;
   Else
      If &OneEvent.JSONMembers <> Null Then
         &OneEvent.JSONMembers.addMembers(&tmpMembers);
      Else
         &OneEvent.JSONMembers = &tmpMembers;
      End-If;
   End-If;

end-method;

method GetNextType
   /+ Returns String +/
   Local string &str_ret = &TYPE | &X;
   &X = &X + 1;
   Return &str_ret;
end-method;

method FormatDateTimeText
   /+ &strDtTm as String +/
   /+ Returns String +/
   Return Substring(Replace(Replace(Replace(&strDtTm, 11, 1, "T"), 14, 1, ":"), 17, 1, ":"), 1, 23);

end-method;
