instance.peoplecode_text:
import PTPPB_PAGELET:UTILITY:*;
import PTPPB_PAGELET:PageletWizard;
import PTPPB_PAGELET:Pagelet;
import PTPPB_PAGELET:DataSource:*;
import PTPPB_PAGELET:EXCEPTION:*;

class MultiPageletDataSource extends PTPPB_PAGELET:DataSource:DataSource
   property array of PTPPB_PAGELET:UTILITY:Setting MultiPageletID readonly;

   method MultiPageletDataSource(&id_param As string);
   method execute() Returns string;
   method Clone() Returns object;
   method initializeSettings(&NewSettings As PTPPB_PAGELET:UTILITY:Collection);
   method processSettingsChange();
   method evaluateOutputFormat() Returns string;

private
   Constant &NumPagelets = 3;

end-class;

Declare Function GetPageletBuilderHTML PeopleCode FUNCLIB_PTPPB.HOMEPAGE FieldFormula;

method MultiPageletDataSource
   /+ &id_param as String +/
   %Super = create PTPPB_PAGELET:DataSource:DataSource(&id_param);

   %This.setObjectSubType("MULTIPAGELET");
   %This.setCanHaveParameters( True);

   %This.initializeSettings(%This.Settings);

end-method;

method execute
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.execute +/
   Local PTPPB_PAGELET:PageletWizard &tempObj = create PTPPB_PAGELET:PageletWizard();
   Local PTPPB_PAGELET:Pagelet &tempPagelet;
   Local XmlDoc &xmlDoc;
   Local XmlNode &xmlDocNode, &baseNode, &thisNode, &topNode;
   Local number &i;
   Local string &idName;

   &xmlDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8'?><multipagelet/>");

   &xmlDocNode = &xmlDoc.DocumentElement;
   &baseNode = &xmlDocNode.AddElement("multipageletproperties");
   &thisNode = &baseNode.AddElement("pageletid");
   &thisNode.NodeValue = %This.PageletID;

   &baseNode = &xmlDocNode.AddElement("pageletgroup");
   &baseNode.AddAttribute("numofpagelets", String(&NumPagelets));

   For &i = 1 To &NumPagelets

      &idName = %This.ParameterCollection.getItemByID("PageletID_" | &i).evaluatedValue();
      try
         &tempPagelet = &tempObj.getPageletByID(&idName, True);
      catch Exception &e1
         &topNode = &baseNode.AddElement("pagelet");
         &topNode.AddAttribute("pageletid", &idName);
         &thisNode = &topNode.AddElement("exception");
         &thisNode.NodeValue = &e1.ToString();

      end-try;
      If &tempPagelet <> Null Then
         &topNode = &baseNode.AddElement("pagelet");
         &topNode.AddAttribute("pageletid", &idName);
         &topNode.AddAttribute("title", &tempPagelet.Title);
         &thisNode = &topNode.AddCDataSection(GetPageletBuilderHTML(&idName));
      End-If;
   End-For;


   Return &xmlDoc.GenFormattedXmlString();
end-method;

method initializeSettings
   /+ &NewSettings as PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.initializeSettings +/
   Local PTPPB_PAGELET:UTILITY:Setting &thisSetting;
   Local number &i;

   If &NewSettings = Null Then
      &NewSettings = create PTPPB_PAGELET:UTILITY:Collection("MultiPageletDataSourceSettings");
   End-If;
   %This.setSettings(&NewSettings);

   &MultiPageletID = CreateArrayRept(&thisSetting, &NumPagelets);

   For &i = 1 To &NumPagelets
      &thisSetting = %This.Settings.getItemByID("PageletID_" | &i);
      If &thisSetting = Null Then
         &MultiPageletID [&i] = %This.createSettingProperty("PageletID_" | &i, "");
      Else
         &MultiPageletID [&i] = &thisSetting;
      End-If;

      &MultiPageletID [&i].Enabled = True;
      &MultiPageletID [&i].Visible = True;
      &MultiPageletID [&i].Required = False;
      &MultiPageletID [&i].RefreshOnChange = True;
      &MultiPageletID [&i].LongName = MsgGetText(263, 302, "(Message Not Found)Pagelet ID") | " #" | &i;
      &MultiPageletID [&i].EditType = &MultiPageletID [&i].EDITTYPE_PROMPTTABLE;
      &MultiPageletID [&i].PromptTable = "PTPPB_PAGELET";
      &MultiPageletID [&i].setObjectToRefreshOnValueChange(%This);

   End-For;

   /* turn off step 3 in the wizard */
   %This.setCanHaveParameters( False);

end-method;

/**
  * Any changes to settings will initiate a settings check and validation of setting
  * values.  If all settings are present and valid, then the list of OutputFields 
  * are assembled.
  * 
  * @Exception thrown if invalid settings or non-existent record name.
  */
method processSettingsChange
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.processSettingsChange +/
   Local PTPPB_PAGELET:UTILITY:Setting &PageletIDSetting;
   Local string &PageletIDValue;
   Local PTPPB_PAGELET:DataSource:DataSourceParameter &thisParam;
   Local PTPPB_PAGELET:UTILITY:Collection &Params;
   Local number &i;

   /* set settings incomplete and do nothing if no settings */
   If %This.Settings = Null Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &Params = %This.createEmptyParameterCollection();
   try
      For &i = 1 To &NumPagelets
         &PageletIDSetting = %This.Settings.getItemByID("PageletID_" | &i);
         /* set settings incomplete and do nothing if no setting */
         If &PageletIDSetting = Null Then
            %This.setSettingsComplete( False);
            Return;
         End-If;

         &PageletIDValue = &PageletIDSetting.Value;
         /* set settings incomplete and do nothing if no value */
         If &PageletIDValue = "" Then
            %This.setSettingsComplete( False);
            Return;
         End-If;

         If &PageletIDValue = %This.PageletID Then
            throw create PTPPB_PAGELET:EXCEPTION:PageletException(0, 0, "Don't use the same Pagelet Wizard ID");
         End-If;

         /* Add in the settings as internal parameters */
         &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter("PageletID_" | &i);
         &thisParam.Value = &MultiPageletID [&i].Value;
         &thisParam.UsageType = &thisParam.USAGETYPE_INTERNAL;
         &Params.Insert(&thisParam);

      End-For;
   catch Exception &e1
      WinMessage(&e1.ToString() | " asdf=" | &i);
      Return;
   end-try;
   /* Set the ParameterCollection to be immutable */
   &Params.setImmutable();

   /* Mark the settings as complete */
   %This.setSettingsComplete( True);

end-method;

/**
  * Returns an exact duplicate of this MultiPageletDataSource
  *
  * @return Object New copy of this MultiPageletDataSource
  */
method Clone
   /+ Returns Object +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.Clone +/
   Local CIBER_PTPPB_PAGELET:DataSource:MultiPageletDataSource &NewDS = create CIBER_PTPPB_PAGELET:DataSource:MultiPageletDataSource(%This.ID);
   &NewDS.PageletID = %This.PageletID;
   &NewDS.ParameterCollection = %This.ParameterCollection.Clone();
   &NewDS.initializeSettings(%This.Settings.Clone());
   Return &NewDS;
end-method;


method evaluateOutputFormat
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.evaluateOutputFormat +/
   %This.hasXPATHInfo = True;
   %This.BuilderContextNode = "/multipagelet/pageletgroup/pagelet";
   Return %This.OUTPUTFORMAT_OTHER;
end-method;

