/** 
  *  The HTML DataSource allows for the storage and retrieval of text or HTML
  *    for a pagelet.
  */
class MultiXMLDataSource extends PTPPB_PAGELET:DataSource:DataSource
   property PTPPB_PAGELET:UTILITY:Setting HTML readonly;

   method MultiXMLDataSource(&id_param As string);
   method execute() Returns string;
   method Clone() Returns object;
   method initializeSettings(&NewSettings As PTPPB_PAGELET:UTILITY:Collection);
   method processSettingsChange();
   method onPageletSave();
   method onPageletDelete();

private
   Constant &NumPagelets = 3;
end-class;


rem  --- for RTE HTML Process;
Declare Function WrapRTEHTML PeopleCode WEBLIB_PTRTE.ISCRIPT1 FieldFormula;


/**
  * Constructor
  */
method MultiXMLDataSource
   /+ &id_param as String +/
   %Super = create PTPPB_PAGELET:DataSource:DataSource(&id_param);
   %This.setObjectSubType("HTML");
   %This.setCanHaveParameters( True);
   %This.initializeSettings(%This.Settings);
   %This.allowGrouplet = True;
end-method;

/**
  * Returns the text or HTML for the pagelet
  *
  * @return string text or HTML
  */
method execute
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.execute +/

   Local string &strHTML, &temp;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;
   Local XmlDoc &xmlDoc, &xmlDocTmp;
   Local XmlNode &xmlDocNode, &baseNode, &thisNode;

   &strPageletID = %This.PageletID;

   If %This.Settings <> Null Then
      &strHTML = %This.Settings.getItemByID("HTML").Value;
   End-If;

   If &strHTML = "" Then
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &strHTML = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If All(&strHTML) Then
      try
         &xmlDoc = CreateXmlDoc(&strHTML);
      catch Exception &ex
         /*
         &strHTML = "<?xml version='1.0' encoding='UTF-8'?><!DOCTYPE xsl:stylesheet [<!ENTITY nbsp '&#160;'>]>" | WrapRTEHTML(&strHTML);
         */
         &temp = "<?xml version='1.0' encoding='UTF-8'?>" | GetHTMLText(HTML.PTPPB_XHTML_SPECIAL_CHARACTERS) | WrapRTEHTML(&strHTML);
         try
            &xmlDoc = CreateXmlDoc(&temp);
            &strHTML = &temp;
         catch Exception &ex1
            &strHTML = WrapRTEHTML(&strHTML);
         end-try;
      end-try;
   End-If;

   &xmlDocNode = &xmlDoc.DocumentElement;
   &baseNode = &xmlDocNode.AddElement("multipageletxml");

   Local number &i;
   Local PTPPB_PAGELET:PageletWizard &objPW;
   Local PTPPB_PAGELET:Pagelet &objPagelet;
   Local PTPPB_PAGELET:DataSource:DataSource &objDS;

   For &i = 1 To &NumPagelets
      If %This.ParameterCollection.getItemByID(".PAGELETID" | &i).Value <> "" Then
         &thisNode = &baseNode.AddElement("pageletxml");

         try
            &objPW = create PTPPB_PAGELET:PageletWizard();
            &objPagelet = &objPW.getPageletByID(%This.ParameterCollection.getItemByID(".PAGELETID" | &i).Value, True);
            &objPagelet.fullDataSourceLoad();
            &objPagelet.PopulateInputsForHPPagelet();
            &objDS = &objPagelet.DataSource;

            If Not &objPagelet.Authorized Then
               &thisNode = &thisNode.AddElement("Exception");
               &thisNode.NodeValue = "User " | %UserId | " not authorized to execute pagelet";
            Else
               &xmlDocTmp = CreateXmlDoc("");
               If &xmlDocTmp.ParseXmlString(&objDS.execute()) Then
                  &thisNode.AddAttribute("Title", &objPagelet.Title);
                  &thisNode.AddAttribute("PORTAL_OBJNAME", &objPagelet.ID);
                  &thisNode.AddAttribute("Description", &objPagelet.Descr);
                  &thisNode.AddAttribute("PageletWizardId", ".PAGELETID" | &i);
                  &thisNode.AddNode(&xmlDocTmp.DocumentElement);
               Else
                  &thisNode = &thisNode.AddElement("Exception");
                  &thisNode.NodeValue = "Pagelet Wizard XML not valid";

               End-If;
            End-If;

         catch Exception &e1
            &thisNode = &thisNode.AddElement("Exception");
            &thisNode.NodeValue = &e1.ToString();

         end-try;

         &objPagelet = Null;
      End-If;

   End-For;
   Return &xmlDoc.GenFormattedXmlString();
   rem Return &strHTML;

end-method;

/**
  * Initialize the HTML Setting and retrieve from the DataBase if appropriate for the pagelet.
  *
  * @param NewSettings if null, a new collection of settings is created.
  */
method initializeSettings
   /+ &NewSettings as PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.initializeSettings +/
   Local PTPPB_PAGELET:UTILITY:Setting &thisSetting;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If &NewSettings = Null Then
      &NewSettings = create PTPPB_PAGELET:UTILITY:Collection("HTMLDataSourceSettings");
   End-If;

   %This.setSettings(&NewSettings);
   &thisSetting = %This.Settings.getItemByID("HTML");

   If &thisSetting = Null Then
      &HTML = %This.createSettingProperty("HTML", "");
   Else
      &HTML = &thisSetting;
   End-If;

   If &HTML.Value = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &HTML.Value = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If &HTML.Value <> "" Then
      %This.setSettingsComplete( True);
   End-If;

   &HTML.FieldType = &HTML.FIELDTYPE_LONGCHARACTER;
   &HTML.EditType = &HTML.EDITTYPE_NOTABLEEDIT;
   &HTML.Enabled = True;
   &HTML.Visible = True;
   &HTML.Required = True;
   &HTML.LongName = MsgGetText(219, 602, "Message Not Found");

   &HTML.setObjectToRefreshOnValueChange(%This);
end-method;

/**
  * Determines if any text/HTML has been set.  If so, the settins are marked complete.
  */
method processSettingsChange
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.processSettingsChange +/;

   Local string &strHTMLText;
   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;

   Local PTPPB_PAGELET:DataSource:DataSourceParameter &thisParam;
   Local PTPPB_PAGELET:UTILITY:Collection &Params;

   Local number &i;

   /* do nothing if no settings */
   If %This.Settings = Null Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &HTMLSetting = %This.Settings.getItemByID("HTML");
   /* do nothing if no queryname setting */
   If &HTMLSetting = Null Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return
   End-If;

   &strHTMLText = &HTMLSetting.Value;
   /* do nothing if the HTML is blank */
   If &strHTMLText = "" Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &Params = %This.createEmptyParameterCollection();

   For &i = 1 To &NumPagelets
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".PAGELETID" | &i);
      &thisParam.LongName = "Pagelet ID " | &i;
      &thisParam.DefaultValue = "";
      REM   &thisParam.Required = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &Params.Insert(&thisParam);
   End-For;

   &Params.setImmutable();

   /* Mark the settings as complete */
   %This.setSettingsComplete( True);

end-method;

/**
  * Clones this datasource
  *
  * @return Object exact duplicate of this HTMLDataSource
  */
method Clone
   /+ Returns Object +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.Clone +/
   Local CIBER_PTPPB_PAGELET:DataSource:MultiXMLDataSource &NewDS = create CIBER_PTPPB_PAGELET:DataSource:MultiXMLDataSource(%This.ID);
   &NewDS.PageletID = %This.PageletID;
   &NewDS.initializeSettings(%This.Settings.Clone());
   Return &NewDS;
end-method;

/**
  * Executes when the pagelet is saved.  Saves the text/HTML to the HTML tables.
  */
method onPageletSave
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletSave +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

   If %This.SettingsComplete Then
      REM We actually have some settings to save... ;

      &HTMLSetting = %This.Settings.getItemByID("HTML");
      &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
      &REC_PTPPB_PGLT_HTML.DESCR.Value = %This.Descr;
      &REC_PTPPB_PGLT_HTML.HTMLAREA.Value = &HTMLSetting.Value;
      &REC_PTPPB_PGLT_HTML.LASTUPDDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.LASTUPDOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.CREATEDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.CREATEOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.Insert();

   End-If;
end-method;

/**
  * Executes when the pagelet is deleted.  Deletes the text/HTML from the HTML tables.
  */
method onPageletDelete
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletDelete +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

end-method;

