import PTPPB_PAGELET:EXCEPTION:*;

/**
  * Performs XSL Tranformations.
  */
class XSL2Transformer extends PTPPB_PAGELET:Transformer:Transformer
   method XSL2Transformer(&ID_param As string);
   method getParameterCollection() Returns PTPPB_PAGELET:UTILITY:Collection;
   method execute(&NameSpaceID As string) Returns string;
   method Clone() Returns PTPPB_PAGELET:Transformer:Transformer;

end-class;

/** 
  * Constructor.
  *
  * @param id_param ID of this object.  Should be unique.
  */
method XSL2Transformer
   /+ &ID_param as String +/
   %Super = create PTPPB_PAGELET:Transformer:Transformer(&ID_param);
end-method;

/**
  * Returns the current parameter collection, or a new collection with a single "XSL" parameter if null.
  * 
  * @return Collection The parameter collection.
  */
method getParameterCollection
   /+ Returns PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:Transformer:Transformer.getParameterCollection +/

   If %This.ParameterCollection <> Null Then
      Return %This.ParameterCollection;
   End-If;

   %This.ParameterCollection = create PTPPB_PAGELET:UTILITY:Collection("ParameterCollection");

   Local PTPPB_PAGELET:UTILITY:Parameter &newParam;
   /* add the XSL parameter */
   &newParam = create PTPPB_PAGELET:UTILITY:Parameter("XSL");
   &newParam.Name = "XSL";
   %This.ParameterCollection.Insert(&newParam);
   %This.ParameterCollection.setImmutable();
   Return %This.ParameterCollection;

end-method;

/**
  * Perform the transformation.
  *
  * @param pageletID ID of the pagelet being executed.
  * @return String Transformed data.
  */
method execute
   /+ &NameSpaceID as String +/
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:Transformer:Transformer.execute +/

   Local string &sXML = %This.DataToTransform.Value;
   If &sXML = "" Then
      throw create PTPPB_PAGELET:EXCEPTION:InvalidValueException("XML", "");
   End-If;

   Local string &sXSL = %This.ParameterCollection.getItemByID("XSL").Value;
   If &sXSL = "" Then
      throw create PTPPB_PAGELET:EXCEPTION:InvalidValueException("XSL", "");
   End-If;

   /* Look for the special tag for inserting the namespaceid */
   /* pageletid is a synonym for namespaceid - backward compatability */
   &sXSL = Substitute(&sXSL, "<PS_NAMESPACEID/>", &NameSpaceID);
   &sXSL = Substitute(&sXSL, "<PS_PAGLETID/>", &NameSpaceID);

   /* substituting the &apos; entity with single quot */
   try
      Local JavaObject &JObjXML, &JObjXSL, &JObjTest;
      &JObjXML = CreateJavaObject("java.lang.String", &sXML);
      &JObjXSL = CreateJavaObject("java.lang.String", &sXSL);
      &JObjTest = GetJavaClass("XSL2Transform");
      Local string &RetString = &JObjTest.transformXSL2(&JObjXML, &JObjXSL);
      &JObjXML = Null;
      &JObjXSL = Null;
      &JObjTest = Null;

   catch Exception &ex
      throw create PTPPB_PAGELET:EXCEPTION:XSLTransformationException();
   end-try;
   /* &RetString = Substitute(&RetString, "&apos;", "'"); */
   Return &RetString;

end-method;

/**
  * Make an exact copy of this object.
  *
  * @return Text Exact copy of this object
  */
method Clone
   /+ Returns PTPPB_PAGELET:Transformer:Transformer +/
   /+ Extends/implements PTPPB_PAGELET:Transformer:Transformer.Clone +/
   Local number &i;
   Local PTPPB_PAGELET:UTILITY:Parameter &param;
   Local CIBER_PTPPB_PAGELET:Transformer:XSL2Transformer &NewTransformer = create CIBER_PTPPB_PAGELET:Transformer:XSL2Transformer(%This.ID);
   If %This.DataToTransform <> Null Then
      &NewTransformer.DataToTransform = %This.DataToTransform.Clone();
      &NewTransformer.ParameterCollection = %This.ParameterCollection.Clone();
   End-If;
   Return &NewTransformer;
end-method;

import PTPPB_PAGELET:PageletWizard;
import PTPPB_PAGELET:Pagelet;
import PTPPB_PAGELET:DataSource:DataSource;

class PageletWizardXML
   method PageletWizardXML(&id_param As string);
   method GetXMLDoc() Returns XmlDoc;

   property string pageletid;
   property XmlDoc pwXMLDoc;

end-class;

method PageletWizardXML
   /+ &id_param as String +/
   &pageletid = &id_param;
end-method;

method GetXMLDoc
   /+ Returns XmlDoc +/
   Local string &tmpStr;
   Local XmlDoc &xmlDoc;
   Local XmlNode &xmlDocNode, &baseNode, &thisNode;

   &xmlDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><pageletwizardxml/>");

   try
      Local PTPPB_PAGELET:PageletWizard &objPW = create PTPPB_PAGELET:PageletWizard();
      Local PTPPB_PAGELET:Pagelet &objPagelet = &objPW.getPageletByID(&pageletid, True);

      If Not &objPagelet.Authorized Then
         &xmlDocNode = &xmlDoc.DocumentElement;
         &thisNode = &xmlDocNode.AddElement("Exception");
         &thisNode.NodeValue = "User " | %UserId | " not authorized to execute pagelet";
      Else
         &objPagelet.fullDataSourceLoad();
         Local PTPPB_PAGELET:DataSource:DataSource &objDS = &objPagelet.DataSource;
         &tmpStr = &objDS.execute();

         If Not &xmlDoc.ParseXmlString(&tmpStr) Then
            &xmlDocNode = &xmlDoc.DocumentElement;
            &thisNode = &xmlDocNode.AddElement("Exception");
            &thisNode.NodeValue = "Pagelet Wizard XML did not parse correctly";
         End-If;

      End-If;
   catch Exception &e1
      &xmlDocNode = &xmlDoc.DocumentElement;
      &thisNode = &xmlDocNode.AddElement("Exception");
      &thisNode.NodeValue = &e1.ToString();
   end-try;

   Return &xmlDoc;

end-method;
