/**
  * Executes when the pagelet is saved.  Saves the URL for later use.
  */
/* The URL is saved in a separate table because Pagelet Wizard's default tables for storing */
/*   parameters will often end up truncating the BaseURL.  Note that only the Base part of the */
/*   URL is saved in the table.  Any query string parameters (defined as pagelet parameters) are */
/*   stored automatically by pagelet wizard.  This is OK since paramater values are generally not */
/*   very long. */
method onPageletSave
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletSave +/

   Local PTPPB_PAGELET:UTILITY:Setting &URLSetting;
   Local Record &REC_PTPPB_PGLT_URL;

   &REC_PTPPB_PGLT_URL = CreateRecord(Record.PTPPB_PGLT_URL);
   &REC_PTPPB_PGLT_URL.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_URL.Delete();

   If %This.SettingsComplete Then
      REM We actually have some settings to save... ;

      &URLSetting = %This.Settings.getItemByID("URL");
      &REC_PTPPB_PGLT_URL.PTPPB_PAGELET_ID.Value = %This.PageletID;
      rem &REC_PTPPB_PGLT_URL.HTMLAREA.Value = &URLSetting.Value;
      &REC_PTPPB_PGLT_URL.HTMLAREA.Value = %This.getFullURL();
      &REC_PTPPB_PGLT_URL.LASTUPDDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_URL.LASTUPDOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_URL.CREATEDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_URL.CREATEOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_URL.Insert();
   End-If;
end-method;

/**
  * Executes when the pagelet is deleted.  Deletes the URL from the URL table.
  */
method onPageletDelete
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletDelete +/

   Local PTPPB_PAGELET:UTILITY:Setting &URLSetting;
   Local Record &REC_PTPPB_PGLT_URL;

   &REC_PTPPB_PGLT_URL = CreateRecord(Record.PTPPB_PGLT_URL);
   &REC_PTPPB_PGLT_URL.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_URL.Delete();

end-method;

method getFullURL
   /+ Returns String +/
   Local string &URLNode, &recName, &fldName, &pcodeEvntName, &pcodeFuncName, &targetPortal, &targetNode, &AddParams, &moreParams;
   Local string &returnURL;

   If %This.ParameterCollection <> Null Then

      &URLNode = %This.ParameterCollection.getItemByID(".NODE_NAME").VALUE;
      &recName = %This.ParameterCollection.getItemByID(".RECORD_NAME").VALUE;
      &fldName = %This.ParameterCollection.getItemByID(".FIELD_NAME").value;
      &pcodeEvntName = %This.ParameterCollection.getItemByID(".PCODE_EVENT_NAME").value;
      &pcodeFuncName = %This.ParameterCollection.getItemByID(".PCODE_FUNC_NAME").value;
      &targetPortal = %This.ParameterCollection.getItemByID(".TARGET_PORTAL").value;
      &targetNode = %This.ParameterCollection.getItemByID(".TARGET_NODE").value;
      &AddParams = %This.ParameterCollection.getItemByID(".ADD_PARAMS").value;

      &moreParams = %This.moreParameters;
      If All(&AddParams) Or
            All(&moreParams) Then
         &AddParams = "?" | &AddParams | "&" | &moreParams;
      End-If;

      SQLExec("SELECT URI_TEXT FROM PSNODEURITEXT WHERE URI_TYPE = 'CN' AND MSGNODENAME = :1", &URLNode, &returnURL);

      If None(&returnURL) Then
         &returnURL = "NODE_NAME: " | &URLNode | " does not have it's URI defined. Please correct";
      Else
         &returnURL = &returnURL | &targetPortal | "/" | &targetNode | "/s/" | &recName | "." | &fldName | "." | &pcodeEvntName | "." | &pcodeFuncName | &AddParams;
      End-If;
   Else
      &returnURL = "No parameters defined to create URL";
   End-If;

   Return &returnURL;

end-method;

method isNodeURLLocal
   /+ Returns Boolean +/

   Local string &URLNode, &URLstr, &compURLstr;
   Local number &len;

   &URLNode = %This.ParameterCollection.getItemByID(".NODE_NAME").VALUE;
   SQLExec("SELECT URI_TEXT FROM PSNODEURITEXT WHERE URI_TYPE = 'CN' AND MSGNODENAME = :1", &URLNode, &URLstr);

   &len = Len(%Request.ContentURI);

   If Substring(&URLstr, 0, &len) = %Request.ContentURI Then
      Return True;
   Else
      Return False;
   End-If;


end-method;

import PTPPB_PAGELET:UTILITY:*;
import PTPPB_PAGELET:Transformer:*;
import PTPPB_PAGELET:EXCEPTION:*;

/**
  * Performs XSL Tranformations.
  */
class XSL2Transformer extends PTPPB_PAGELET:Transformer:Transformer
   method XSL2Transformer(&ID_param As string);
   method getParameterCollection() Returns PTPPB_PAGELET:UTILITY:Collection;
   method execute(&NameSpaceID As string) Returns string;
   method Clone() Returns PTPPB_PAGELET:Transformer:Transformer;

end-class;

/** 
  * Constructor.
  *
  * @param id_param ID of this object.  Should be unique.
  */
method XSL2Transformer
   /+ &ID_param as String +/
   %Super = create PTPPB_PAGELET:Transformer:Transformer(&ID_param);
end-method;

/**
  * Returns the current parameter collection, or a new collection with a single "XSL" parameter if null.
  * 
  * @return Collection The parameter collection.
  */
method getParameterCollection
   /+ Returns PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:Transformer:Transformer.getParameterCollection +/

   If %This.ParameterCollection <> Null Then
      Return %This.ParameterCollection;
   End-If;

   %This.ParameterCollection = create PTPPB_PAGELET:UTILITY:Collection("ParameterCollection");

   Local PTPPB_PAGELET:UTILITY:Parameter &newParam;
   /* add the XSL parameter */
   &newParam = create PTPPB_PAGELET:UTILITY:Parameter("XSL");
   &newParam.Name = "XSL";
   %This.ParameterCollection.Insert(&newParam);
   %This.ParameterCollection.setImmutable();
   Return %This.ParameterCollection;

end-method;

/**
  * Perform the transformation.
  *
  * @param pageletID ID of the pagelet being executed.
  * @return String Transformed data.
  */
method execute
   /+ &NameSpaceID as String +/
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:Transformer:Transformer.execute +/

   Local string &sXML = %This.DataToTransform.Value;
   If &sXML = "" Then
      throw create PTPPB_PAGELET:EXCEPTION:InvalidValueException("XML", "");
   End-If;

   Local string &sXSL = %This.ParameterCollection.getItemByID("XSL").Value;
   If &sXSL = "" Then
      throw create PTPPB_PAGELET:EXCEPTION:InvalidValueException("XSL", "");
   End-If;

   /* Look for the special tag for inserting the namespaceid */
   /* pageletid is a synonym for namespaceid - backward compatability */
   &sXSL = Substitute(&sXSL, "<PS_NAMESPACEID/>", &NameSpaceID);
   &sXSL = Substitute(&sXSL, "<PS_PAGLETID/>", &NameSpaceID);

   /* substituting the &apos; entity with single quot */
   try
      Local JavaObject &JObjXML, &JObjXSL, &JObjTest;
      &JObjXML = CreateJavaObject("java.lang.String", &sXML);
      &JObjXSL = CreateJavaObject("java.lang.String", &sXSL);
      &JObjTest = GetJavaClass("XSL2Transform");
      Local string &RetString = &JObjTest.transformXSL2(&JObjXML, &JObjXSL);
      &JObjXML = Null;
      &JObjXSL = Null;
      &JObjTest = Null;

   catch Exception &ex
      throw create PTPPB_PAGELET:EXCEPTION:XSLTransformationException();
   end-try;
   /* &RetString = Substitute(&RetString, "&apos;", "'"); */
   Return &RetString;

end-method;

/**
  * Make an exact copy of this object.
  *
  * @return Text Exact copy of this object
  */
method Clone
   /+ Returns PTPPB_PAGELET:Transformer:Transformer +/
   /+ Extends/implements PTPPB_PAGELET:Transformer:Transformer.Clone +/
   Local number &i;
   Local PTPPB_PAGELET:UTILITY:Parameter &param;
   Local CIBER_PTPPB_PAGELET:Transformer:XSL2Transformer &NewTransformer = create CIBER_PTPPB_PAGELET:Transformer:XSL2Transformer(%This.ID);
   If %This.DataToTransform <> Null Then
      &NewTransformer.DataToTransform = %This.DataToTransform.Clone();
      &NewTransformer.ParameterCollection = %This.ParameterCollection.Clone();
   End-If;
   Return &NewTransformer;
end-method;

import PTFP_FEED:UTILITY:Utility;
import EPPCA_CALENDARS:Utility:Month;
import CIBER_PTPPB_PAGELET:XMLDataSource:PageletWizardXML;

class EventsXML
   method EventsXML(&id_param As string);
   method GetXMLDoc(&inputDate As date, &periodLength As string) Returns XmlDoc;

   property string pageletid;

private
   method GetDateRangeXML(&inputXMLDoc As XmlDoc, &startDt As date, &endDt As date) Returns XmlDoc;
   method GetStartEndDateforWeek(&inputDate As date, &outputStateDate As date out, &outputEndDate As date out);
   method GetStartEndDateforMonth(&inputDate As date, &outputStateDate As date out, &outputEndDate As date out);
   method GetXPathWithDateField(&inputFldName As string) Returns string;

   instance string &datefield_xpath;
   instance string &rows_xpath;
   instance string &attr_name_numrows;
   instance string &attr_name_rownum;
   instance string &datefieldvalue_xpath;
   instance string &datefieldvalue_xpath_attr;
   instance string &datefieldvalue_xpath_element;

end-class;

method EventsXML
   /+ &id_param as String +/
   &pageletid = &id_param;

   &datefield_xpath = "/queryresult/queryproperties/datefield";
   &rows_xpath = "/queryresult/queryrows";
   &attr_name_numrows = "numrows";
   &attr_name_rownum = "rownumber";
   &datefieldvalue_xpath = "querydata";
   &datefieldvalue_xpath_attr = "fieldname";
   &datefieldvalue_xpath_element = "text";
end-method;

method GetXMLDoc
   /+ &inputDate as Date, +/
   /+ &periodLength as String +/
   /+ Returns XmlDoc +/
   Local CIBER_PTPPB_PAGELET:XMLDataSource:PageletWizardXML &objPageletWizXML = create CIBER_PTPPB_PAGELET:XMLDataSource:PageletWizardXML(&pageletid);
   Local XmlDoc &xmlDoc = &objPageletWizXML.GetXMLDoc();
   Local XmlNode &baseNode, &thisNode, &thatNode;

   &thisNode = &xmlDoc.DocumentElement.FindNode("/pageletwizardxml/Exception");

   If &thisNode.IsNull Then
      Local date &startDate, &endDate;

      Evaluate &periodLength
      When "WK"
         %This.GetStartEndDateforWeek(&inputDate, &startDate, &endDate);
      When "MN"
         %This.GetStartEndDateforMonth(&inputDate, &startDate, &endDate);
      When-Other
         &startDate = &inputDate;
         &endDate = &inputDate;
      End-Evaluate;

      &xmlDoc = %This.GetDateRangeXML(&xmlDoc, &startDate, &endDate);
   End-If;

   Return &xmlDoc;
end-method;

method GetDateRangeXML
   /+ &inputXMLDoc as XmlDoc, +/
   /+ &startDt as Date, +/
   /+ &endDt as Date +/
   /+ Returns XmlDoc +/

   Local PTFP_FEED:UTILITY:Utility &objUtil = create PTFP_FEED:UTILITY:Utility();
   Local XmlDoc &retXMLDoc = CreateXmlDoc("");
   Local XmlNode &baseNode, &thisNode, &thatNode, &retNode;
   Local number &i, &j, &rowCount, &fieldCount, &updatedRowCnt;
   Local string &datefieldname, &tmp;
   Local date &evDate;

   Local boolean &bool = &retXMLDoc.ParseXmlString(&inputXMLDoc.GenFormattedXmlString());
   &datefieldname = &retXMLDoc.DocumentElement.FindNode(&datefield_xpath).NodeValue;
   &retNode = &retXMLDoc.DocumentElement.FindNode(&rows_xpath);
   &retNode.RemoveAllChildNode();

   &baseNode = &inputXMLDoc.DocumentElement.FindNode(&rows_xpath); /* get queryrows element */
   &rowCount = Value(&baseNode.GetAttributeValue(&attr_name_numrows));
   &updatedRowCnt = 0;

   For &i = 1 To &rowCount
      &thisNode = &baseNode.GetChildNode(&i); /* get row element */
      rem      &thatNode = &thisNode.FindNode("querydata[@fieldname='" | &datefieldname | "']");
      &thatNode = &thisNode.FindNode(%This.GetXPathWithDateField(&datefieldname));

      rem &evDate = Date(Value(Substitute(Substring(&thatNode.FindNode(&datefieldvalue_xpath_element).NodeValue, 1, 10), "-", ""))); /* careful as there may be other formats to convert */
      &evDate = DatePart(&objUtil.stringToDatetime(&thatNode.FindNode(&datefieldvalue_xpath_element).NodeValue));

      /* compare if date is in between start date and end date */
      If Days365(&startDt, &evDate) >= 0 And
            Days365(&evDate, &endDt) >= 0 Then

         &updatedRowCnt = &updatedRowCnt + 1;

         /* update rownumber attr */
         &thisNode.AddAttribute(&attr_name_rownum, String(&updatedRowCnt));

         /* if in between dates, then mark this node to be copied to new xmldoc */
         &retNode.AddNode(&thisNode);


         rem         &thatNode.GetChildNode(1).NodeValue = "evDate: " | &evDate | " startDt: " | &startDt | "  enddt: " | &endDt | "    " | String(Days365(&evDate, &endDt)) | " row #: " | &i | " count:" | &updatedRowCnt;
         rem         &retXMLDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><pageletwizardxml/>");
         rem         &retXMLDoc.DocumentElement.AddNode(&thatNode.GetChildNode(1));
      End-If;

   End-For;

   /* update queryrows attribute "numrows" */
   &retNode.AddAttribute(&attr_name_numrows, String(&updatedRowCnt));

   Return &retXMLDoc;
end-method;

method GetXPathWithDateField
   /+ &inputFldName as String +/
   /+ Returns String +/

   Return &datefieldvalue_xpath | "[@" | &datefieldvalue_xpath_attr | "='" | &inputFldName | "']";
end-method;

method GetStartEndDateforWeek
   /+ &inputDate as Date, +/
   /+ &outputStateDate as Date out, +/
   /+ &outputEndDate as Date out +/

   Local number &nWeekday = Weekday(&inputDate);
   Local number &nOption = Value(GetUserOption("PPTL", "WEEKFIRSTDAY"));

   &outputStateDate = AddToDate(&inputDate, 0, 0, 0 - (&nWeekday - (&nOption + 1)));
   &outputEndDate = AddToDate(&outputStateDate, 0, 0, 6);

end-method;

method GetStartEndDateforMonth
   /+ &inputDate as Date, +/
   /+ &outputStateDate as Date out, +/
   /+ &outputEndDate as Date out +/
   Local EPPCA_CALENDARS:Utility:Month &objMonth;

   &outputStateDate = Date3(Year(&inputDate), Month(&inputDate), 1);

   &objMonth = create EPPCA_CALENDARS:Utility:Month(Year(&inputDate), Month(&inputDate));

   &outputEndDate = AddToDate(&outputStateDate, 0, 0, &objMonth.DaysinMonth - 1);

end-method;

import PTPPB_PAGELET:UTILITY:*;
import PTPPB_PAGELET:DataSource:*;
import PTPPB_PAGELET:EXCEPTION:*;

import PTPPB_PAGELET:PageletWizard:*;
import PTPPB_PAGELET:Pagelet:*;

/** 
  *  The HTML DataSource allows for the storage and retrieval of text or HTML
  *    for a pagelet.
  */
class MultiXMLDataSource extends PTPPB_PAGELET:DataSource:DataSource
   property PTPPB_PAGELET:UTILITY:Setting HTML readonly;

   method MultiXMLDataSource(&id_param As string);
   method execute() Returns string;
   method Clone() Returns object;
   method initializeSettings(&NewSettings As PTPPB_PAGELET:UTILITY:Collection);
   method processSettingsChange();
   method onPageletSave();
   method onPageletDelete();

private
   Constant &NumPagelets = 3;
end-class;


rem  --- for RTE HTML Process;
Declare Function WrapRTEHTML PeopleCode WEBLIB_PTRTE.ISCRIPT1 FieldFormula;


/**
  * Constructor
  */
method MultiXMLDataSource
   /+ &id_param as String +/
   %Super = create PTPPB_PAGELET:DataSource:DataSource(&id_param);
   %This.setObjectSubType("HTML");
   %This.setCanHaveParameters( True);
   %This.initializeSettings(%This.Settings);
   %This.allowGrouplet = True;
end-method;

/**
  * Returns the text or HTML for the pagelet
  *
  * @return string text or HTML
  */
method execute
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.execute +/

   Local string &strHTML, &temp;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;
   Local XmlDoc &xmlDoc, &xmlDocTmp;
   Local XmlNode &xmlDocNode, &baseNode, &thisNode;

   &strPageletID = %This.PageletID;

   If %This.Settings <> Null Then
      &strHTML = %This.Settings.getItemByID("HTML").Value;
   End-If;

   If &strHTML = "" Then
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &strHTML = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If All(&strHTML) Then
      try
         &xmlDoc = CreateXmlDoc(&strHTML);
      catch Exception &ex
         /*
         &strHTML = "<?xml version='1.0' encoding='UTF-8'?><!DOCTYPE xsl:stylesheet [<!ENTITY nbsp '&#160;'>]>" | WrapRTEHTML(&strHTML);
         */
         &temp = "<?xml version='1.0' encoding='UTF-8'?>" | GetHTMLText(HTML.PTPPB_XHTML_SPECIAL_CHARACTERS) | WrapRTEHTML(&strHTML);
         try
            &xmlDoc = CreateXmlDoc(&temp);
            &strHTML = &temp;
         catch Exception &ex1
            &strHTML = WrapRTEHTML(&strHTML);
         end-try;
      end-try;
   End-If;

   &xmlDocNode = &xmlDoc.DocumentElement;
   &baseNode = &xmlDocNode.AddElement("multipageletxml");

   Local number &i;
   Local PTPPB_PAGELET:PageletWizard &objPW;
   Local PTPPB_PAGELET:Pagelet &objPagelet;
   Local PTPPB_PAGELET:DataSource:DataSource &objDS;

   For &i = 1 To &NumPagelets
      If %This.ParameterCollection.getItemByID(".PAGELETID" | &i).Value <> "" Then
         &thisNode = &baseNode.AddElement("pageletxml");

         try
            &objPW = create PTPPB_PAGELET:PageletWizard();
            &objPagelet = &objPW.getPageletByID(%This.ParameterCollection.getItemByID(".PAGELETID" | &i).Value, True);
            &objPagelet.fullDataSourceLoad();
            &objPagelet.PopulateInputsForHPPagelet();
            &objDS = &objPagelet.DataSource;

            If Not &objPagelet.Authorized Then
               &thisNode = &thisNode.AddElement("Exception");
               &thisNode.NodeValue = "User " | %UserId | " not authorized to execute pagelet";
            Else
               &xmlDocTmp = CreateXmlDoc("");
               If &xmlDocTmp.ParseXmlString(&objDS.execute()) Then
                  &thisNode.AddAttribute("Title", &objPagelet.Title);
                  &thisNode.AddAttribute("PORTAL_OBJNAME", &objPagelet.ID);
                  &thisNode.AddAttribute("Description", &objPagelet.Descr);
                  &thisNode.AddAttribute("PageletWizardId", ".PAGELETID" | &i);
                  &thisNode.AddNode(&xmlDocTmp.DocumentElement);
               Else
                  &thisNode = &thisNode.AddElement("Exception");
                  &thisNode.NodeValue = "Pagelet Wizard XML not valid";

               End-If;
            End-If;

         catch Exception &e1
            &thisNode = &thisNode.AddElement("Exception");
            &thisNode.NodeValue = &e1.ToString();

         end-try;

         &objPagelet = Null;
      End-If;

   End-For;
   Return &xmlDoc.GenFormattedXmlString();
   rem Return &strHTML;

end-method;

/**
  * Initialize the HTML Setting and retrieve from the DataBase if appropriate for the pagelet.
  *
  * @param NewSettings if null, a new collection of settings is created.
  */
method initializeSettings
   /+ &NewSettings as PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.initializeSettings +/
   Local PTPPB_PAGELET:UTILITY:Setting &thisSetting;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If &NewSettings = Null Then
      &NewSettings = create PTPPB_PAGELET:UTILITY:Collection("HTMLDataSourceSettings");
   End-If;

   %This.setSettings(&NewSettings);
   &thisSetting = %This.Settings.getItemByID("HTML");

   If &thisSetting = Null Then
      &HTML = %This.createSettingProperty("HTML", "");
   Else
      &HTML = &thisSetting;
   End-If;

   If &HTML.Value = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &HTML.Value = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If &HTML.Value <> "" Then
      %This.setSettingsComplete( True);
   End-If;

   &HTML.FieldType = &HTML.FIELDTYPE_LONGCHARACTER;
   &HTML.EditType = &HTML.EDITTYPE_NOTABLEEDIT;
   &HTML.Enabled = True;
   &HTML.Visible = True;
   &HTML.Required = True;
   &HTML.LongName = MsgGetText(219, 602, "Message Not Found");

   &HTML.setObjectToRefreshOnValueChange(%This);
end-method;

/**
  * Determines if any text/HTML has been set.  If so, the settins are marked complete.
  */
method processSettingsChange
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.processSettingsChange +/;

   Local string &strHTMLText;
   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;

   Local PTPPB_PAGELET:DataSource:DataSourceParameter &thisParam;
   Local PTPPB_PAGELET:UTILITY:Collection &Params;

   Local number &i;

   /* do nothing if no settings */
   If %This.Settings = Null Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &HTMLSetting = %This.Settings.getItemByID("HTML");
   /* do nothing if no queryname setting */
   If &HTMLSetting = Null Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return
   End-If;

   &strHTMLText = &HTMLSetting.Value;
   /* do nothing if the HTML is blank */
   If &strHTMLText = "" Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &Params = %This.createEmptyParameterCollection();

   For &i = 1 To &NumPagelets
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".PAGELETID" | &i);
      &thisParam.LongName = "Pagelet ID " | &i;
      &thisParam.DefaultValue = "";
      REM   &thisParam.Required = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &Params.Insert(&thisParam);
   End-For;

   &Params.setImmutable();

   /* Mark the settings as complete */
   %This.setSettingsComplete( True);

end-method;

/**
  * Clones this datasource
  *
  * @return Object exact duplicate of this HTMLDataSource
  */
method Clone
   /+ Returns Object +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.Clone +/
   Local CIBER_PTPPB_PAGELET:XMLDataSource:MultiXMLDataSource &NewDS = create CIBER_PTPPB_PAGELET:XMLDataSource:MultiXMLDataSource(%This.ID);
   &NewDS.PageletID = %This.PageletID;
   &NewDS.initializeSettings(%This.Settings.Clone());
   Return &NewDS;
end-method;

/**
  * Executes when the pagelet is saved.  Saves the text/HTML to the HTML tables.
  */
method onPageletSave
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletSave +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

   If %This.SettingsComplete Then
      REM We actually have some settings to save... ;

      &HTMLSetting = %This.Settings.getItemByID("HTML");
      &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
      &REC_PTPPB_PGLT_HTML.DESCR.Value = %This.Descr;
      &REC_PTPPB_PGLT_HTML.HTMLAREA.Value = &HTMLSetting.Value;
      &REC_PTPPB_PGLT_HTML.LASTUPDDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.LASTUPDOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.CREATEDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.CREATEOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.Insert();

   End-If;
end-method;

/**
  * Executes when the pagelet is deleted.  Deletes the text/HTML from the HTML tables.
  */
method onPageletDelete
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletDelete +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

end-method;

import PTPPB_PAGELET:PageletWizard;
import PTPPB_PAGELET:Pagelet;

class PageletWizardXML
   method PageletWizardXML(&id_param As string);
   method GetXMLDoc() Returns XmlDoc;

   property string pageletId;
   property XmlDoc pwXMLDoc;

protected
   property PTPPB_PAGELET:Pagelet objPagelet;

end-class;

method PageletWizardXML
   /+ &id_param as String +/

   %This.pageletId = &id_param;
   %This.objPagelet = Null;

   %This.pwXMLDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><pageletwizardxml/>");

   try
      %This.objPagelet = (create PTPPB_PAGELET:PageletWizard()).getPageletByID(%This.pageletId, True);
   catch Exception &e1
      (%This.pwXMLDoc.DocumentElement.AddElement("Exception")).NodeValue = &e1.ToString();
   end-try;

end-method;

method GetXMLDoc
   /+ Returns XmlDoc +/

   If %This.objPagelet <> Null Then

      If Not %This.objPagelet.Authorized Then
         (%This.pwXMLDoc.DocumentElement.AddElement("Exception")).NodeValue = "User " | %UserId | " not authorized to execute pagelet";
      Else
         %This.objPagelet.fullDataSourceLoad();
         %This.objPagelet.PopulateInputsForHPPagelet();

         try
            Local boolean &bool = %This.pwXMLDoc.ParseXmlString((%This.objPagelet.DataSource).execute());
         catch Exception &e1
            (%This.pwXMLDoc.DocumentElement.AddElement("Exception")).NodeValue = "Pagelet Wizard XML did not parse correctly: " | &e1.ToString();
         end-try;
      End-If;

   End-If;

   Return %This.pwXMLDoc;

end-method;
