instance.peoplecode_text:
import PTPPB_PAGELET:PageletWizard:*;
import PTPPB_PAGELET:UTILITY:*;
import PTPPB_PAGELET:DataSource:*;
import PTPPB_PAGELET:EXCEPTION:*;
import CIBER_UI_FORMS:Utilities:RecordXML:*;


/** 
  *  DataSource to produce XML structure based on PS Record
  */
class PSRecordDataSource extends PTPPB_PAGELET:DataSource:DataSource
   property PTPPB_PAGELET:UTILITY:Setting PSRecord readonly;

   method PSRecordDataSource(&id_param As string);
   method execute() Returns string;
   method Clone() Returns object;
   method initializeSettings(&NewSettings As PTPPB_PAGELET:UTILITY:Collection);
   method processSettingsChange();
   method onPageletSave();
   method onPageletDelete();

private

   Constant &NUMBER_OF_PARMS = 5;
   instance array of string &FieldsWithPrompt;
   instance array of string &SubRecFieldsWithPrompt;
   method getFieldsWithPrompt() Returns array of string;
   method getSubRecFieldsWithPrompt() Returns array of string;

end-class;

/**
  * Constructor
  */
method PSRecordDataSource
   /+ &id_param as String +/
   %Super = create PTPPB_PAGELET:DataSource:DataSource(&id_param);
   %This.setObjectSubType("PSRecord");
   %This.setCanHaveParameters( True);
   %This.initializeSettings(%This.Settings);
   rem   %This.allowGrouplet = True;
end-method;

/**
  Returns PS Record XML
  */
method execute
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.execute +/

   Local string &strRecName, &retStr;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If %This.Settings <> Null Then
      &strRecName = %This.Settings.getItemByID("PSRecord").Value;
   End-If;

   If &strRecName = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &strRecName = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;
   rem   Return "<?xml version='1.0'?><psrecordxml><psrecordxmlproperties><recordname>" | &strRecName | "</recordname><fieldcount>10</fieldcount></psrecordxmlproperties></psrecordxml>";

   If &strRecName <> "" Then
      Local CIBER_UI_FORMS:Utilities:RecordXML &objRecXML = create CIBER_UI_FORMS:Utilities:RecordXML(&strRecName);

      /* get keys for record and set */
      /* see how many parameter collection items there are and then create array based on that number */

      /* fill array with param collec items - order is important */
      Local array of string &aryKeys = CreateArrayRept("", 0);
      Local array of string &aryEvaVals = CreateArrayRept("", 0);
      Local string &val, &ev_val;
      Local number &x;

      For &x = 1 To &NUMBER_OF_PARMS
         &val = "";
         &val = %This.ParameterCollection.getItemByID(".PARAMETER" | &x).value;

         If All(&val) Then
            &ev_val = %This.ParameterCollection.getItemByID(".PARAMETER" | &x).evaluatedValue();

            If &val = &ev_val Then
               /* add query string parameter value */
               &aryKeys.Push(&val);
               &aryEvaVals.Push(%Request.GetParameter(&val));
            Else
               /* add evaluated value */
               &aryKeys.Push(&val);
               &aryEvaVals.Push(&ev_val);
            End-If;
         End-If;

      End-For;

      rem      Return "<?xml version='1.0'?><psrecordxml><recordname>" | &aryEvaVals.Len | "</recordname></psrecordxml>";

      If &aryEvaVals.Len > 0 Then
         &objRecXML.setRecordKeyValues(&aryEvaVals);
      End-If;

      &FieldsWithPrompt = %This.getFieldsWithPrompt();
      For &x = 1 To &FieldsWithPrompt.Len
         &objRecXML.addFieldPrmtKeyVals(&FieldsWithPrompt [&x] | "=" | %This.ParameterCollection.getItemByID("." | &FieldsWithPrompt [&x]).Value);
      End-For;

      &SubRecFieldsWithPrompt = %This.getSubRecFieldsWithPrompt();
      For &x = 1 To &SubRecFieldsWithPrompt.Len
         &objRecXML.addSubRecFieldPrmtKeyVals(&SubRecFieldsWithPrompt [&x] | "=" | %This.ParameterCollection.getItemByID("." | &SubRecFieldsWithPrompt [&x]).Value);
      End-For;

      rem add config record field attributes;
      If %This.ParameterCollection.getItemByID(".CONFIGREC").Value <> "" Then
         &objRecXML.initObjCfgRecXML(%This.ParameterCollection.getItemByID(".CONFIGREC").Value);
      End-If;

      rem set config record query string parameter key(s);
      If %This.ParameterCollection.getItemByID(".CONFIGREC_QPARMSTR").Value <> "" Then
         &objRecXML.objCfgRecXML.addNameValuePairsToQStrArry(%This.ParameterCollection.getItemByID(".CONFIGREC_QPARMSTR").Value);
      End-If;

      Local XmlDoc &tmpxmldoc = &objRecXML.GetXMLDoc();

      Local XmlNode &DocNode, &topNode, &workNode;
      &DocNode = &tmpxmldoc.DocumentElement;
      &topNode = &DocNode.FindNode("/psrecordxml/psrecordxmlproperties");

      Local PTPPB_PAGELET:PageletWizard &objPW = create PTPPB_PAGELET:PageletWizard();
      &workNode = &topNode.AddElement("pageletid");
      &workNode.AddAttribute("Title", &objPW.getPageletByID(%This.PageletID, True).Title);
      &workNode.AddAttribute("Description", &objPW.getPageletByID(%This.PageletID, True).Descr);
      &workNode.NodeValue = %This.PageletID;

      If %This.ParameterCollection.getItemByID(".CONFIGREC").Value <> "" Then
         &workNode = &topNode.AddElement("CONFIGREC");
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID(".CONFIGREC").Value);
      End-If;

      For &x = 1 To &aryKeys.Len
         &workNode = &topNode.AddElement("PARAMETER" | &x);
         &workNode = &workNode.AddText(&aryKeys [&x] | "=" | &aryEvaVals [&x]);
      End-For;

      For &x = 1 To &SubRecFieldsWithPrompt.Len
         &workNode = &topNode.AddElement("KeyField" | &x);
         &workNode.AddAttribute("KeyField", &SubRecFieldsWithPrompt [&x]);
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID("." | &SubRecFieldsWithPrompt [&x]).Value);
      End-For;

      If %This.ParameterCollection.getItemByID(".ADMINROLE").Value <> "" Then
         &workNode = &topNode.AddElement("ADMINROLE");
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID(".ADMINROLE").Value);
      End-If;

      If %This.ParameterCollection.getItemByID(".CONFIGREC_QPARMSTR").Value <> "" Then
         &workNode = &topNode.AddElement("CONFIGREC_QPARMSTR");
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID(".CONFIGREC_QPARMSTR").Value);
      End-If;

      &retStr = &tmpxmldoc.GenFormattedXmlString();

   End-If;

   Return &retStr;

end-method;

/**
  * Initialize the HTML Setting and retrieve from the DataBase if appropriate for the pagelet.
  *
  * @param NewSettings if null, a new collection of settings is created.
  */
method initializeSettings
   /+ &NewSettings as PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.initializeSettings +/
   Local PTPPB_PAGELET:UTILITY:Setting &thisSetting;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If &NewSettings = Null Then
      &NewSettings = create PTPPB_PAGELET:UTILITY:Collection("PSRecordDataSourceSettings");
   End-If;

   %This.setSettings(&NewSettings);
   &thisSetting = %This.Settings.getItemByID("PSRecord");

   If &thisSetting = Null Then
      &PSRecord = %This.createSettingProperty("PSRecord", "");
   Else
      &PSRecord = &thisSetting;
   End-If;

   If &PSRecord.Value = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &PSRecord.Value = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If &PSRecord.Value <> "" Then
      %This.setSettingsComplete( True);
   End-If;

   &PSRecord.Enabled = True;
   &PSRecord.Visible = True;
   &PSRecord.Required = True;
   &PSRecord.RefreshOnChange = True;
   &PSRecord.LongName = MsgGetText(219, 518, "(Message Not Found)Record Name");
   &PSRecord.EditType = &PSRecord.EDITTYPE_PROMPTTABLE;
   &PSRecord.PromptTable = "PSRECDEFNPVW";
   rem &PSRecord.PromptTable = "PSRECDEFN";
   &PSRecord.setObjectToRefreshOnValueChange(%This);

   %This.setCanHaveParameters( True);

end-method;

/**
  * Determines if any text/HTML has been set.  If so, the settins are marked complete.
  */
method processSettingsChange
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.processSettingsChange +/;
   Local PTPPB_PAGELET:UTILITY:Setting &RecordNameSetting;
   Local string &RecordNameValue, &returnRecordName;
   Local PTPPB_PAGELET:DataSource:DataSourceParameter &thisParam;
   Local PTPPB_PAGELET:UTILITY:Collection &Params;
   Local number &i;

   /* set settings incomplete and do nothing if no settings */
   If %This.Settings = Null Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &RecordNameSetting = %This.Settings.getItemByID("PSRecord");
   /* set settings incomplete and do nothing if no setting */
   If &RecordNameSetting = Null Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &RecordNameValue = &RecordNameSetting.Value;
   /* set settings incomplete and do nothing if no value */
   If &RecordNameValue = "" Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   /* Validate that this record exists */
   SQLExec(SQL.PTPPB_RS_GET_RECORDNAME, &RecordNameValue, &returnRecordName);
   If None(&returnRecordName) Then
      throw create PTPPB_PAGELET:EXCEPTION:RowsetException(MsgGetText(219, 517, "Message Not Found - Invalid record name '%1'", &RecordNameValue));
   End-If;


   &Params = %This.createEmptyParameterCollection();

   &thisParam = %This.ParameterCollection.getItemByID(".CONFIGREC");
   If &thisParam = Null Then
      /* No parameter with this field name exists ... just use the field name for the parameter name */
      /* Note:  This is the most common case */
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".CONFIGREC");
      &thisParam.LongName = "Config Record Key Value";
      &thisParam.DefaultValue = "";
      &thisParam.Required = False;
      rem &thisParam.allowChangesToRequired = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &Params.Insert(&thisParam);
   End-If;

   &thisParam = %This.ParameterCollection.getItemByID(".CONFIGREC_QPARMSTR");
   If &thisParam = Null Then
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".CONFIGREC_QPARMSTR");
      &thisParam.LongName = "Config Rec Qry Str Parm Key";
      &thisParam.DefaultValue = "";
      REM   &thisParam.Required = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &Params.Insert(&thisParam);
   End-If;

   For &i = 1 To &NUMBER_OF_PARMS;
      &thisParam = %This.ParameterCollection.getItemByID(".PARAMETER" | &i);
      If &thisParam = Null Then
         &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".PARAMETER" | &i);
         &thisParam.LongName = "Key QStr Parameter " | &i;
         &thisParam.DefaultValue = "";
         &thisParam.Required = False;
         &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
         &Params.Insert(&thisParam);
      End-If;
   End-For;

   &FieldsWithPrompt = %This.getFieldsWithPrompt();
   For &i = 1 To &FieldsWithPrompt.Len
      &thisParam = %This.ParameterCollection.getItemByID("." | &FieldsWithPrompt [&i]);
      If &thisParam = Null Then
         /* No parameter with this field name exists ... just use the field name for the parameter name */
         /* Note:  This is the most common case */
         &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter("." | &FieldsWithPrompt [&i]);
         &thisParam.LongName = "Prompt Table Key Value";
         &thisParam.DefaultValue = "";
         rem &thisParam.Required = False;
         &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
         &Params.Insert(&thisParam);
      End-If;
   End-For;


   &SubRecFieldsWithPrompt = %This.getSubRecFieldsWithPrompt();
   For &i = 1 To &SubRecFieldsWithPrompt.Len
      &thisParam = %This.ParameterCollection.getItemByID("." | &SubRecFieldsWithPrompt [&i]);
      REM       &thisParam = %This.ParameterCollection.getItemByID(".SUBRECFIELDPROMPTKEY" | &i);
      If &thisParam = Null Then
         /* No parameter with this field name exists ... just use the field name for the parameter name */
         /* Note:  This is the most common case */
         &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter("." | &SubRecFieldsWithPrompt [&i]);
         REM    &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".SUBRECFIELDPROMPTKEY" | &i);
         &thisParam.LongName = "SubRec Prompt Table Key Value";
         &thisParam.DefaultValue = "";
         rem &thisParam.Required = False;
         &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
         &Params.Insert(&thisParam);
      End-If;
   End-For;

   &thisParam = %This.ParameterCollection.getItemByID(".ADMINROLE");
   If &thisParam = Null Then
      /* No parameter with this field name exists ... just use the field name for the parameter name */
      /* Note:  This is the most common case */
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".ADMINROLE");
      &thisParam.LongName = "Admin Security Role";
      &thisParam.DefaultValue = "";
      &thisParam.Required = False;
      rem &thisParam.allowChangesToRequired = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &thisParam.EditType = &thisParam.EDITTYPE_PROMPTTABLE; /* prompt table = 2 */
      &thisParam.PromptTable = "PSROLEDEFN";
      &Params.Insert(&thisParam);
   End-If;

   /* and set the ParameterCollection to be immutable */
   &Params.setImmutable();

   /* Mark the settings as complete */
   %This.setSettingsComplete( True);


end-method;

/**
  * Clones this datasource
  *
  * @return Object exact duplicate of this DS
  */
method Clone
   /+ Returns Object +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.Clone +/
   Local CIBER_UI_FORMS:DataSource:PSRecordDataSource &NewDS = create CIBER_UI_FORMS:DataSource:PSRecordDataSource(%This.ID);
   &NewDS.PageletID = %This.PageletID;
   &NewDS.initializeSettings(%This.Settings.Clone());
   Return &NewDS;
end-method;


/**
  * Executes when the pagelet is saved.  Saves the text/HTML to the HTML tables.
  */
method onPageletSave
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletSave +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

   If %This.SettingsComplete Then
      REM We actually have some settings to save... ;

      &HTMLSetting = %This.Settings.getItemByID("PSRecord");
      &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
      &REC_PTPPB_PGLT_HTML.DESCR.Value = %This.Descr;
      &REC_PTPPB_PGLT_HTML.HTMLAREA.Value = &HTMLSetting.Value;
      &REC_PTPPB_PGLT_HTML.LASTUPDDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.LASTUPDOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.CREATEDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.CREATEOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.Insert();

   End-If;
end-method;

/**
  * Executes when the pagelet is deleted.  Deletes the text/HTML from the HTML tables.
  */
method onPageletDelete
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletDelete +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

end-method;

method getFieldsWithPrompt
   /+ Returns Array of String +/
   Local array of string &fields = CreateArrayRept("", 0);
   Local Rowset &psRecFieldRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psKeyDefnRS = CreateRowset(Record.PSKEYDEFN);
   Local string &psKeyDefn_RecName, &psKeyDefn_FieldName;

   Local number &x, &y;

   &psRecFieldRS.Fill("WHERE RECNAME = :1 AND EDITTABLE <> ' ' ORDER BY FIELDNUM", %This.Settings.getItemByID("PSRecord").Value);
   If All(&psRecFieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psRecFieldRS.ActiveRowCount
         &psKeyDefn_RecName = &psRecFieldRS(&x).GetRecord(1).GetField(Field.EDITTABLE).Value;
         &psKeyDefnRS.Fill("WHERE INDEXID='_' AND RECNAME = :1 ORDER BY KEYPOSN", &psKeyDefn_RecName);
         For &y = 1 To &psKeyDefnRS.ActiveRowCount - 1
            &psKeyDefn_FieldName = &psKeyDefnRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value;
            &fields.Push(&psKeyDefn_RecName | "." | &psKeyDefn_FieldName);
         End-For;
      End-For;
   End-If;
   Return &fields;

end-method;

method getSubRecFieldsWithPrompt
   /+ Returns Array of String +/
   Local array of string &fields = CreateArrayRept("", 0);
   Local Rowset &psRecFieldRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psPromptRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psKeyDefnRS = CreateRowset(Record.PSKEYDEFN);
   Local Record &tmpRec;
   Local number &x, &y, &z;
   Local string &psKeyDefn_RecName, &psKeyDefn_FieldName;
   Local string &strTmp;

   &psRecFieldRS.Fill("WHERE RECNAME = :1 AND SUBRECORD = 'Y' ORDER BY FIELDNUM", %This.Settings.getItemByID("PSRecord").Value);
   If All(&psRecFieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psRecFieldRS.ActiveRowCount
         rem if subrec then find fields in subrec that have prompts;

         rem get subrecord name;
         &psKeyDefn_RecName = &psRecFieldRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value;
         rem get edittables of subrecord;
         &psPromptRS.Fill("WHERE RECNAME = :1 AND  EDITTABLE <> ' ' ORDER BY FIELDNUM", &psKeyDefn_RecName);
         rem for each edittable get keys;
         If All(&psPromptRS(1).GetRecord(1).GetField(1).Value) Then
            For &y = 1 To &psPromptRS.ActiveRowCount
               rem get keys of edittable;

               &tmpRec = CreateRecord(@("RECORD." | &psPromptRS(&y).GetRecord(1).GetField(Field.EDITTABLE).Value));
               Local array of string &recKeys = CreateArrayRept("", 0);
               For &z = 1 To &tmpRec.FieldCount
                  If &tmpRec.GetField(&z).IsKey Then
                     &recKeys.Push(&tmpRec.GetField(&z).Name);
                  End-If;
               End-For;

               For &z = 1 To &recKeys.Len - 1

                  rem &strTmp = &psKeyDefn_RecName | "." | &psPromptRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value;
                  rem &strTmp = &strTmp | ":" | &tmpRec.Name | "." | &tmpRec.GetField(&z).Name;
                  &strTmp = "SBR" | &x | ":" | &tmpRec.Name | "." | &tmpRec.GetField(@("FIELD." | &recKeys [&z])).Name;
                  &fields.Push(&strTmp);
               End-For;
            End-For;
         End-If;

      End-For;
   End-If;

   Return &fields;
end-method;

class ConfigRecordXML
   method ConfigRecordXML(&ObjParm As string);
   method addFieldAttributesToNode(&parmNode As XmlNode);
   method addNameValuePairsToQStrArry(&parmStr As string);

private
   instance string &ConfigRecKeyValue; /* Config Rec Key Field Value */
   instance array of array of string &qStrParmPairs;
   instance Rowset &ConfigRowSet; /* rowset to be loaded with row(s) from config record */
   instance boolean &ConfigRowSetIsEmpty;

   method initialize();
   method parseNameValueIntoArray(&queryString As string, &ary2str As array of array of string);
   method compPToolsQStrVals(&strParm As string) Returns boolean;
   method isItemInArray(&aryParm As array of string, &aryParm_2 As array of array of string) Returns boolean;
   method translateBinaryToNameValuePairs(&num As number) Returns array of array of string;
   method calcDecimalToBinary(&num As number) Returns array of number;
   method addElementTextToNode(&parmNode As XmlNode, &parmAry As array of string);
   method addNameValuePairToNode(&parmNode As XmlNode, &parmAry As array of string);
   method addNameValuePairArrayToNode(&parmNode As XmlNode, &parmAry2 As array of array of string);

   Constant &CONFIGRECORDNAME = "CBR_CONFIG"; /* Config Record Name */
   Constant &CONFIGKEY1 = "OBJNAME"; /* Config Record 1st Key Field Name where record name / etc. are stored */
   Constant &CONFIGKEY2 = "KEY1"; /* Config Record 2nd Key Field Name where QStrParms are stored */
   Constant &CONFIGKEY3 = "KEY2"; /* Config Record 3RD Key Field Name where QStrParms are stored */
   Constant &CONFIGKEY4 = "KEY3"; /* Config Record 4TH Key Field Name where QStrParms are stored */
   Constant &FIELDNAME = "FIELDNAME";
   Constant &BITFIELD = "USEEDIT";
   Constant &FIELDVALUE = "FIELDVALUE";
   Constant &ELEMENTID = "ELEMENT_UID";
   Constant &TOOLTIP = "PTPPB_OPENTXT";
   Constant &ERRORMSG = "ERROR_MESSAGE_TXT";
   Constant &BLANK_EMPTY = "*";

end-class;

method ConfigRecordXML
   /+ &ObjParm as String +/
   %This.ConfigRecKeyValue = &ObjParm;
   %This.initialize();
end-method;

method initialize

   Local number &x;
   Local array of string &tmpAryStr;

   rem get query string parameters and store in qStrParmPairs array;
   &tmpAryStr = %Request.GetParameterNames();
   &qStrParmPairs = CreateArrayRept(CreateArrayRept("", 2), 0);
   If &tmpAryStr.Len > 0 Then
      For &x = 1 To &tmpAryStr.Len
         If Not %This.compPToolsQStrVals(&tmpAryStr [&x]) Then
            Local array of string &qStrPair = CreateArrayRept("", 2);
            &qStrPair [1] = &tmpAryStr [&x];
            &qStrPair [2] = %Request.GetParameter(&tmpAryStr [&x]);
            &qStrParmPairs.Push(&qStrPair);
         End-If;
      End-For;
   End-If;

   &ConfigRowSet = CreateRowset(@("Record." | &CONFIGRECORDNAME));
   &ConfigRowSet.Fill("WHERE " | &CONFIGKEY1 | " =:1", %This.ConfigRecKeyValue);

   If All(&ConfigRowSet(1).GetRecord(1).GetField(1).Value) Then
      &ConfigRowSetIsEmpty = False;
   Else
      &ConfigRowSetIsEmpty = True;
   End-If;

   /* debug */
   rem For &x = 1 To &qStrParmPairs.Len
   rem   WriteToLog(%ApplicationLogFence_Error, "qstr: " | &qStrParmPairs [&x][1] | " = " | &qStrParmPairs [&x][2]);
   rem End-For;
   /*
      For &x = 1 To &ConfigRowSet(1).GetRecord(1).FieldCount
      WriteToLog(%ApplicationLogFence_Error, String(&ConfigRowSet(1).GetRecord(1).GetField(&x).Name | " = " | &ConfigRowSet(1).GetRecord(1).GetField(&x).Value));
   End-For;
   For &x = 1 To &ConfigRowSet(1).GetRecord(1).FieldCount
      WriteToLog(&ConfigRowSet(1).GetRecord(1).GetField(&x).Name | " = " | &ConfigRowSet(1).GetRecord(1).GetField(&x).Value));
   End-For;
   */

end-method;

method addFieldAttributesToNode
   /+ &parmNode as XmlNode +/
   Local array of Row &ConfigRow = CreateArrayRept(CreateRowset(@("Record." | &CONFIGRECORDNAME)).GetRow(1), 0);
   Local string &strFieldName;
   Local number &x, &y;

   &strFieldName = &parmNode.GetAttributeValue("name");
   If None(&strFieldName) Then
      Return;
   End-If;

   If Not %This.ConfigRowSetIsEmpty Then

      /* collect all rows that have this fieldname */
      For &x = 1 To %This.ConfigRowSet.ActiveRowCount
         If %This.ConfigRowSet.GetRow(&x).GetRecord(1).GetField(@("Field." | &FIELDNAME)).Value = &strFieldName Then
            &ConfigRow.Push(&ConfigRowSet(&x)); /* gather all rows into array, assumes first row will be blank key/qstrparm value */
         End-If;
      End-For;

      rem WriteToLog(%ApplicationLogFence_Error, String("config row count = " | &ConfigRow.Len));

      For &x = 1 To &ConfigRow.Len

         Local array of string &CfgRecKeys = CreateArrayRept("", 3);
         &CfgRecKeys [1] = &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &CONFIGKEY2)).Value;
         &CfgRecKeys [2] = &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &CONFIGKEY3)).Value;
         &CfgRecKeys [3] = &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &CONFIGKEY4)).Value;

         If &CfgRecKeys [1] = &BLANK_EMPTY And
               &CfgRecKeys [2] = &BLANK_EMPTY And
               &CfgRecKeys [2] = &BLANK_EMPTY Then
            %This.addNameValuePairArrayToNode(&parmNode, %This.translateBinaryToNameValuePairs(&ConfigRow [&x].GetRecord(1).GetField(@("Field." | &BITFIELD)).Value));

         Else

            If &CfgRecKeys [1] <> &BLANK_EMPTY Then
               Local array of array of string &keyValuePairs = CreateArrayRept(CreateArrayRept("", 2), 0);
               %This.parseNameValueIntoArray(&CfgRecKeys [1], &keyValuePairs);

               If &CfgRecKeys [2] <> &BLANK_EMPTY Then
                  %This.parseNameValueIntoArray(&CfgRecKeys [2], &keyValuePairs);
               End-If;

               If &CfgRecKeys [3] <> &BLANK_EMPTY Then
                  %This.parseNameValueIntoArray(&CfgRecKeys [3], &keyValuePairs);
               End-If;

               Local number &match = 0;
               For &y = 1 To &keyValuePairs.Len
                  If %This.isItemInArray(&keyValuePairs [&y], &qStrParmPairs) Then
                     &match = &match + 1;
                  End-If;
               End-For;

               If &match = &keyValuePairs.Len Then
                  %This.addNameValuePairArrayToNode(&parmNode, %This.translateBinaryToNameValuePairs(&ConfigRow [&x].GetRecord(1).GetField(@("Field." | &BITFIELD)).Value));
                  %This.addNameValuePairToNode(&parmNode, CreateArray(Lower(&FIELDVALUE), &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &FIELDVALUE)).Value));
                  %This.addNameValuePairToNode(&parmNode, CreateArray(Lower(&ELEMENTID), &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &ELEMENTID)).Value));
                  %This.addNameValuePairToNode(&parmNode, CreateArray(Lower(&TOOLTIP), &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &TOOLTIP)).Value));
                  %This.addNameValuePairToNode(&parmNode, CreateArray(Lower(&ERRORMSG), &ConfigRow [&x].GetRecord(1).GetField(@("Field." | &ERRORMSG)).Value));
               End-If;
            End-If;

         End-If;

      End-For;

   End-If;

end-method;

method isItemInArray
   /+ &aryParm as Array of String, +/
   /+ &aryParm_2 as Array2 of String +/
   /+ Returns Boolean +/

   Local number &x;

   For &x = 1 To &aryParm_2.Len
      If Upper(&aryParm [1]) = Upper(&aryParm_2 [&x][1]) And
            Upper(&aryParm [2]) = Upper(&aryParm_2 [&x][2]) Then
         Return True;
      End-If;
   End-For;


   Return False;
end-method;

/* takes string and parses "x=y" value into array structure */
method parseNameValueIntoArray
   /+ &queryString as String, +/
   /+ &ary2str as Array2 of String +/

   /* note that this function assumes that the name/value string format will NOT have duplicate names 
		if there are dups, the first instance of name in name/value pair will not be overwritten in the
		dup checking process */

   If Find("&", &queryString) > 0 Then
      If Len(Substring(&queryString, 1, Find("&", &queryString) - 1)) > 0 Then
         %This.parseNameValueIntoArray(Substring(&queryString, 1, Find("&", &queryString) - 1), &ary2str);
      End-If;
      If Len(Substring(&queryString, Find("&", &queryString) + 1, Len(&queryString))) > 0 Then
         %This.parseNameValueIntoArray(Substring(&queryString, Find("&", &queryString) + 1, Len(&queryString)), &ary2str);
      End-If;
   Else
      If Len(Substring(&queryString, 1, Find("=", &queryString) - 1)) > 0 And
            Len(Substring(&queryString, Find("=", &queryString) + 1, Len(&queryString))) > 0 Then

         Local array of string &NameValPair = CreateArrayRept("", 2);

         &NameValPair [1] = Substring(&queryString, 1, Find("=", &queryString) - 1);
         &NameValPair [2] = Substring(&queryString, Find("=", &queryString) + 1, Len(&queryString));

         rem make sure that there are no dups;
         Local number &x;
         Local boolean &found;
         &found = False;
         For &x = 1 To &ary2str.Len
            If &ary2str [&x][1] = &NameValPair [1] Then
               &found = True;
            End-If;
         End-For;

         If Not &found Then
            &ary2str.Push(&NameValPair);
         End-If;

      End-If;
   End-If;

end-method;

/* takes in number parm and breaks down bit values
and translates them to name/value pair (xml attribute=value).
-stores pair as a 2 item str array
-then stores (2 item str array) as array item inside container array (array of array)
*/
method translateBinaryToNameValuePairs
   /+ &num as Number +/
   /+ Returns Array2 of String +/

   Local array of string &strAry;
   Local array of array of string &returnAry = CreateArrayRept(CreateArrayRept("", 2), 0);
   Local array of number &UseEditAryNums = %This.calcDecimalToBinary(&num);
   Local number &x;
   rem WriteToLog(%ApplicationLogFence_Error, "NUM=" | &num | "    ARY.LEN=" | &UseEditAryNums.Len);
   For &x = 1 To &UseEditAryNums.Len
      &strAry = CreateArrayRept("", 2);
      Evaluate &UseEditAryNums [&x]
      When 1 /* required - 1*/
         &strAry [1] = "required";
         &strAry [2] = "true";
      When 2 /* no html - 2*/
         &strAry [1] = "nohtml";
         &strAry [2] = "true";
      When 3 /* hidden - 4*/
         &strAry [1] = "type";
         &strAry [2] = "hidden";
      When 4 /* data-parsley-required - 8*/
         &strAry [1] = "data-parsley-required";
         &strAry [2] = "";
      When 5 /* data-parsley-maxlength - 16*/
         &strAry [1] = "data-parsley-maxlength";
         &strAry [2] = "";
      When 6 /*  Bootstrap Hidden - 32 */
         &strAry [1] = "hidden";
         &strAry [2] = "";
      When 7 /* Typeahead - 64 */
         &strAry [1] = "typeahead";
         &strAry [2] = "true";
      When 8 /* fill in - 128*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 9 /* fill in - 256*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 10 /* fill in - 512*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 11 /* fill in - 1024*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 12 /* fill in - 2048*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 13 /* fill in - 4096*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 14 /* fill in - 8192*/
         &strAry [1] = "";
         &strAry [2] = "";
      When 15 /* fill in - 16384*/
         &strAry [1] = "";
         &strAry [2] = "";
      When-Other /* fill in - 32768*/
         &strAry [1] = "";
         &strAry [2] = "";
      End-Evaluate;

      If &strAry [1] = "" And
            &strAry [2] = "" Then
         &strAry [1] = "bitvalue" | String(&UseEditAryNums [&x]);
         &strAry [2] = "notdefined";
      End-If;

      &returnAry.Push(&strAry);

   End-For;

   Return &returnAry;

end-method;

/* actual math calculation to break down bit numbers
stores them into array */
method calcDecimalToBinary
   /+ &num as Number +/
   /+ Returns Array of Number +/
   Local array of number &retAry = CreateArrayRept(9, 0);
   Local array of number &bits = CreateArrayRept(9, 0);
   Local number &bitValue = &num;
   Local number &x, &y;
   Local string &binary, &tmpStr;

   rem   &debug = "";

   While &bitValue > 0
      &bits.Push(Mod(&bitValue, 2));
      &bitValue = Int(&bitValue / 2);
   End-While;

   rem convert arry into binary string;
   For &x = 1 To &bits.Len
      &binary = String(&bits [&x]) | &binary;
   End-For;

   rem determine which position is a "1" and place in arry;
   For &x = 1 To Len(&binary)
      &y = 1 + Len(&binary) - &x;
      If Substring(&binary, &y, 1) = "1" Then
         &retAry.Push(&x);
      End-If;
   End-For;

   Return &retAry;
end-method;

