method addNameValuePairArrayToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmAry2 as Array2 of String +/

   Local number &x;
   For &x = 1 To &parmAry2.Len
      rem      WriteToLog(%ApplicationLogFence_Error, &parmAry2 [&x][1] | " = " | &parmAry2 [&x][2]);
      &parmNode.AddAttribute(&parmAry2 [&x][1], &parmAry2 [&x][2]);
   End-For;

end-method;

method addNameValuePairToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmAry as Array of String +/

   If All(&parmAry [1]) And
         All(&parmAry [2]) Then
      &parmNode.AddAttribute(&parmAry [1], &parmAry [2]);
   End-If;

end-method;

method addElementTextToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmAry as Array of String +/

   Local XmlNode &tmpXmlNode;
   &tmpXmlNode = &parmNode.AddElement(&parmAry [1]);
   &tmpXmlNode = &tmpXmlNode.AddText(&parmAry [2]);

end-method;

method addNameValuePairsToQStrArry
   /+ &parmStr as String +/

   rem do a check on the incoming string for format correctness?;

   %This.parseNameValueIntoArray(&parmStr, &qStrParmPairs);

   rem debug;
   rem   Local number &x;
   rem   For &x = 1 To &qStrParmPairs.Len
rem      WriteToLog(%ApplicationLogFence_Error, String(&qStrParmPairs [&x][1]) | " = " | String(&qStrParmPairs [&x][2]));
   rem   End-For;

end-method;

import CIBER_UI_FORMS:Utilities:FileUpload:*;

class JSONForm
   method JSONForm(&jsonStr As string);

private

   method initInternalKeys();
   method setFieldsToRec(&parmRec As Record, &fieldsAry As array of array of string);
   method checkIfEmptyJson(&tmpRec As Record, &JsonFields As array of array of string) Returns boolean;
   method InsertUpdateToRecord(&parmRec As Record, &JsonFields As array of array of string);
   method tryDeleteRecord(&parmRec As Record);
   method reorderSeqNum(&parmRec As Record);
   method inExceptionFieldList(&parmField As string) Returns boolean;

   instance array of array of string &KeyNameValues;
   instance array of string &ExceptionFieldNames;

   instance string &RecordName;

   Constant &RECORDTAG = "psrecord";
   Constant &FIELDTAG = "fields";
   Constant &NAME = "name";
   Constant &VALUE = "value";

   Constant &KEY1 = "OPRID";
   Constant &KEY2 = "UD_OLA_APPL_NBR";
   Constant &KEY3 = "";
   Constant &KEY4 = "";

   Constant &SEQNUM = "SEQNUM";
   rem audit fields;
   Constant &A_OPRID = "LASTUPDOPRID";
   Constant &A_DTTM = "LASTUPDDTTM";

   Constant &FILEUPLOADREC = "CBR_UPLOAD";


   rem json format;
   /*
[
  {
    "psrecord": "cbr_record",
    "fields": [
      {
        "name": "state",
        "value": "asdf"
      },
      {
        "name": "postal",
        "value": "asdf"
      }
    ]
  },
  {
    "psrecord": "cbr_other",
    "fields": [
      {
        "name": "name",
        "value": "asdf"
      },
      {
        "name": "title",
        "value": "asdf"
      }
    ]
  }
]
*/


end-class;

/* constructor */
method JSONForm
   /+ &jsonStr as String +/

   Local JavaObject &JStr = CreateJavaObject("java.lang.String", &jsonStr);
   Local JavaObject &JSON = GetJavaClass("javax.json.Json"); /* this needs jar file added to appserver */
   Local JavaObject &StringReader = CreateJavaObject("java.io.StringReader", &JStr);
   Local JavaObject &JSONReader = &JSON.createReader(&StringReader);
   Local JavaObject &JSONArray = &JSONReader.readArray();
   &JSONReader.close();
   &JStr = Null;
   &JSON = Null;
   &StringReader = Null;
   &JSONReader = Null;

   Local number &x, &y;

   rem initialize key array;
   &KeyNameValues = CreateArrayRept(CreateArrayRept("", 2), 0);
   %This.initInternalKeys();

   For &x = 1 To &JSONArray.size()
      rem WriteToLog(1, &JSONArray.getJsonObject(&x - 1).getString(&RECORDTAG));
      Local JavaObject &RecJsonObj = &JSONArray.getJsonObject(&x - 1);
      Local JavaObject &JSONArrayFields = &RecJsonObj.getJsonArray(&FIELDTAG);
      Local array of array of string &tmpAry2 = CreateArrayRept(CreateArrayRept("", 2), 0);

      &RecJsonObj = Null;
      For &y = 1 To &JSONArrayFields.size()
         Local array of string &tmpAryStr = CreateArrayRept("", 2);
         Local JavaObject &jsonObj = &JSONArrayFields.getJsonObject(&y - 1);
         &tmpAryStr [1] = &jsonObj.getString(&NAME);
         &tmpAryStr [2] = &jsonObj.getString(&VALUE);
         &jsonObj = Null;
         &tmpAry2.Push(&tmpAryStr);
      End-For;


      Local Record &rec = CreateRecord(@("RECORD." | &JSONArray.getJsonObject(&x - 1).getString(&RECORDTAG)));
      &RecordName = &rec.Name;
      rem set all keys from internal keys;
      %This.setFieldsToRec(&rec, &KeyNameValues);
      rem set all fields from json fields;
      %This.setFieldsToRec(&rec, &tmpAry2);

      If %This.checkIfEmptyJson(&rec, &tmpAry2) Then
         rem try delete;
         %This.tryDeleteRecord(&rec);
      Else
         rem insert/update rec;
         %This.InsertUpdateToRecord(&rec, &tmpAry2);
      End-If;

      rem deubg;
      rem      For &y = 1 To &JSONArrayFields.size()
rem         Local JavaObject &jsonObj = &JSONArrayFields.getJsonObject(&y - 1);
      rem         WriteToLog(1, &jsonObj.getString(&NAME) | "=" | &jsonObj.getString(&VALUE));
      rem      End-For;

      &JSONArrayFields = Null;
   End-For;
   &JSONArray = Null;

end-method;


method initInternalKeys

   Local array of string &StrAry;

   &StrAry = CreateArrayRept("", 2);
   &StrAry [1] = &KEY1;
   &StrAry [2] = %OperatorId;
   &KeyNameValues.Push(&StrAry);

   &StrAry = CreateArrayRept("", 2);
   &StrAry [1] = &KEY2;
   &StrAry [2] = %Request.GetParameter("ADM_APPL_NBR"); /* fill in for new key */
   &KeyNameValues.Push(&StrAry);

   &StrAry = CreateArrayRept("", 2);
   &StrAry [1] = &KEY3;
   &StrAry [2] = ""; /* fill in for new key */
   &KeyNameValues.Push(&StrAry);

   &StrAry = CreateArrayRept("", 2);
   &StrAry [1] = &KEY4;
   &StrAry [2] = ""; /* fill in for new key */
   &KeyNameValues.Push(&StrAry);

   rem list of fields that are exception to check for inExceptionFieldList;
   &ExceptionFieldNames = CreateArrayRept("", 0);
   &ExceptionFieldNames.Push("ATTACHSYSFILENAME");

end-method;

method setFieldsToRec
   /+ &parmRec as Record, +/
   /+ &fieldsAry as Array2 of String +/

   Local number &x, &y;

   For &x = 1 To &parmRec.FieldCount
      For &y = 1 To &fieldsAry.Len
         If &parmRec.GetField(&x).Name = &fieldsAry [&y][1] Then
            If &parmRec.GetField(&x).Type = "DATE" Then
               Local string &str = Substitute(&fieldsAry [&y][2], "/", "");
               rem change from DDMMYYYY to YYYYMMDD;
               &str = Substring(&str, 5, 4) | Substring(&str, 3, 2) | Substring(&str, 1, 2);
               &parmRec.GetField(&x).Value = Date(Value(&str));
            Else
               &parmRec.GetField(&x).Value = &fieldsAry [&y][2];
            End-If;
         End-If;
      End-For;
   End-For;

end-method;

method checkIfEmptyJson
   /+ &tmpRec as Record, +/
   /+ &JsonFields as Array2 of String +/
   /+ Returns Boolean +/

   Local number &x, &y;
   Local boolean &empty = True;

   rem check if field is key AND (if field is audit field);
   rem then check against left over fields against json gathered fields;
   rem if match and has value, then record is not empty;
   For &x = 1 To &tmpRec.FieldCount
      If Not (&tmpRec.GetField(&x).IsKey) And
            Not (&tmpRec.GetField(&x).Name = &A_OPRID Or
               &tmpRec.GetField(&x).Name = &A_DTTM) Then
         For &y = 1 To &JsonFields.Len
            If &JsonFields [&y][1] = &tmpRec.GetField(&x).Name And
                  All(&JsonFields [&y][2]) Then
               rem               WriteToLog(1, "checkIfEmptyJson is false");
               &empty = False;
            End-If;
         End-For;
      End-If;
   End-For;

   Return &empty;

end-method;

method InsertUpdateToRecord
   /+ &parmRec as Record, +/
   /+ &JsonFields as Array2 of String +/

   Local Record &tmpRec = CreateRecord(@("RECORD." | &parmRec.Name));
   Local Record &saveRec = CreateRecord(@("RECORD." | &parmRec.Name));

   &parmRec.CopyFieldsTo(&tmpRec);
   &parmRec.CopyFieldsTo(&saveRec);

   rem read in row if exists;
   If &saveRec.SelectByKey( False) Then
      %This.setFieldsToRec(&saveRec, &JsonFields);
      rem      WriteToLog(1, "selectbykey true");
   Else
      rem      WriteToLog(1, "selectbykey false");
      rem set fields again due to selectbykey not returning row;
      &tmpRec.CopyFieldsTo(&saveRec);
   End-If;

   rem fill in audit fields;
   &saveRec.GetField(@("FIELD." | &A_OPRID)).Value = %OperatorId;
   &saveRec.GetField(@("FIELD." | &A_DTTM)).Value = %Datetime;

   rem save;
   If &saveRec.Save() Then
      rem       WriteToLog(1, "save success");
   Else
      rem      WriteToLog(1, "save fail");
      rem throw exception;
   End-If;

end-method;

method tryDeleteRecord
   /+ &parmRec as Record +/

   rem read in row if exists;
   If &parmRec.SelectByKey( False) Then
      If &RecordName = &FILEUPLOADREC Then
         Local string &filename = &parmRec.GetField(Field.ATTACHSYSFILENAME).Value;
         Local CIBER_UI_FORMS:Utilities:FileUpload &objFileUpload = create CIBER_UI_FORMS:Utilities:FileUpload();
         &objFileUpload.deleteFile(&filename);
      End-If;
      If &parmRec.Delete() Then
         %This.reorderSeqNum(&parmRec);
         rem         WriteToLog(1, "delete success");
      Else
         rem         WriteToLog(1, "delete fail");
         rem throw exception;
      End-If;
   Else
      rem   WriteToLog(1, "try delete selecybykey fail");
   End-If;

end-method;

method reorderSeqNum
   /+ &parmRec as Record +/
   Local Rowset &rs = CreateRowset(@("RECORD." | &parmRec.Name));
   Local array of string &keyfields = CreateArrayRept("", 0);
   Local number &x, &Y;

   For &x = 1 To &parmRec.FieldCount
      If &parmRec.GetField(&x).IsKey And
            Not %This.inExceptionFieldList(&parmRec.GetField(&x).Name) And
            Not &parmRec.GetField(&x).IsAltKey And
            Not &parmRec.GetField(&x).IsDuplKey And
            &parmRec.GetField(&x).Name <> &SEQNUM Then
         &keyfields.Push(&parmRec.GetField(&x).Name);
      End-If;
   End-For;

   Local string &WhereClause;
   For &x = 1 To &keyfields.Len
      If All(&parmRec.GetField(@("FIELD." | &keyfields [&x])).Value) Then
         If &WhereClause = "" Then
            &WhereClause = "WHERE " | &keyfields [&x] | "='" | &parmRec.GetField(@("FIELD." | &keyfields [&x])).Value | "'";
         Else
            &WhereClause = &WhereClause | " AND " | &keyfields [&x] | "='" | &parmRec.GetField(@("FIELD." | &keyfields [&x])).Value | "'";
         End-If;
      End-If;
   End-For;
   WriteToLog(1, "WHERE= " | &WhereClause);

   &rs.Fill(&WhereClause | " ORDER BY " | &SEQNUM);
   If All(&rs(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &rs.ActiveRowCount
         Local Record &rec = &rs(&x).GetRecord(1);
         Local Record &updRec = CreateRecord(@("RECORD." | &rec.Name));
         &rec.CopyFieldsTo(&updRec);
         &updRec.GetField(@("FIELD." | &SEQNUM)).Value = &x;
         If &updRec.Update(&rec) Then
            WriteToLog(1, "reorder success");
         Else
            WriteToLog(1, "reorder fail");
         End-If;

      End-For;
   End-If;


end-method;

method inExceptionFieldList
   /+ &parmField as String +/
   /+ Returns Boolean +/
   Local number &x;

   For &x = 1 To &ExceptionFieldNames.Len
      If &ExceptionFieldNames [&x] = &parmField Then
         Return True;
      End-If;
   End-For;

   Return False;
end-method;

rem    Local number &x;
rem      For &x = 1 To &parmRec.FieldCount
rem         WriteToLog(1, &parmRec.GetField(&x).Name | " = " | &parmRec.GetField(&x).Value);
rem      End-For;

import CIBER_UI_FORMS:Utilities:ConfigRecordXML:*;

class RecordXML
   property CIBER_UI_FORMS:Utilities:ConfigRecordXML objCfgRecXML;

   method RecordXML(&recName As string);
   method GetXMLDoc() Returns XmlDoc;
   method setRecordKeyValues(&values As array of string);
   method addFieldPrmtKeyVals(&NameValPair As string);
   method addSubRecFieldPrmtKeyVals(&NameValPair As string);

   method initObjCfgRecXML(&parmStr As string); /* initiates ConfigRecordXML object; */

private
   instance string &RecordName;
   instance number &RowNumber;
   instance array of string &KeyFieldNames, &KeyFieldValues, &FieldPromptKeyVals, &SubRecDetails, &SubRecFieldPromptKeyVals;
   rem instance CIBER_UI_FORMS:Utilities:ConfigRecordXML &objCfgRecXML;

   method initRecordKeyFields();
   method getWhereClause() Returns string;
   method calcUSEEDIT(&parmFieldName As string) Returns array of number;
   method addDefaultValueAsAttribute(&parmNode As XmlNode);
   method addFieldAttributeToNode(&parmNode As XmlNode, &parmBitArry As array of number);
   method addFieldTypeAndLengthToNode(&parmNode As XmlNode);
   method isSubRecordField(&parmFieldName As string) Returns string;
   method getSubRecordNummber(&parmFieldName As string) Returns number;


end-class;

method RecordXML
   /+ &recName as String +/
   &RecordName = &recName;

   /* need to make sure record name is verified, then call next method */
   %This.initRecordKeyFields();

end-method;

method setRecordKeyValues
   /+ &values as Array of String +/
   Local number &x;
   If &values.Len <= &KeyFieldNames.Len Then
      For &x = 1 To &values.Len
         &KeyFieldValues.Push(&values [&x]);
      End-For;
   Else
      /* throw exception about too many values for keys */
      rem throw CreateException(2, 160, "'%1' doesn't support property or method '%2'", "SomeClass", "SomeMethod");
      throw CreateException(0, 0, "There are too many parameters listed as keys for record, '%1'", &RecordName);
   End-If;

end-method;

method GetXMLDoc
   /+ Returns XmlDoc +/
   Local number &x, &y, &rowcount;
   Local XmlDoc &retXMLDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><psrecordxml/>");
   Local XmlNode &baseNode, &thatNode, &thisNode, &recNode, &fieldsNode, &fieldNode;
   Local Record &thisRecord = CreateRecord(@("RECORD." | &RecordName));
   Local Rowset &thisRowSet;

   &thisRowSet = CreateRowset(@("RECORD." | &RecordName));
   &thisRowSet.Fill(%This.getWhereClause());

   &baseNode = &retXMLDoc.DocumentElement;
   &thatNode = &baseNode.AddElement("psrecordxmlproperties");
   &thisNode = &thatNode.AddElement("recordname");
   &thisNode.NodeValue = &RecordName;
   &thisNode = &thatNode.AddElement("fieldcount");
   &thisNode.NodeValue = String(&thisRecord.FieldCount);

   &thatNode = &baseNode.AddElement("psrecordrows");
   &thatNode.AddAttribute("count", String(&thisRowSet.ActiveRowCount));

   For &x = 1 To &thisRowSet.ActiveRowCount
      &RowNumber = &x;
      &recNode = &thatNode.AddElement("psrecord");
      &recNode.AddAttribute("rownumber", String(&x));
      &fieldsNode = &recNode.AddElement("psrecordfields");
      For &y = 1 To &thisRecord.FieldCount
         &fieldNode = &fieldsNode.AddElement("field");
         &fieldNode.AddAttribute("name", &thisRecord.GetField(&y).Name);
         &fieldNode.AddAttribute("field_number", String(&y));
         &fieldNode.AddAttribute("placeholder", &thisRecord.GetField(&y).GetLongLabel(&thisRecord.GetField(&y).Name));
         &fieldNode.AddAttribute("short_label", &thisRecord.GetField(&y).GetShortLabel(&thisRecord.GetField(&y).Name));
         If &thisRecord.GetField(&y).IsKey And
               Not &thisRecord.GetField(&y).IsAltKey And
               Not &thisRecord.GetField(&y).IsDuplKey Then
            &fieldNode.AddAttribute("pstype", "key");
         End-If;
         %This.addFieldTypeAndLengthToNode(&fieldNode);
         %This.addDefaultValueAsAttribute(&fieldNode);
         %This.addFieldAttributeToNode(&fieldNode, %This.calcUSEEDIT(&thisRecord.GetField(&y).Name));
         If &objCfgRecXML <> Null Then
            &objCfgRecXML.addFieldAttributesToNode(&fieldNode);
         End-If;
         rem insert data value to xml;
         If &x = 1 Then
            If &thisRowSet.GetRow(&x).GetRecord(1).GetField(1).Value <> "" Then
               &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
            End-If;
         Else
            &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
         End-If;

      End-For
   End-For;
   rem debug code;
   /*   For &x = 1 To &SubRecDetails.Len
      &thisNode = &thatNode.AddElement("subrecord_" | &x);
      &thisNode.NodeValue = &SubRecDetails [&x];
   End-For; */
   rem end debug code;

   Return &retXMLDoc;
end-method;

method initRecordKeyFields

   Local Rowset &psKeydefnRS = CreateRowset(Record.PSKEYDEFN);
   Local Rowset &psRecfieldRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psRecFldTargetRS = CreateRowset(Record.PSRECFIELD);
   Local number &x, &y;

   &psKeydefnRS.Fill("WHERE RECNAME=:1 AND INDEXID='_' ORDER BY KEYPOSN", &RecordName);

   &KeyFieldNames = CreateArrayRept("", &psKeydefnRS.ActiveRowCount);
   &KeyFieldValues = CreateArrayRept("", 0);

   For &x = 1 To &psKeydefnRS.ActiveRowCount
      &KeyFieldNames [&x] = &psKeydefnRS.GetRow(&x).GetRecord(1).GetField(Field.FIELDNAME).Value;
   End-For;

   &SubRecDetails = CreateArrayRept("", 0);
   rem subrecdetails holds SUBRECORD:FIELDNAME pairs;

   &psRecfieldRS.Fill("WHERE RECNAME=:1 AND SUBRECORD = 'Y' ORDER BY FIELDNUM", &RecordName);
   If All(&psRecfieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psRecfieldRS.ActiveRowCount
         &psRecFldTargetRS.Fill("WHERE RECNAME=:1 ORDER BY FIELDNUM", &psRecfieldRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value);
         For &y = 1 To &psRecFldTargetRS.ActiveRowCount
            &SubRecDetails.Push(&psRecFldTargetRS(&y).GetRecord(1).GetField(Field.RECNAME).Value | ":" | &psRecFldTargetRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value);
         End-For;
      End-For;
   End-If;

   &SubRecFieldPromptKeyVals = CreateArrayRept("", 0);
   &FieldPromptKeyVals = CreateArrayRept("", 0);

end-method;

method getWhereClause
   /+ Returns String +/
   Local string &retStr = "";
   Local number &x;

   For &x = 1 To &KeyFieldValues.Len
      If &retStr = "" Then
         &retStr = &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "'";
      Else
         &retStr = &retStr | " AND " | &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "'";
      End-If;
   End-For;

   If All(&retStr) Then
      &retStr = "WHERE " | &retStr;
   End-If;

   Return &retStr;
end-method;

method calcUSEEDIT
   /+ &parmFieldName as String +/
   /+ Returns Array of Number +/
   Local number &bitValue, &tmpValue, &x, &y;
   Local string &subRecName;
   Local array of number &bitNumbers = CreateArrayRept(0, 1);
   Local Rowset &tmpRS = CreateRowset(Record.PSRECFIELD);

   &subRecName = %This.isSubRecordField(&parmFieldName);
   If All(&subRecName) Then
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &subRecName, &parmFieldName);
   Else
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &parmFieldName);
   End-If;
   &bitValue = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.USEEDIT).Value;

   While &bitValue > 0
      /* get largest base2 number from input */
      /* set it tmp */
      &tmpValue = Int(Log10(&bitValue) / Log10(2));

      /* get remainder from largest base2 number */
      /* set it bitVal */
      &bitValue = Int(&bitValue - Exp(&tmpValue * Log10(2) * Ln(10)));


      If &bitNumbers [1] = 0 Then
         &bitNumbers [1] = &tmpValue;
      Else
         &bitNumbers.Push(&tmpValue);
      End-If;
   End-While;

   Return &bitNumbers;
end-method;

method addDefaultValueAsAttribute
   /+ &parmNode as XmlNode +/

   Local string &tmpStrRec, &tmpStrFld, &attrVal, &fldName, &subRecName;
   Local Rowset &tmpRS, &tmpRSprompt;
   Local number &x;

   &fldName = &parmNode.GetAttributeValue("name");
   &subRecName = %This.isSubRecordField(&fldName);

   If All(&subRecName) Then
      &tmpRS = CreateRowset(Record.PSRECFIELD);
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &subRecName, &fldName);
   Else
      &tmpRS = CreateRowset(Record.PSRECFIELD);
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &fldName);
   End-If;

   &tmpStrFld = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value;
   If All(&tmpStrFld) Then
      &tmpStrRec = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFRECNAME).Value;

      If All(&tmpStrRec) And
            All(&tmpStrFld) Then
         &tmpRSprompt = CreateRowset(@("RECORD." | &tmpStrRec));
         &tmpRSprompt.Fill();
         &attrVal = &tmpRSprompt.GetRow(1).GetRecord(1).GetField(@("FIELD." | &tmpStrFld)).Value;
      Else
         If All(&tmpStrFld) Then
            &attrVal = &tmpStrFld;
         End-If;
         &attrVal = ""; /* no default value */
      End-If;

      If &attrVal <> "" Then
         &parmNode.AddAttribute("default_value", &attrVal);
      End-If;
   End-If;

end-method;

method addFieldTypeAndLengthToNode
   /+ &parmNode as XmlNode +/
   Local Rowset &tmpRS;
   Local string &tmpStr;

   &tmpRS = CreateRowset(Record.PSDBFIELD);
   &tmpRS.Fill("WHERE FIELDNAME = :1", &parmNode.GetAttributeValue("name"));
   Evaluate &tmpRS.GetRow(1).GetRecord(1).GetField(Field.FIELDTYPE).Value
   When 0
      &tmpStr = "Character";
   When 1
      &tmpStr = "LongCharacter";
   When 2
      &tmpStr = "number";
   When 3
      &tmpStr = "SignedNumber";
   When 4
      &tmpStr = "Date";
   When 5
      &tmpStr = "Time";
   When 6
      &tmpStr = "DateTime";
   When 7
      &tmpStr = "NA";
   When 8
      &tmpStr = "ImageORAttachment";
   When 9
      &tmpStr = "ImageReference";
   When-Other;
      &tmpStr = "NA";
   End-Evaluate;
   &parmNode.AddAttribute("field_type", &tmpStr);
   &parmNode.AddAttribute("maxlength", &tmpRS.GetRow(1).GetRecord(1).GetField(Field.LENGTH).Value);

end-method;

method addFieldAttributeToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmBitArry as Array of Number +/

   Local number &x, &y, &z;
   Local Rowset &rs, &rsPSRECFLD, &rsPSKEYDEFN, &rsDefltRecVals;
   Local Record &tmpRec;
   Local XmlNode &tmpNode;

   For &x = 1 To &parmBitArry.Len
      Evaluate &parmBitArry [&x]
         rem When 8 /* required */
         rem &parmNode.AddAttribute("required", "true");
      When 9 /* xlat */
         &rs = CreateRowset(Record.PSXLATITEM);
         &rs.Fill("WHERE FIELDNAME = :1 AND %EffDtCheck(PSXLATITEM B,FILL,%CurrentDateIn) AND EFF_STATUS = 'A'", &parmNode.GetAttributeValue("name"));
         For &y = 1 To &rs.ActiveRowCount
            &tmpNode = &parmNode.AddElement("xlatitem");
            &tmpNode.AddAttribute("long_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATLONGNAME).Value);
            &tmpNode.AddAttribute("short_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATSHORTNAME).Value);
            &tmpNode.AddAttribute("field_value", &rs.GetRow(&y).GetRecord(1).GetField(Field.FIELDVALUE).Value);
         End-For;
      When 13 /* Y/N edit */
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "Yes");
         &tmpNode.AddAttribute("field_value", "Y");
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "No");
         &tmpNode.AddAttribute("field_value", "N");

      When 14 /* prompt table edit */
         &rsPSRECFLD = CreateRowset(Record.PSRECFIELD);

         Local string &subRecName = %This.isSubRecordField(&parmNode.GetAttributeValue("name"));

         If All(&subRecName) Then
            &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &subRecName, &parmNode.GetAttributeValue("name"));
         Else
            &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &RecordName, &parmNode.GetAttributeValue("name"));
         End-If;

         Local string &editTbl = &rsPSRECFLD(1).GetRecord(1).GetField(Field.EDITTABLE).Value;

         Local Record &editTblRec = CreateRecord(@("RECORD." | &editTbl));
         Local array of string &editTblKeys = CreateArrayRept("", 0);
         For &y = 1 To &editTblRec.FieldCount
            If &editTblRec.GetField(&y).IsKey And
                  Not &editTblRec.GetField(&y).IsAltKey And
                  Not &editTblRec.GetField(&y).IsDuplKey Then
               &editTblKeys.Push(&editTblRec.GetField(&y).Name);
            End-If;
         End-For;

         rem only insert all keys except last key, so pop last one out;
         If &editTblKeys.Len > 0 Then
            Local string &tmpNull = &editTblKeys.Pop();
         End-If;

         Local array of string &valsForWhereClause = CreateArrayRept("", 0);
         Local array of string &namesForWhereClause = CreateArrayRept("", 0);
         rem if subrecord then match keys;
         If All(&subRecName) Then
            For &y = 1 To &SubRecFieldPromptKeyVals.Len
               Local string &currentField, &NameFromPair, &ValueFromPair;
               Local number &subRecNum = %This.getSubRecordNummber(&parmNode.GetAttributeValue("name"));

               For &z = 1 To &editTblKeys.Len
                  &currentField = "SBR" | &subRecNum | ":" | &editTbl | "." | &editTblKeys [&z];

                  &NameFromPair = Substring(&SubRecFieldPromptKeyVals [&y], 1, Find("=", &SubRecFieldPromptKeyVals [&y]) - 1);
                  &ValueFromPair = Substring(&SubRecFieldPromptKeyVals [&y], Find("=", &SubRecFieldPromptKeyVals [&y]) + 1, Len(&SubRecFieldPromptKeyVals [&y]));

                  If &NameFromPair = &currentField And
                        All(&ValueFromPair) Then
                     &namesForWhereClause.Push(&NameFromPair);
                     &valsForWhereClause.Push(&ValueFromPair);
                  Else
                     rem codeline for fetching values from default record/field first row to be tied to this key;

                     rem codeline for using default values from target field;
                  End-If;
               End-For;
            End-For;
         Else
            rem regular prompt table;
            For &y = 1 To &FieldPromptKeyVals.Len
               Local string &currentField2, &NameFromPair2, &ValueFromPair2;

               For &z = 1 To &editTblKeys.Len
                  &currentField2 = &editTbl | "." | &editTblKeys [&z];
                  &NameFromPair2 = Substring(&FieldPromptKeyVals [&y], 1, Find("=", &FieldPromptKeyVals [&y]) - 1);
                  &ValueFromPair2 = Substring(&FieldPromptKeyVals [&y], Find("=", &FieldPromptKeyVals [&y]) + 1, Len(&FieldPromptKeyVals [&y]));
               End-For;

               If &NameFromPair2 = &currentField2 And
                     All(&ValueFromPair2) Then
                  &namesForWhereClause.Push(&NameFromPair2);
                  &valsForWhereClause.Push(&ValueFromPair2);
               Else
                  rem codeline for fetching values from default record/field first row to be tied to this key;

                  rem codeline for using default values from target field;
               End-If;
            End-For;


         End-If;

         rem parse out "FIELD." items;
         rem format:  FIELD.COUNTRY|GBR    ;
         For &y = 1 To &valsForWhereClause.Len
            If Find("FIELD.", &valsForWhereClause [&y]) = 1 Then
               Local string &tmpFldName1 = Substring(&valsForWhereClause [&y], 7, Find("|", &valsForWhereClause [&y]) - 7);
               Local string &tmpDflt1 = Substring(&valsForWhereClause [&y], Find("|", &valsForWhereClause [&y]) + 1, Len(&valsForWhereClause [&y]));
               Local Rowset &tmpRs1 = CreateRowset(@("RECORD." | &RecordName));
               &tmpRs1.Fill(%This.getWhereClause());
               Local string &tmpFldVal = &tmpRs1(&RowNumber).GetRecord(1).GetField(@("FIELD." | &tmpFldName1)).Value;

               If All(&tmpFldVal) Then
                  &valsForWhereClause [&y] = &tmpFldVal;
               Else
                  &valsForWhereClause [&y] = &tmpDflt1;
               End-If;

            End-If;
         End-For;

         rem create WHERE clause;
         Local string &WHEREstr;
         If &valsForWhereClause.Len > 0 Then
            For &y = 1 To &valsForWhereClause.Len
               Local string &tmpName = Substring(&namesForWhereClause [&y], Find(".", &namesForWhereClause [&y]) + 1, Len(&namesForWhereClause [&y]));
               If All(&WHEREstr) Then
                  &WHEREstr = &WHEREstr | " AND " | &tmpName | " = '" | &valsForWhereClause [&y] | "'";
               Else
                  &WHEREstr = "WHERE " | &tmpName | " = '" | &valsForWhereClause [&y] | "'";
               End-If;
            End-For;
         End-If;

         &rs = CreateRowset(@("RECORD." | &editTbl));
         &rs.Fill(&WHEREstr);

         If All(&rs(1).GetRecord(1).GetField(1).Value) Then
            For &y = 1 To &rs.ActiveRowCount
               &tmpNode = &parmNode.AddElement("promptitem");
               For &z = 1 To &rs(&y).GetRecord(1).FieldCount
                  &tmpNode.AddAttribute(&rs.GetRow(&y).GetRecord(1).GetField(&z).Name, &rs.GetRow(&y).GetRecord(1).GetField(&z).Value);
               End-For;
            End-For;
         End-If;

      When-Other;

      End-Evaluate;

   End-For;

end-method;

method isSubRecordField
   /+ &parmFieldName as String +/
   /+ Returns String +/

   Local number &x;
   Local string &RecName;

   &x = 1;
   While &SubRecDetails.Len >= &x
      If Substring(&SubRecDetails [&x], Find(":", &SubRecDetails [&x]) + 1, Len(&SubRecDetails [&x])) = &parmFieldName Then
         &RecName = Substring(&SubRecDetails [&x], 1, Find(":", &SubRecDetails [&x]) - 1);
         Return &RecName;
      End-If;
      &x = &x + 1;
   End-While;

   Return &RecName;

end-method;

method getSubRecordNummber
   /+ &parmFieldName as String +/
   /+ Returns Number +/

   Local Rowset &psrecfieldRS = CreateRowset(Record.PSRECFIELD);
   Local Record &tmpREC;
   Local number &x, &y;

   &psrecfieldRS.Fill("WHERE RECNAME = :1 AND SUBRECORD = 'Y' ORDER BY FIELDNUM", &RecordName);
   If All(&psrecfieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psrecfieldRS.ActiveRowCount
         &tmpREC = CreateRecord(@("RECORD." | &psrecfieldRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value));
         For &y = 1 To &tmpREC.FieldCount
            If &tmpREC.GetField(&y).Name = &parmFieldName Then
               Return &x;
            End-If;
         End-For;
      End-For;
   End-If;

   Return 0;
end-method;

method addFieldPrmtKeyVals
   /+ &NameValPair as String +/
   &FieldPromptKeyVals.Push(&NameValPair);
end-method;

method addSubRecFieldPrmtKeyVals
   /+ &NameValPair as String +/
   &SubRecFieldPromptKeyVals.Push(&NameValPair);
end-method;

method initObjCfgRecXML
   /+ &parmStr as String +/
   &objCfgRecXML = create CIBER_UI_FORMS:Utilities:ConfigRecordXML(&parmStr);

end-method;
