import CIBER_UI_FORMS:Utilities:ConfigRecordXML:*;

class RecordXML
   property CIBER_UI_FORMS:Utilities:ConfigRecordXML objCfgRecXML;

   method RecordXML(&recName As string);
   method GetXMLDoc() Returns XmlDoc;
   method setRecordKeyValues(&values As array of string);
   method addSubRecFieldPrmtKeyVals(&NameValPair As string);

   method initObjCfgRecXML(&parmStr As string); /* initiates ConfigRecordXML object; */

private
   instance string &RecordName;
   instance array of string &KeyFieldNames, &KeyFieldValues, &SubRecDetails, &SubRecFieldPromptKeyVals;
   rem instance CIBER_UI_FORMS:Utilities:ConfigRecordXML &objCfgRecXML;

   method initRecordKeyFields();
   method getWhereClause() Returns string;
   method calcUSEEDIT(&parmFieldName As string) Returns array of number;
   method addDefaultValueAsAttribute(&parmNode As XmlNode);
   method addFieldAttributeToNode(&parmNode As XmlNode, &parmBitArry As array of number);
   method addFieldTypeAndLengthToNode(&parmNode As XmlNode);
   method isSubRecordField(&parmFieldName As string) Returns string;

end-class;

method RecordXML
   /+ &recName as String +/
   &RecordName = &recName;

   /* need to make sure record name is verified, then call next method */
   %This.initRecordKeyFields();

end-method;

method setRecordKeyValues
   /+ &values as Array of String +/
   Local number &x;
   If &values.Len <= &KeyFieldValues.Len Then
      For &x = 1 To &values.Len
         &KeyFieldValues [&x] = &values [&x];
      End-For;
   Else
      /* throw exception about too many values for keys */
      rem throw CreateException(2, 160, "'%1' doesn't support property or method '%2'", "SomeClass", "SomeMethod");
      throw CreateException(0, 0, "There are too many parameters listed as keys for record, '%1'", &RecordName);
   End-If;

end-method;

method GetXMLDoc
   /+ Returns XmlDoc +/
   Local number &x, &y, &rowcount;
   Local XmlDoc &retXMLDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><psrecordxml/>");
   Local XmlNode &baseNode, &thatNode, &thisNode, &recNode, &fieldsNode, &fieldNode;
   Local Record &thisRecord = CreateRecord(@("RECORD." | &RecordName));
   Local Rowset &thisRowSet;

   &thisRowSet = CreateRowset(@("RECORD." | &RecordName));
   &thisRowSet.Fill(%This.getWhereClause());

   &baseNode = &retXMLDoc.DocumentElement;
   &thatNode = &baseNode.AddElement("psrecordxmlproperties");
   &thisNode = &thatNode.AddElement("recordname");
   &thisNode.NodeValue = &RecordName;
   &thisNode = &thatNode.AddElement("fieldcount");
   &thisNode.NodeValue = String(&thisRecord.FieldCount);

   &thatNode = &baseNode.AddElement("psrecordrows");
   &thatNode.AddAttribute("count", String(&thisRowSet.ActiveRowCount));

   For &x = 1 To &thisRowSet.ActiveRowCount
      &recNode = &thatNode.AddElement("psrecord");
      &recNode.AddAttribute("rownumber", String(&x));
      &fieldsNode = &recNode.AddElement("psrecordfields");
      For &y = 1 To &thisRecord.FieldCount
         &fieldNode = &fieldsNode.AddElement("field");
         &fieldNode.AddAttribute("name", &thisRecord.GetField(&y).Name);
         &fieldNode.AddAttribute("field_number", String(&y));
         &fieldNode.AddAttribute("long_label", &thisRecord.GetField(&y).GetLongLabel(&thisRecord.GetField(&y).Name));
         &fieldNode.AddAttribute("short_label", &thisRecord.GetField(&y).GetShortLabel(&thisRecord.GetField(&y).Name));
         %This.addFieldTypeAndLengthToNode(&fieldNode);
         %This.addDefaultValueAsAttribute(&fieldNode);
         %This.addFieldAttributeToNode(&fieldNode, %This.calcUSEEDIT(&thisRecord.GetField(&y).Name));
         If &objCfgRecXML <> Null Then
            &objCfgRecXML.addFieldAttributesToNode(&fieldNode);
         End-If;
         rem insert data value to xml;
         If &x = 1 Then
            If &thisRowSet.GetRow(&x).GetRecord(1).GetField(1).Value <> "" Then
               &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
            End-If;
         Else
            &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
         End-If;

      End-For
   End-For;
   rem debug code;
   /*   For &x = 1 To &SubRecDetails.Len
      &thisNode = &thatNode.AddElement("subrecord_" | &x);
      &thisNode.NodeValue = &SubRecDetails [&x];
   End-For; */
   rem end debug code;

   Return &retXMLDoc;
end-method;

method initRecordKeyFields

   Local Rowset &psKeydefnRS = CreateRowset(Record.PSKEYDEFN);
   Local Rowset &psRecfieldRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psRecFldTargetRS = CreateRowset(Record.PSRECFIELD);
   Local number &x, &y;

   &psKeydefnRS.Fill("WHERE RECNAME=:1 AND INDEXID=:2", &RecordName, "_");

   &psKeydefnRS.Sort(PSKEYDEFN.KEYPOSN, "A");
   &KeyFieldNames = CreateArrayRept("", &psKeydefnRS.ActiveRowCount);
   &KeyFieldValues = CreateArrayRept("", &psKeydefnRS.ActiveRowCount);

   For &x = 1 To &psKeydefnRS.ActiveRowCount
      &KeyFieldNames [&x] = &psKeydefnRS.GetRow(&x).GetRecord(1).GetField(Field.FIELDNAME).Value;
   End-For;

   &SubRecDetails = CreateArrayRept("", 0);

   &psRecfieldRS.Fill("WHERE RECNAME=:1 AND SUBRECORD = 'Y' ORDER BY FIELDNUM", &RecordName);
   If All(&psRecfieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psRecfieldRS.ActiveRowCount
         &psRecFldTargetRS.Fill("WHERE RECNAME=:1 ORDER BY FIELDNUM", &psRecfieldRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value);
         For &y = 1 To &psRecFldTargetRS.ActiveRowCount
            &SubRecDetails.Push(&psRecFldTargetRS(&y).GetRecord(1).GetField(Field.RECNAME).Value | ":" | &psRecFldTargetRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value);
         End-For;
      End-For;
   End-If;

   &SubRecFieldPromptKeyVals = CreateArrayRept("", 0);

end-method;

method getWhereClause
   /+ Returns String +/
   Local string &retStr = "";
   Local number &x;

   For &x = 1 To &KeyFieldValues.Len
      If &retStr = "" Then
         &retStr = &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "'";
      Else
         &retStr = &retStr | " AND " | &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "'";
      End-If;
   End-For;

   If All(&retStr) Then
      &retStr = "WHERE " | &retStr;
   End-If;

   Return &retStr;
end-method;

method calcUSEEDIT
   /+ &parmFieldName as String +/
   /+ Returns Array of Number +/
   Local number &bitValue, &tmpValue, &x, &y;
   Local string &subRecName;
   Local array of number &bitNumbers = CreateArrayRept(0, 1);
   Local Rowset &tmpRS = CreateRowset(Record.PSRECFIELD);

   &subRecName = %This.isSubRecordField(&parmFieldName);
   If All(&subRecName) Then
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &subRecName, &parmFieldName);
   Else
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &parmFieldName);
   End-If;
   &bitValue = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.USEEDIT).Value;

   While &bitValue > 0
      /* get largest base2 number from input */
      /* set it tmp */
      &tmpValue = Int(Log10(&bitValue) / Log10(2));

      /* get remainder from largest base2 number */
      /* set it bitVal */
      &bitValue = Int(&bitValue - Exp(&tmpValue * Log10(2) * Ln(10)));


      If &bitNumbers [1] = 0 Then
         &bitNumbers [1] = &tmpValue;
      Else
         &bitNumbers.Push(&tmpValue);
      End-If;
   End-While;

   Return &bitNumbers;
end-method;

method addDefaultValueAsAttribute
   /+ &parmNode as XmlNode +/

   Local string &tmpStrRec, &tmpStrFld, &attrVal, &fldName, &subRecName;
   Local Rowset &tmpRS, &tmpRSprompt;
   Local number &x;

   &fldName = &parmNode.GetAttributeValue("name");
   &subRecName = %This.isSubRecordField(&fldName);

   If All(&subRecName) Then
      &tmpRS = CreateRowset(Record.PSRECFIELD);
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &subRecName, &fldName);
   Else
      &tmpRS = CreateRowset(Record.PSRECFIELD);
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &fldName);
   End-If;

   &tmpStrFld = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value;
   If All(&tmpStrFld) Then
      &tmpStrRec = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFRECNAME).Value;

      If All(&tmpStrRec) And
            All(&tmpStrFld) Then
         &tmpRSprompt = CreateRowset(@("RECORD." | &tmpStrRec));
         &tmpRSprompt.Fill();
         &attrVal = &tmpRSprompt.GetRow(1).GetRecord(1).GetField(@("FIELD." | &tmpStrFld)).Value;
      Else
         If All(&tmpStrFld) Then
            &attrVal = &tmpStrFld;
         End-If;
         &attrVal = ""; /* no default value */
      End-If;

      If &attrVal <> "" Then
         &parmNode.AddAttribute("default_value", &attrVal);
      End-If;
   End-If;

end-method;

method addFieldTypeAndLengthToNode
   /+ &parmNode as XmlNode +/
   Local Rowset &tmpRS;
   Local string &tmpStr;

   &tmpRS = CreateRowset(Record.PSDBFIELD);
   &tmpRS.Fill("WHERE FIELDNAME = :1", &parmNode.GetAttributeValue("name"));
   Evaluate &tmpRS.GetRow(1).GetRecord(1).GetField(Field.FIELDTYPE).Value
   When 0
      &tmpStr = "Character";
   When 1
      &tmpStr = "LongCharacter";
   When 2
      &tmpStr = "Number";
   When 3
      &tmpStr = "SignedNumber";
   When 4
      &tmpStr = "Date";
   When 5
      &tmpStr = "Time";
   When 6
      &tmpStr = "DateTime";
   When 7
      &tmpStr = "NA";
   When 8
      &tmpStr = "ImageORAttachment";
   When 9
      &tmpStr = "ImageReference";
   When-Other;
      &tmpStr = "NA";
   End-Evaluate;
   &parmNode.AddAttribute("field_type", &tmpStr);
   &parmNode.AddAttribute("field_length", &tmpRS.GetRow(1).GetRecord(1).GetField(Field.LENGTH).Value);

end-method;

method addFieldAttributeToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmBitArry as Array of Number +/

   Local number &x, &y, &z;
   Local Rowset &rs, &rsPSRECFLD, &rsPSKEYDEFN, &rsDefltRecVals;
   Local XmlNode &tmpNode;
   Local string &tmpStr;

   For &x = 1 To &parmBitArry.Len
      Evaluate &parmBitArry [&x]
      When 8 /* required */
         rem &parmNode.AddAttribute("required", "true");
      When 9 /* xlat */
         &rs = CreateRowset(Record.PSXLATITEM);
         &rs.Fill("WHERE FIELDNAME = :1 AND %EffDtCheck(PSXLATITEM B,FILL,%CurrentDateIn) AND EFF_STATUS = 'A'", &parmNode.GetAttributeValue("name"));
         For &y = 1 To &rs.ActiveRowCount
            &tmpNode = &parmNode.AddElement("xlatitem");
            &tmpNode.AddAttribute("long_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATLONGNAME).Value);
            &tmpNode.AddAttribute("short_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATSHORTNAME).Value);
            &tmpNode.AddAttribute("field_value", &rs.GetRow(&y).GetRecord(1).GetField(Field.FIELDVALUE).Value);
         End-For;
      When 13 /* Y/N edit */
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "Yes");
         &tmpNode.AddAttribute("field_value", "Y");
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "No");
         &tmpNode.AddAttribute("field_value", "N");

      When 14 /* prompt table edit */
         &rsPSRECFLD = CreateRowset(Record.PSRECFIELD);
         Local string &subRecName = %This.isSubRecordField(&parmNode.GetAttributeValue("name"));
         If All(&subRecName) Then
            &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &subRecName, &parmNode.GetAttributeValue("name"));
         Else
            &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &RecordName, &parmNode.GetAttributeValue("name"));
         End-If;
         &tmpStr = &rsPSRECFLD(1).GetRecord(1).GetField(Field.EDITTABLE).Value;

         &rs = CreateRowset(@("RECORD." | &tmpStr));

         /* check for effective date and effective status ??*/

         /* check the prompt table for default values for keys and use them for keys to fill */
         /* find key, then search against psrecfield */
         &rsPSKEYDEFN = CreateRowset(Record.PSKEYDEFN);
         &rsPSKEYDEFN.Fill("WHERE INDEXID='_' AND RECNAME = :1 ORDER BY KEYPOSN", &tmpStr);

         Local array of string &dfltVals = CreateArrayRept("", 0);
         Local string &WHEREstr;

         If &rsPSKEYDEFN.ActiveRowCount > 1 Then
            For &y = 1 To &rsPSKEYDEFN.ActiveRowCount - 1

               &rsPSRECFLD = CreateRowset(Record.PSRECFIELD);
               &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &tmpStr, &rsPSKEYDEFN(&y).GetRecord(1).GetField(Field.FIELDNAME).Value);

               rem check pagelet wizard setup data first for key value, if not then do default checks;
               rem if VARIABLE is not empty, then set defltval;
               For &z = 1 To &SubRecFieldPromptKeyVals.Len

                  Local string &currentField, &NameFromPair, &ValueFromPair;
                  &currentField = &subRecName | "." | &parmNode.GetAttributeValue("name") | ":" | &rsPSKEYDEFN(&y).GetRecord(1).GetField(Field.RECNAME).Value | "." | &rsPSKEYDEFN(&y).GetRecord(1).GetField(Field.FIELDNAME).Value;
                  &NameFromPair = Substring(&SubRecFieldPromptKeyVals [&z], 1, Find("=", &SubRecFieldPromptKeyVals [&z]) - 1);
                  &ValueFromPair = Substring(&SubRecFieldPromptKeyVals [&z], Find("=", &SubRecFieldPromptKeyVals [&z]) + 1, Len(&SubRecFieldPromptKeyVals [&z]));

                  rem if prompt field eq current prompt field, AND if valuefrompair has something, then push into array;
                  If &NameFromPair = &currentField And
                        All(&ValueFromPair) Then
                     &dfltVals.Push(&ValueFromPair);
                  Else
                     rem check if default record and def field exist and use first row from that record as key value;
                     If All(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFRECNAME).Value) And
                           All(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value) Then
                        try
                           &rsDefltRecVals = CreateRowset(@("RECORD." | &rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFRECNAME).Value));
                           &rsDefltRecVals.Fill();
                           &dfltVals.Push(&rsDefltRecVals(1).GetRecord(1).GetField(@("FIELD." | &rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value)).Value);

                        catch Exception &e1

                        end-try;
                     Else
                        rem check if def field exists and use that value as key value;
                        If All(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value) Then
                           &dfltVals.Push(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value);
                        End-If;
                     End-If;
                  End-If;

               End-For;

            End-For;

            rem debug hardcoded value;
            rem &dfltVals = CreateArrayRept("", 0);
            rem &dfltVals.Push("USA");
         End-If;

         If &dfltVals.Len > 0 Then
            rem check if dfltvals.len equals to pskeydefn number of rows?;

            For &y = 1 To &dfltVals.Len
               If All(&WHEREstr) Then
                  &WHEREstr = &WHEREstr | " AND " | &rsPSKEYDEFN(&y).GetRecord(1).GetField(Field.FIELDNAME).Value | " = '" | &dfltVals [&y] | "'";
               Else
                  &WHEREstr = "WHERE " | &rsPSKEYDEFN(&y).GetRecord(1).GetField(Field.FIELDNAME).Value | " = '" | &dfltVals [&y] | "'";
               End-If;
            End-For;
         End-If;

         &rs.Fill(&WHEREstr);

         If All(&rs(1).GetRecord(1).GetField(1).Value) Then
            For &y = 1 To &rs.ActiveRowCount
               &tmpNode = &parmNode.AddElement("promptitem");
               For &z = 1 To &rs(&y).GetRecord(1).FieldCount
                  &tmpNode.AddAttribute(&rs.GetRow(&y).GetRecord(1).GetField(&z).Name, &rs.GetRow(&y).GetRecord(1).GetField(&z).Value);
               End-For;
            End-For;
         End-If;

      When-Other;

      End-Evaluate;

   End-For;

end-method;

method isSubRecordField
   /+ &parmFieldName as String +/
   /+ Returns String +/

   Local number &x;
   Local string &RecName;

   &x = 1;
   While &SubRecDetails.Len >= &x
      If Substring(&SubRecDetails [&x], Find(":", &SubRecDetails [&x]) + 1, Len(&SubRecDetails [&x])) = &parmFieldName Then
         &RecName = Substring(&SubRecDetails [&x], 1, Find(":", &SubRecDetails [&x]) - 1);
         Return &RecName;
      End-If;
      &x = &x + 1;
   End-While;

   Return &RecName;

end-method;

method addSubRecFieldPrmtKeyVals
   /+ &NameValPair as String +/
   &SubRecFieldPromptKeyVals.Push(&NameValPair);
end-method;

method initObjCfgRecXML
   /+ &parmStr as String +/
   &objCfgRecXML = create CIBER_UI_FORMS:Utilities:ConfigRecordXML(&parmStr);

end-method;
