instance.peoplecode_text:
class RecordXML
   method RecordXML(&recName As string);
   method GetXMLDoc() Returns XmlDoc;
   method setRecordKeyValues(&values As array of string);


private
   instance string &RecordName;
   instance array of string &KeyFieldNames, &KeyFieldValues;

   method initRecordKeyFields();
   method getWhereClause() Returns string;
   method calcUSEEDIT(&parmFieldName As string) Returns array of number;
   method addDefaultValueAsAttribute(&parmNode As XmlNode);
   method addFieldAttributeToNode(&parmNode As XmlNode, &parmBitArry As array of number);
   method addFieldTypeAndLengthToNode(&parmNode As XmlNode);

end-class;

method RecordXML
   /+ &recName as String +/
   &RecordName = &recName;

   /* need to make sure record name is verified, then call next method */
   %This.initRecordKeyFields();

end-method;

method setRecordKeyValues
   /+ &values as Array of String +/
   Local number &x;
   If &values.Len <= &KeyFieldValues.Len Then
      For &x = 1 To &values.Len
         &KeyFieldValues [&x] = &values [&x];
      End-For;
   Else
      /* throw exception about too many values for keys */
   End-If;

end-method;

method GetXMLDoc
   /+ Returns XmlDoc +/
   Local number &x, &y;
   Local XmlDoc &retXMLDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><psrecordxml/>");
   Local XmlNode &baseNode, &thatNode, &thisNode, &recNode, &fieldsNode, &fieldNode;
   Local Record &thisRecord = CreateRecord(@("RECORD." | &RecordName));
   Local Field &thisField;
   Local Rowset &thisRowSet;

   &thisRowSet = CreateRowset(@("RECORD." | &RecordName));
   &thisRowSet.Fill(%This.getWhereClause());

   &baseNode = &retXMLDoc.DocumentElement;
   &thatNode = &baseNode.AddElement("psrecordxmlproperties");
   &thisNode = &thatNode.AddElement("recordname");
   &thisNode.NodeValue = &RecordName;
   &thisNode = &thatNode.AddElement("fieldcount");
   &thisNode.NodeValue = String(&thisRecord.FieldCount);

   &thatNode = &baseNode.AddElement("psrecordrows");
   &thatNode.AddAttribute("count", String(&thisRowSet.ActiveRowCount));

   For &x = 1 To &thisRowSet.ActiveRowCount
      &recNode = &thatNode.AddElement("psrecord");
      &recNode.AddAttribute("rownumber", String(&x));
      &fieldsNode = &recNode.AddElement("psrecordfields");
      For &y = 1 To &thisRecord.FieldCount
         &fieldNode = &fieldsNode.AddElement("field");
         &fieldNode.AddAttribute("name", &thisRecord.GetField(&y).Name);
         &fieldNode.AddAttribute("field_number", String(&y));
         &fieldNode.AddAttribute("long_label", &thisRecord.GetField(&y).GetLongLabel(&thisRecord.GetField(&y).Name));
         &fieldNode.AddAttribute("short_label", &thisRecord.GetField(&y).GetShortLabel(&thisRecord.GetField(&y).Name));
         %This.addFieldTypeAndLengthToNode(&fieldNode);
         %This.addDefaultValueAsAttribute(&fieldNode);
         %This.addFieldAttributeToNode(&fieldNode, %This.calcUSEEDIT(&thisRecord.GetField(&y).Name));

         If &x = 1 Then
            If &thisRowSet.GetRow(&x).GetRecord(1).GetField(1).Value <> "" Then
               &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
            End-If;
         Else
            &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
         End-If;

      End-For
   End-For;

   Return &retXMLDoc;
end-method;

method initRecordKeyFields

   Local Rowset &pskeydefnRS = CreateRowset(Record.PSKEYDEFN);
   Local number &x;

   &pskeydefnRS.Fill("WHERE RECNAME=:1 AND INDEXID=:2", &RecordName, "_");

   &pskeydefnRS.Sort(PSKEYDEFN.KEYPOSN, "A");
   &KeyFieldNames = CreateArrayRept("", &pskeydefnRS.ActiveRowCount);
   &KeyFieldValues = CreateArrayRept("", &pskeydefnRS.ActiveRowCount);

   For &x = 1 To &pskeydefnRS.ActiveRowCount
      &KeyFieldNames [&x] = &pskeydefnRS.GetRow(&x).GetRecord(1).GetField(Field.FIELDNAME).Value;
   End-For;

end-method;

method getWhereClause
   /+ Returns String +/
   Local string &retStr = "WHERE ";
   Local number &x;

   For &x = 1 To &KeyFieldValues.Len
      If Not None(&KeyFieldValues [&x]) Then
         If &retStr = "WHERE " Then
            &retStr = &retStr | &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "' ";
         Else
            &retStr = &retStr | "AND " | &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "' ";
         End-If;
      End-If;
   End-For;

   Return &retStr;
end-method;

method calcUSEEDIT
   /+ &parmFieldName as String +/
   /+ Returns Array of Number +/
   Local number &bitValue, &tmpValue, &x, &y;
   Local array of number &bitNumbers = CreateArrayRept(0, 1);
   Local Rowset &tmpRS = CreateRowset(Record.PSRECFIELD);
   &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &parmFieldName);
   &bitValue = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.USEEDIT).Value;

   While &bitValue > 0
      /* get largest base2 number from input */
      /* set it tmp */
      &tmpValue = Int(Log10(&bitValue) / Log10(2));

      /* get remainder from largest base2 number */
      /* set it bitVal */
      &bitValue = Int(&bitValue - Exp(&tmpValue * Log10(2) * Ln(10)));


      If &bitNumbers [1] = 0 Then
         &bitNumbers [1] = &tmpValue;
      Else
         &bitNumbers.Push(&tmpValue);
      End-If;
   End-While;

   Return &bitNumbers;
end-method;

method addDefaultValueAsAttribute
   /+ &parmNode as XmlNode +/
   Local string &tmpStr;
   Local Rowset &tmpRS, &MainRS;

   &MainRS = CreateRowset(@("RECORD." | &RecordName));
   &MainRS.GetRow(1).GetRecord(1).SetDefault();

   &tmpRS = CreateRowset(Record.PSRECFIELD);
   &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &parmNode.GetAttributeValue("name"));

   &tmpStr = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value;
   If All(&tmpStr) Then
      &parmNode.AddAttribute("default_value", &MainRS.GetRow(1).GetRecord(1).GetField(@("FIELD." | &parmNode.GetAttributeValue("name"))).Value);
   End-If;

end-method;

method addFieldTypeAndLengthToNode
   /+ &parmNode as XmlNode +/
   Local Rowset &tmpRS;
   Local string &tmpStr;

   &tmpRS = CreateRowset(Record.PSDBFIELD);
   &tmpRS.Fill("WHERE FIELDNAME = :1", &parmNode.GetAttributeValue("name"));
   Evaluate &tmpRS.GetRow(1).GetRecord(1).GetField(Field.FIELDTYPE).Value
   When 0
      &tmpStr = "Character";
   When 1
      &tmpStr = "LongCharacter";
   When 2
      &tmpStr = "Number";
   When 3
      &tmpStr = "SignedNumber";
   When 4
      &tmpStr = "Date";
   When 5
      &tmpStr = "Time";
   When 6
      &tmpStr = "DateTime";
   When 7
      &tmpStr = "NA";
   When 8
      &tmpStr = "ImageORAttachment";
   When 9
      &tmpStr = "ImageReference";
   When-Other;
      &tmpStr = "NA";
   End-Evaluate;
   &parmNode.AddAttribute("field_type", &tmpStr);
   &parmNode.AddAttribute("field_length", &tmpRS.GetRow(1).GetRecord(1).GetField(Field.LENGTH).Value);

end-method;

method addFieldAttributeToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmBitArry as Array of Number +/

   Local number &x, &y;
   Local Rowset &rs;
   Local XmlNode &tmpNode;
   Local string &tmpStr;

   For &x = 1 To &parmBitArry.Len
      Evaluate &parmBitArry [&x]
      When 8 /* required */
         &parmNode.AddAttribute("required", "true");
      When 9 /* xlat */
         &rs = CreateRowset(Record.PSXLATITEM);
         &rs.Fill("WHERE FIELDNAME = :1 AND %EffDtCheck(PSXLATITEM B,FILL,%CurrentDateIn) AND EFF_STATUS = 'A'", &parmNode.GetAttributeValue("name"));
         For &y = 1 To &rs.ActiveRowCount
            &tmpNode = &parmNode.AddElement("xlatitem");
            &tmpNode.AddAttribute("long_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATLONGNAME).Value);
            &tmpNode.AddAttribute("short_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATSHORTNAME).Value);
            &tmpNode.AddAttribute("field_value", &rs.GetRow(&y).GetRecord(1).GetField(Field.FIELDVALUE).Value);
         End-For;
      When 13 /* Y/N edit */
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "Yes");
         &tmpNode.AddAttribute("field_value", "Y");
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "No");
         &tmpNode.AddAttribute("field_value", "N");

      When 14 /* prompt table edit */
         SQLExec("SELECT EDITTABLE FROM PSRECFIELD WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &parmNode.GetAttributeValue("name"), &tmpStr);
         &rs = CreateRowset(@("RECORD." | &tmpStr));
         /* check for effective date and effective status !!!!! */
         &rs.Fill();
         For &y = 1 To &rs.ActiveRowCount
            &tmpNode = &parmNode.AddElement("promptitem");
            &tmpNode.AddAttribute("first_field", &rs.GetRow(&y).GetRecord(1).GetField(1).Value);
            &tmpNode.AddAttribute("second_field", &rs.GetRow(&y).GetRecord(1).GetField(2).Value);
            &tmpNode.AddAttribute("third_field", &rs.GetRow(&y).GetRecord(1).GetField(3).Value);
            &tmpNode.AddAttribute("fourth_field", &rs.GetRow(&y).GetRecord(1).GetField(4).Value);
            rem &tmpNode.AddAttribute("fifth_field", &rs.GetRow(&y).GetRecord(1).GetField(5).Value);
         End-For;

      When-Other;

      End-Evaluate;

   End-For;

end-method;
