instance.peoplecode_text:
import PTPPB_PAGELET:UTILITY:*;
import PTPPB_PAGELET:DataSource:*;
import PTPPB_PAGELET:EXCEPTION:*;
import CIBER_UI_FORMS:Utilities:RecordXML:*;

/** 
  *  DataSource to produce XML structure based on PS Record
  */
class PSRecordDataSource extends PTPPB_PAGELET:DataSource:DataSource
   property PTPPB_PAGELET:UTILITY:Setting PSRecord readonly;

   method PSRecordDataSource(&id_param As string);
   method execute() Returns string;
   method Clone() Returns object;
   method initializeSettings(&NewSettings As PTPPB_PAGELET:UTILITY:Collection);
   method processSettingsChange();
   method onPageletSave();
   method onPageletDelete();

private

   Constant &NUMBER_OF_PARMS = 5;
   instance array of string &FieldsWithPrompt;
   method getFieldsWithPrompt() Returns array of string;

end-class;

/**
  * Constructor
  */
method PSRecordDataSource
   /+ &id_param as String +/
   %Super = create PTPPB_PAGELET:DataSource:DataSource(&id_param);
   %This.setObjectSubType("PSRecord");
   %This.setCanHaveParameters( True);
   %This.initializeSettings(%This.Settings);
   rem   %This.allowGrouplet = True;
end-method;

/**
  Returns PS Record XML
  */
method execute
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.execute +/

   Local string &strRecName, &retStr;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If %This.Settings <> Null Then
      &strRecName = %This.Settings.getItemByID("PSRecord").Value;
   End-If;

   If &strRecName = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &strRecName = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;
   rem   Return "<?xml version='1.0'?><psrecordxml><psrecordxmlproperties><recordname>" | &strRecName | "</recordname><fieldcount>10</fieldcount></psrecordxmlproperties></psrecordxml>";

   If &strRecName <> "" Then
      Local CIBER_UI_FORMS:Utilities:RecordXML &objRecXML = create CIBER_UI_FORMS:Utilities:RecordXML(&strRecName);

      /* get keys for record and set */
      /* see how many parameter collection items there are and then create array based on that number */

      /* fill array with param collec items - order is important */
      Local array of string &aryKeys = CreateArrayRept("", 0);
      Local array of string &aryEvaVals = CreateArrayRept("", 0);
      Local string &val, &ev_val;
      Local number &x;

      For &x = 1 To &NUMBER_OF_PARMS
         &val = "";
         &val = %This.ParameterCollection.getItemByID(".PARAMETER" | &x).value;

         If All(&val) Then
            &ev_val = %This.ParameterCollection.getItemByID(".PARAMETER" | &x).evaluatedValue();

            If &val = &ev_val Then
               /* add query string parameter value */
               &aryKeys.Push(&val);
               &aryEvaVals.Push(%Request.GetParameter(&val));
            Else
               /* add evaluated value */
               &aryKeys.Push(&val);
               &aryEvaVals.Push(&ev_val);
            End-If;
         End-If;

      End-For;

      rem      Return "<?xml version='1.0'?><psrecordxml><recordname>" | &aryEvaVals.Len | "</recordname></psrecordxml>";

      If &aryEvaVals.Len > 0 Then
         &objRecXML.setRecordKeyValues(&aryEvaVals);
      End-If;

      Local XmlDoc &tmpxmldoc = &objRecXML.GetXMLDoc();

      Local XmlNode &DocNode, &topNode, &workNode;
      &DocNode = &tmpxmldoc.DocumentElement;
      &topNode = &DocNode.FindNode("/psrecordxml/psrecordxmlproperties");

      If %This.ParameterCollection.getItemByID(".CONFIGREC").Value <> "" Then
         &workNode = &topNode.AddElement("CONFIGREC");
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID(".CONFIGREC").Value);
      End-If;

      For &x = 1 To &aryKeys.Len
         &workNode = &topNode.AddElement("PARAMETER" | &x);
         &workNode = &workNode.AddText(&aryKeys [&x] | "=" | &aryEvaVals [&x]);
      End-For;

      &FieldsWithPrompt = %This.getFieldsWithPrompt();
      For &x = 1 To &FieldsWithPrompt.Len
         &workNode = &topNode.AddElement("KeyField" | &x);
         &workNode.AddAttribute("KeyField", &FieldsWithPrompt [&x]);
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID("." | &FieldsWithPrompt [&x]).Value);
      End-For;

      If %This.ParameterCollection.getItemByID(".ADMINROLE").Value <> "" Then
         &workNode = &topNode.AddElement("ADMINROLE");
         &workNode = &workNode.AddText(%This.ParameterCollection.getItemByID(".ADMINROLE").Value);
      End-If;

      &retStr = &tmpxmldoc.GenFormattedXmlString();

   End-If;

   Return &retStr;

end-method;

/**
  * Initialize the HTML Setting and retrieve from the DataBase if appropriate for the pagelet.
  *
  * @param NewSettings if null, a new collection of settings is created.
  */
method initializeSettings
   /+ &NewSettings as PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.initializeSettings +/
   Local PTPPB_PAGELET:UTILITY:Setting &thisSetting;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If &NewSettings = Null Then
      &NewSettings = create PTPPB_PAGELET:UTILITY:Collection("PSRecordDataSourceSettings");
   End-If;

   %This.setSettings(&NewSettings);
   &thisSetting = %This.Settings.getItemByID("PSRecord");

   If &thisSetting = Null Then
      &PSRecord = %This.createSettingProperty("PSRecord", "");
   Else
      &PSRecord = &thisSetting;
   End-If;

   If &PSRecord.Value = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &PSRecord.Value = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If &PSRecord.Value <> "" Then
      %This.setSettingsComplete( True);
   End-If;

   &PSRecord.Enabled = True;
   &PSRecord.Visible = True;
   &PSRecord.Required = True;
   &PSRecord.RefreshOnChange = True;
   &PSRecord.LongName = MsgGetText(219, 518, "(Message Not Found)Record Name");
   &PSRecord.EditType = &PSRecord.EDITTYPE_PROMPTTABLE;
   &PSRecord.PromptTable = "PSRECDEFNPVW";
   rem &PSRecord.PromptTable = "PSRECDEFN";
   &PSRecord.setObjectToRefreshOnValueChange(%This);

   %This.setCanHaveParameters( True);

end-method;

/**
  * Determines if any text/HTML has been set.  If so, the settins are marked complete.
  */
method processSettingsChange
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.processSettingsChange +/;
   Local PTPPB_PAGELET:UTILITY:Setting &RecordNameSetting;
   Local string &RecordNameValue, &returnRecordName;
   Local PTPPB_PAGELET:DataSource:DataSourceParameter &thisParam;
   Local PTPPB_PAGELET:UTILITY:Collection &Params;
   Local number &i;

   /* set settings incomplete and do nothing if no settings */
   If %This.Settings = Null Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &RecordNameSetting = %This.Settings.getItemByID("PSRecord");
   /* set settings incomplete and do nothing if no setting */
   If &RecordNameSetting = Null Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &RecordNameValue = &RecordNameSetting.Value;
   /* set settings incomplete and do nothing if no value */
   If &RecordNameValue = "" Then
      %This.setSettingsComplete( False);
      Return;
   End-If;

   /* Validate that this record exists */
   SQLExec(SQL.PTPPB_RS_GET_RECORDNAME, &RecordNameValue, &returnRecordName);
   If None(&returnRecordName) Then
      throw create PTPPB_PAGELET:EXCEPTION:RowsetException(MsgGetText(219, 517, "Message Not Found - Invalid record name '%1'", &RecordNameValue));
   End-If;


   &Params = %This.createEmptyParameterCollection();

   /*   
   &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".CONFIGREC");
   &thisParam.LongName = "Config Record";
   &thisParam.DefaultValue = "";
   &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
   rem &thisParam.UsageType = &thisParam.USAGETYPE_INTERNAL;
   &Params.Insert(&thisParam);
   */

   &thisParam = %This.ParameterCollection.getItemByID(".CONFIGREC");
   If &thisParam = Null Then
      /* No parameter with this field name exists ... just use the field name for the parameter name */
      /* Note:  This is the most common case */
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".CONFIGREC");
      &thisParam.LongName = "Config Record";
      &thisParam.DefaultValue = "";
      &thisParam.Required = False;
      rem &thisParam.allowChangesToRequired = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &thisParam.EditType = &thisParam.EDITTYPE_PROMPTTABLE; /* prompt table = 2 */
      &thisParam.PromptTable = "PSRECDEFNPVW";
      &Params.Insert(&thisParam);
   End-If;

   For &i = 1 To &NUMBER_OF_PARMS;
      &thisParam = %This.ParameterCollection.getItemByID(".PARAMETER" | &i);
      If &thisParam = Null Then
         &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".PARAMETER" | &i);
         &thisParam.LongName = "Parameter " | &i;
         &thisParam.DefaultValue = "";
         REM   &thisParam.Required = True;
         &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
         &Params.Insert(&thisParam);
      End-If;
   End-For;

   &FieldsWithPrompt = %This.getFieldsWithPrompt();
   For &i = 1 To &FieldsWithPrompt.Len
      &thisParam = %This.ParameterCollection.getItemByID("." | &FieldsWithPrompt [&i]);
      If &thisParam = Null Then
         /* No parameter with this field name exists ... just use the field name for the parameter name */
         /* Note:  This is the most common case */
         &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter("." | &FieldsWithPrompt [&i]);
         &thisParam.LongName = "Prompt Table Key";
         &thisParam.DefaultValue = "";
         REM   &thisParam.Required = True;
         &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
         &Params.Insert(&thisParam);
      End-If;
   End-For;

   &thisParam = %This.ParameterCollection.getItemByID(".ADMINROLE");
   If &thisParam = Null Then
      /* No parameter with this field name exists ... just use the field name for the parameter name */
      /* Note:  This is the most common case */
      &thisParam = create PTPPB_PAGELET:DataSource:DataSourceParameter(".ADMINROLE");
      &thisParam.LongName = "Admin Security Role";
      &thisParam.DefaultValue = "";
      &thisParam.Required = False;
      rem &thisParam.allowChangesToRequired = True;
      &thisParam.FieldType = &thisParam.FIELDTYPE_LONGCHARACTER;
      &thisParam.EditType = &thisParam.EDITTYPE_PROMPTTABLE; /* prompt table = 2 */
      &thisParam.PromptTable = "PSROLEDEFN";
      &Params.Insert(&thisParam);
   End-If;


   /* and set the ParameterCollection to be immutable */
   &Params.setImmutable();

   /* Mark the settings as complete */
   %This.setSettingsComplete( True);


end-method;

/**
  * Clones this datasource
  *
  * @return Object exact duplicate of this DS
  */
method Clone
   /+ Returns Object +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.Clone +/
   Local CIBER_UI_FORMS:DataSource:PSRecordDataSource &NewDS = create CIBER_UI_FORMS:DataSource:PSRecordDataSource(%This.ID);
   &NewDS.PageletID = %This.PageletID;
   &NewDS.initializeSettings(%This.Settings.Clone());
   Return &NewDS;
end-method;


/**
  * Executes when the pagelet is saved.  Saves the text/HTML to the HTML tables.
  */
method onPageletSave
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletSave +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

   If %This.SettingsComplete Then
      REM We actually have some settings to save... ;

      &HTMLSetting = %This.Settings.getItemByID("PSRecord");
      &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
      &REC_PTPPB_PGLT_HTML.DESCR.Value = %This.Descr;
      &REC_PTPPB_PGLT_HTML.HTMLAREA.Value = &HTMLSetting.Value;
      &REC_PTPPB_PGLT_HTML.LASTUPDDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.LASTUPDOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.CREATEDTTM.Value = %Datetime;
      &REC_PTPPB_PGLT_HTML.CREATEOPRID.Value = %UserId;
      &REC_PTPPB_PGLT_HTML.Insert();

   End-If;
end-method;

/**
  * Executes when the pagelet is deleted.  Deletes the text/HTML from the HTML tables.
  */
method onPageletDelete
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.onPageletDelete +/

   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;
   Local Record &REC_PTPPB_PGLT_HTML;

   &REC_PTPPB_PGLT_HTML = CreateRecord(Record.PTPPB_PGLT_HTML);
   &REC_PTPPB_PGLT_HTML.PTPPB_PAGELET_ID.Value = %This.PageletID;
   &REC_PTPPB_PGLT_HTML.Delete();

end-method;


method getFieldsWithPrompt
   /+ Returns Array of String +/
   Local array of string &fields = CreateArrayRept("", 0);
   Local Rowset &psRecFieldRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psPromptRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psKeyDefnRS = CreateRowset(Record.PSKEYDEFN);
   Local number &x, &y, &z;
   Local string &psKeyDefn_RecName, &psKeyDefn_FieldName;
   Local string &strTmp;

   &psRecFieldRS.Fill("WHERE RECNAME = :1 AND ( EDITTABLE <> ' ' OR SUBRECORD = 'Y' ) ORDER BY FIELDNUM", %This.Settings.getItemByID("PSRecord").Value);
   If All(&psRecFieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psRecFieldRS.ActiveRowCount
         If &psRecFieldRS(&x).GetRecord(1).GetField(Field.SUBRECORD).Value = "Y" Then
            rem if subrec then find fields in subrec that have prompts;

            rem get subrecord name;
            &psKeyDefn_RecName = &psRecFieldRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value;
            rem get edittables of subrecord;
            &psPromptRS.Fill("WHERE RECNAME = :1 AND  EDITTABLE <> ' ' ORDER BY FIELDNUM", &psKeyDefn_RecName);
            rem for each edittable get keys;
            If All(&psPromptRS(1).GetRecord(1).GetField(1).Value) Then
               For &y = 1 To &psPromptRS.ActiveRowCount
                  rem get keys of edittable;
                  &psKeyDefnRS.Flush();
                  &psKeyDefnRS.Fill("WHERE INDEXID='_' AND RECNAME = :1 ORDER BY KEYPOSN", &psPromptRS(&y).GetRecord(1).GetField(Field.EDITTABLE).Value);
                  &z = 1;
                  While &psKeyDefnRS.ActiveRowCount > &z
                     rem if more than one key then push onto array;
                     &strTmp = &psKeyDefn_RecName | "." | &psPromptRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value;
                     &strTmp = &strTmp | ":" | &psKeyDefnRS(&z).GetRecord(1).GetField(Field.RECNAME).Value | "." | &psKeyDefnRS(&z).GetRecord(1).GetField(Field.FIELDNAME).Value;
                     &fields.Push(&strTmp);
                     &z = &z + 1;
                  End-While;
               End-For;
            End-If;

         Else

            rem regular field with prompt table codeline;
            /*
            &psKeyDefn_RecName = &psRecFieldRS(&x).GetRecord(1).GetField(Field.EDITTABLE).Value;
            &psKeyDefnRS.Flush();
            &psKeyDefnRS.Fill("WHERE INDEXID='_' AND RECNAME = :1 ORDER BY KEYPOSN", &psKeyDefn_RecName);

            &y = 1;
            While &psKeyDefnRS.ActiveRowCount > &y
               &psKeyDefn_FieldName = &psKeyDefnRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value;

               &fields.Push(&psKeyDefn_RecName | "." | &psKeyDefn_FieldName);
               &y = &y + 1;
            End-While;
*/
         End-If;
      End-For;
   End-If;

   Return &fields;
end-method;

class ConfigRecordXML
   method ConfigRecordXML(&ObjParm As string);
   method addFieldAttributesToNode(&parmNode As XmlNode);


private
   instance string &ConfigRecKeyValue; /* Config Rec Key Field Value */

   instance array of array of string &qStrParmPairs;
   instance array of string &qStrPair;
   instance array of array of string &keyValuePairs;
   instance array of string &keyValue;

   instance boolean &KVPairsExists;
   instance boolean &ConfigRowSetIsEmpty;

   instance Rowset &ConfigRowSet; /* rowset to be loaded with row(s) from config record */

   method initialize();
   method parseNameValuePair(&queryString As string);
   method comparePairs(&kvalpairStr As string) Returns boolean;
   method compPToolsQStrVals(&strParm As string) Returns boolean;
   method translateUseEditPairs(&num As number) Returns array of array of string;
   method calcUseEdit(&num As number) Returns array of number;
   method addNameValuePairToNode(&parmNode As XmlNode, &parmAry2 As array of array of string);

   Constant &CONFIGRECORDNAME = "PSRECFIELD"; /* Config Record Name */
   Constant &CONFIGKEY1 = "LABEL_ID"; /* Config Record 2nd Key Field Name where QStrParms are stored */
   Constant &FIELDNAME = "FIELDNAME";
   Constant &USEEDIT = "USEEDIT";

end-class;

method ConfigRecordXML
   /+ &ObjParm as String +/
   %This.ConfigRecKeyValue = &ObjParm;
   %This.initialize();

end-method;

method initialize

   /* get query string parameters */
   Local number &x;
   Local array of string &tmpAryStr;

   &KVPairsExists = False;
   &tmpAryStr = %Request.GetParameterNames();
   If &tmpAryStr.Len > 0 Then
      &qStrPair = CreateArrayRept("", 2);
      &qStrParmPairs = CreateArrayRept(&qStrPair, 0);
      For &x = 1 To &tmpAryStr.Len
         If Not %This.compPToolsQStrVals(&tmpAryStr [&x]) Then
            &qStrPair [1] = &tmpAryStr [&x];
            &qStrPair [2] = %Request.GetParameter(&tmpAryStr [&x]);
            &qStrParmPairs.Push(&qStrPair);
            rem %Response.WriteLine("qstr: " | &qStrParmPairs [&x][1] | " = " | &qStrParmPairs [&x][2] | "<br>");
         End-If;
      End-For;
   End-If;

   /* get first record field that is a key */
   Local string &ConfigKeyFieldName = "";
   Local Record &tmpRec;

   &x = 1;
   While &ConfigKeyFieldName = ""
      &tmpRec = CreateRecord(@("Record." | &CONFIGRECORDNAME));
      If &tmpRec.GetField(&x).IsKey Then
         &ConfigKeyFieldName = &tmpRec.GetField(&x).Name;
      End-If;
      &x = &x + 1;
   End-While;

   &ConfigRowSet = CreateRowset(@("Record." | &CONFIGRECORDNAME));
   &ConfigRowSet.Fill("WHERE " | &ConfigKeyFieldName | "=:1", %This.ConfigRecKeyValue);
   If All(&ConfigRowSet(1).GetRecord(1).GetField(1).Value) Then
      &ConfigRowSetIsEmpty = False;
   Else
      &ConfigRowSetIsEmpty = True;
   End-If;
   /* debug */

   For &x = 1 To &ConfigRowSet(1).GetRecord(1).FieldCount
      %Response.Write("<br>" | &ConfigRowSet(1).GetRecord(1).GetField(&x).Name | " = " | &ConfigRowSet(1).GetRecord(1).GetField(&x).Value);
   End-For;

end-method;

method addFieldAttributesToNode
   /+ &parmNode as XmlNode +/

   Local array of Row &ConfigRow = CreateArrayRept(%This.ConfigRowSet.GetRow(1), 0);
   Local string &strFieldName;
   Local number &x;

   &strFieldName = &parmNode.GetAttributeValue("name");
   If None(&strFieldName) Then
      Return;
   End-If;

   /* check if strFieldName is an actual field on configrow record */
   Local Rowset &tmpRS = CreateRowset(Record.PSRECFIELD);
   &tmpRS.Fill("WHERE RECNAME = :1", &ConfigRecKeyValue);
   &x = 1;
   While &x < &tmpRS.ActiveRowCount + 1
      If &tmpRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value = &strFieldName Then
         rem %Response.WriteLine("<BR><BR>PASSED!" | " " | &x);
         Break;
      Else
         If &x = &tmpRS.ActiveRowCount Then
            Return;
         End-If;
      End-If;
      &x = &x + 1;
   End-While;

   If Not %This.ConfigRowSetIsEmpty Then

      /* collect all rows that have this fieldname */
      For &x = 1 To %This.ConfigRowSet.ActiveRowCount
         If %This.ConfigRowSet.GetRow(&x).GetRecord(1).GetField(@("Field." | &FIELDNAME)).Value = &strFieldName Then
            &ConfigRow.Push(&ConfigRowSet(&x)); /* gather all rows into array, assumes first row will be blank key/qstrparm value */
         End-If;
      End-For;
      rem %Response.Write("<br> config row count = " | &ConfigRow.Len); /* debug */

      For &x = 1 To &ConfigRow.Len
         If None(&ConfigRow [1].GetRecord(1).GetField(@("Field." | &CONFIGKEY1)).Value) Or /* key is empty */
               %This.comparePairs(&ConfigRow [1].GetRecord(1).GetField(@("Field." | &CONFIGKEY1)).Value) Then /* key(s) matches query string parameters */
            %This.addNameValuePairToNode(&parmNode, %This.translateUseEditPairs(&ConfigRow [1].GetRecord(1).GetField(@("Field." | &USEEDIT)).Value));
         End-If
      End-For;

   End-If;

end-method;

/* string parmeter is broken down to name value pair(s) and then compared
 to query string parameter pair(s) (collected earlier) and check if matches */
method comparePairs
   /+ &kvalpairStr as String +/
   /+ Returns Boolean +/
   If None(&kvalpairStr) Then
      Return False;
   Else
      /* parse string into key value pairs */
      &keyValue = CreateArrayRept("", 2);
      &keyValuePairs = CreateArrayRept(&keyValue, 0);
      %This.parseNameValuePair(&kvalpairStr);

      Local number &x, &y, &match;

      &match = 0;
      rem   %Response.WriteLine("<br> " | &keyValuePairs.Len | " " | &qStrParmPairs.Len);
      For &x = 1 To &keyValuePairs.Len
         For &y = 1 To &qStrParmPairs.Len
            rem %Response.WriteLine("<br>KV: " | &x | " " | &keyValuePairs [&x][1] | " = " | &keyValuePairs [&x][2] | " ############ " | " QStr: " | &y | " " | &qStrParmPairs [&y][1] | " = " | &qStrParmPairs [&y][2] | "<br>");
            If &qStrParmPairs [&y][1] = &keyValuePairs [&x][1] And
                  &qStrParmPairs [&y][2] = &keyValuePairs [&x][2] Then
               &match = 1 + &match;
            End-If;
         End-For;
      End-For;
      rem   %Response.WriteLine("<br>" | " " | &keyValuePairs.Len | " " | &match | "<br>");
      Return (&qStrParmPairs.Len = &match);
   End-If;
end-method;

/* takes string and parses "x=y" value into class array structure */
method parseNameValuePair
   /+ &queryString as String +/

   /* note that this function assumes that the name/value string format will NOT have duplicate names */

   If Find("&", &queryString) > 0 Then
      If Len(Substring(&queryString, 1, Find("&", &queryString) - 1)) > 0 Then
         %This.parseNameValuePair(Substring(&queryString, 1, Find("&", &queryString) - 1));
      End-If;
      If Len(Substring(&queryString, Find("&", &queryString) + 1, Len(&queryString))) > 0 Then
         %This.parseNameValuePair(Substring(&queryString, Find("&", &queryString) + 1, Len(&queryString)));
      End-If;
   Else
      If Len(Substring(&queryString, 1, Find("=", &queryString) - 1)) > 0 And
            Len(Substring(&queryString, Find("=", &queryString) + 1, Len(&queryString))) > 0 Then

         Local array of string &kVary = CreateArrayRept("", 2);

         &kVary [1] = Substring(&queryString, 1, Find("=", &queryString) - 1);
         &kVary [2] = Substring(&queryString, Find("=", &queryString) + 1, Len(&queryString));
         rem %Response.WriteLine(" kv1 = " | &kVary [1] | " ################## kv2 = " | &kVary [2] | "<br>");
         &keyValuePairs.Push(&kVary);
         &KVPairsExists = True;
      End-If;
   End-If;

end-method;

/* takes in number parm and breaks down bit values
and translates them to name/value pair (xml attribute=value).
-stores pair as a 2 item str array
-then stores (2 item str array) as array item inside container array (array of array)
*/
method translateUseEditPairs
   /+ &num as Number +/
   /+ Returns Array2 of String +/

   Local array of string &strAry = CreateArrayRept("", 2);
   Local array of array of string &retAry = CreateArrayRept(&strAry, 0);
   Local array of number &UseEditAryNums;
   Local number &x;

   &UseEditAryNums = %This.calcUseEdit(&num);
   For &x = 1 To &UseEditAryNums.Len
      Evaluate &UseEditAryNums [&x]
      When 1 /* required */
         &strAry [1] = "required";
         &strAry [2] = "";
      When 2 /* fill in */
         &strAry [1] = "";
         &strAry [2] = "";
      When 4 /* fill in */
         &strAry [1] = "";
         &strAry [2] = "";
      When 8 /* fill in */
         &strAry [1] = "";
         &strAry [2] = "";
      When 16 /* fill in */
         &strAry [1] = "";
         &strAry [2] = "";
      When 32 /* fill in */
         &strAry [1] = "";
         &strAry [2] = "";
      When-Other /* fill in */
         &strAry [1] = "";
         &strAry [2] = "";
      End-Evaluate;

      If &strAry [1] <> "" Then
         &retAry.Push(&strAry);
      End-If;
      &strAry [1] = "";
      &strAry [2] = "";
   End-For;

   Return &retAry;

end-method;

/* actual math calculation to break down bit numbers
stores them into array */
method calcUseEdit
   /+ &num as Number +/
   /+ Returns Array of Number +/
   Local array of number &retAry = CreateArrayRept(0, 1);
   Local number &bitValue, &tmpValue;

   &bitValue = &num;

   While &bitValue > 0
      /* get largest base2 number from input */
      /* set it tmp */
      &tmpValue = Int(Log10(&bitValue) / Log10(2));

      /* get remainder from largest base2 number */
      /* set it bitVal */
      &bitValue = Int(&bitValue - Exp(&tmpValue * Log10(2) * Ln(10)));

      If &retAry [1] = 0 Then
         &retAry [1] = &tmpValue;
      Else
         &retAry.Push(&tmpValue);
      End-If;
   End-While;


   Return &retAry;

end-method;

/* compares string to delivered query string parameters that tools uses */
method compPToolsQStrVals
   /+ &strParm as String +/
   /+ Returns Boolean +/

   Local array of string &toolsQStr = CreateArrayRept("", 0);
   Local number &x;

   &toolsQStr.Push("PortalHostNode");
   &toolsQStr.Push("PortalActualURL");
   &toolsQStr.Push("PortalRegistryName");
   &toolsQStr.Push("PortalKeyStruct");
   &toolsQStr.Push("PortalServletURI");
   &toolsQStr.Push("NoCrumbs");
   &toolsQStr.Push("PortalURI");
   &toolsQStr.Push("PSHome");
   &toolsQStr.Push("Portal");
   &toolsQStr.Push("Node");
   &toolsQStr.Push("ContentType");
   &toolsQStr.Push("ContentID");
   &toolsQStr.Push("ICScriptName");

   For &x = 1 To &toolsQStr.Len
      If &toolsQStr [&x] = &strParm Then
         Return True;
      End-If;
   End-For;

   Return False;
end-method;

method addNameValuePairToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmAry2 as Array2 of String +/

   Local number &x;

   For &x = 1 To &parmAry2.Len
      &parmNode.AddAttribute(&parmAry2 [&x][1], &parmAry2 [&x][2]);
   End-For;

end-method;

class JSONForm
   method JSONForm(&recName As string);
   method ApplyJsonToRecord(&JSONStr As string);

private
   instance string &RecordName;

   method verifyREC(&recName As string) Returns boolean;
   method verifyJSON(&JSONStr As string);
end-class;

method JSONForm
   /+ &recName as String +/
   If %This.verifyREC(&recName) Then
      &RecordName = &recName;
   Else
      throw CreateException(0, 0, "Record is not found");
   End-If;
end-method;

method ApplyJsonToRecord
   /+ &JSONStr as String +/

end-method;

method verifyREC
   /+ &recName as String +/
   /+ Returns Boolean +/
   Local number &ret = 0;

   SQLExec("SELECT 1 FROM PSRECDEFN WHERE RECNAME = :1", &recName, &ret);

   If &ret = 1 Then
      Return True;
   Else
      Return False;
   End-If;
end-method;

method verifyJSON
   /+ &JSONStr as String +/

end-method;

class RecordXML
   method RecordXML(&recName As string);
   method GetXMLDoc() Returns XmlDoc;
   method setRecordKeyValues(&values As array of string);
   method addSubRecFieldPrmtKeyVals(&NameValPair As string);

private
   instance string &RecordName;
   instance array of string &KeyFieldNames, &KeyFieldValues, &SubRecDetails, &SubRecFieldPromptKeyVals;

   method initRecordKeyFields();
   method getWhereClause() Returns string;
   method calcUSEEDIT(&parmFieldName As string) Returns array of number;
   method addDefaultValueAsAttribute(&parmNode As XmlNode);
   method addFieldAttributeToNode(&parmNode As XmlNode, &parmBitArry As array of number);
   method addFieldTypeAndLengthToNode(&parmNode As XmlNode);
   method isSubRecordField(&parmFieldName As string) Returns string;

end-class;

method RecordXML
   /+ &recName as String +/
   &RecordName = &recName;

   /* need to make sure record name is verified, then call next method */
   %This.initRecordKeyFields();

end-method;

method setRecordKeyValues
   /+ &values as Array of String +/
   Local number &x;
   If &values.Len <= &KeyFieldValues.Len Then
      For &x = 1 To &values.Len
         &KeyFieldValues [&x] = &values [&x];
      End-For;
   Else
      /* throw exception about too many values for keys */
      rem throw CreateException(2, 160, "'%1' doesn't support property or method '%2'", "SomeClass", "SomeMethod");
      throw CreateException(0, 0, "There are too many parameters listed as keys for record, '%1'", &RecordName);
   End-If;

end-method;

method GetXMLDoc
   /+ Returns XmlDoc +/
   Local number &x, &y, &rowcount;
   Local XmlDoc &retXMLDoc = CreateXmlDoc("<?xml version='1.0' encoding='UTF-8' standalone='yes'?><psrecordxml/>");
   Local XmlNode &baseNode, &thatNode, &thisNode, &recNode, &fieldsNode, &fieldNode;
   Local Record &thisRecord = CreateRecord(@("RECORD." | &RecordName));
   Local Rowset &thisRowSet;

   &thisRowSet = CreateRowset(@("RECORD." | &RecordName));
   &thisRowSet.Fill(%This.getWhereClause());

   &baseNode = &retXMLDoc.DocumentElement;
   &thatNode = &baseNode.AddElement("psrecordxmlproperties");
   &thisNode = &thatNode.AddElement("recordname");
   &thisNode.NodeValue = &RecordName;
   &thisNode = &thatNode.AddElement("fieldcount");
   &thisNode.NodeValue = String(&thisRecord.FieldCount);

   &thatNode = &baseNode.AddElement("psrecordrows");
   &thatNode.AddAttribute("count", String(&thisRowSet.ActiveRowCount));

   For &x = 1 To &thisRowSet.ActiveRowCount
      &recNode = &thatNode.AddElement("psrecord");
      &recNode.AddAttribute("rownumber", String(&x));
      &fieldsNode = &recNode.AddElement("psrecordfields");
      For &y = 1 To &thisRecord.FieldCount
         &fieldNode = &fieldsNode.AddElement("field");
         &fieldNode.AddAttribute("name", &thisRecord.GetField(&y).Name);
         &fieldNode.AddAttribute("field_number", String(&y));
         &fieldNode.AddAttribute("long_label", &thisRecord.GetField(&y).GetLongLabel(&thisRecord.GetField(&y).Name));
         &fieldNode.AddAttribute("short_label", &thisRecord.GetField(&y).GetShortLabel(&thisRecord.GetField(&y).Name));
         %This.addFieldTypeAndLengthToNode(&fieldNode);
         %This.addDefaultValueAsAttribute(&fieldNode);
         %This.addFieldAttributeToNode(&fieldNode, %This.calcUSEEDIT(&thisRecord.GetField(&y).Name));

         rem insert data value to xml;
         If &x = 1 Then
            If &thisRowSet.GetRow(&x).GetRecord(1).GetField(1).Value <> "" Then
               &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
            End-If;
         Else
            &fieldNode.AddAttribute("value", &thisRowSet.GetRow(&x).GetRecord(1).GetField(&y).Value);
         End-If;

      End-For
   End-For;
   rem debug code;
   For &x = 1 To &SubRecDetails.Len
      &thisNode = &thatNode.AddElement("subrecord_" | &x);
      &thisNode.NodeValue = &SubRecDetails [&x];
   End-For;
   rem end debug code;

   Return &retXMLDoc;
end-method;

method initRecordKeyFields

   Local Rowset &psKeydefnRS = CreateRowset(Record.PSKEYDEFN);
   Local Rowset &psRecfieldRS = CreateRowset(Record.PSRECFIELD);
   Local Rowset &psRecFldTargetRS = CreateRowset(Record.PSRECFIELD);
   Local number &x, &y;

   &psKeydefnRS.Fill("WHERE RECNAME=:1 AND INDEXID=:2", &RecordName, "_");

   &psKeydefnRS.Sort(PSKEYDEFN.KEYPOSN, "A");
   &KeyFieldNames = CreateArrayRept("", &psKeydefnRS.ActiveRowCount);
   &KeyFieldValues = CreateArrayRept("", &psKeydefnRS.ActiveRowCount);

   For &x = 1 To &psKeydefnRS.ActiveRowCount
      &KeyFieldNames [&x] = &psKeydefnRS.GetRow(&x).GetRecord(1).GetField(Field.FIELDNAME).Value;
   End-For;

   &SubRecDetails = CreateArrayRept("", 0);

   &psRecfieldRS.Fill("WHERE RECNAME=:1 AND SUBRECORD = 'Y' ORDER BY FIELDNUM", &RecordName);
   If All(&psRecfieldRS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &psRecfieldRS.ActiveRowCount
         &psRecFldTargetRS.Fill("WHERE RECNAME=:1 ORDER BY FIELDNUM", &psRecfieldRS(&x).GetRecord(1).GetField(Field.FIELDNAME).Value);
         For &y = 1 To &psRecFldTargetRS.ActiveRowCount
            &SubRecDetails.Push(&psRecFldTargetRS(&y).GetRecord(1).GetField(Field.RECNAME).Value | ":" | &psRecFldTargetRS(&y).GetRecord(1).GetField(Field.FIELDNAME).Value);
         End-For;
      End-For;
   End-If;

   &SubRecFieldPromptKeyVals = CreateArrayRept("", 0);

end-method;

method getWhereClause
   /+ Returns String +/
   Local string &retStr = "";
   Local number &x;

   For &x = 1 To &KeyFieldValues.Len
      If &retStr = "" Then
         &retStr = &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "'";
      Else
         &retStr = &retStr | " AND " | &KeyFieldNames [&x] | "='" | &KeyFieldValues [&x] | "'";
      End-If;
   End-For;

   If All(&retStr) Then
      &retStr = "WHERE " | &retStr;
   End-If;

   Return &retStr;
end-method;

method calcUSEEDIT
   /+ &parmFieldName as String +/
   /+ Returns Array of Number +/
   Local number &bitValue, &tmpValue, &x, &y;
   Local string &subRecName;
   Local array of number &bitNumbers = CreateArrayRept(0, 1);
   Local Rowset &tmpRS = CreateRowset(Record.PSRECFIELD);

   &subRecName = %This.isSubRecordField(&parmFieldName);
   If All(&subRecName) Then
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &subRecName, &parmFieldName);
   Else
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &parmFieldName);
   End-If;
   &bitValue = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.USEEDIT).Value;

   While &bitValue > 0
      /* get largest base2 number from input */
      /* set it tmp */
      &tmpValue = Int(Log10(&bitValue) / Log10(2));

      /* get remainder from largest base2 number */
      /* set it bitVal */
      &bitValue = Int(&bitValue - Exp(&tmpValue * Log10(2) * Ln(10)));


      If &bitNumbers [1] = 0 Then
         &bitNumbers [1] = &tmpValue;
      Else
         &bitNumbers.Push(&tmpValue);
      End-If;
   End-While;

   Return &bitNumbers;
end-method;

method addDefaultValueAsAttribute
   /+ &parmNode as XmlNode +/

   Local string &tmpStrRec, &tmpStrFld, &attrVal, &fldName, &subRecName;
   Local Rowset &tmpRS, &tmpRSprompt;
   Local number &x;

   &fldName = &parmNode.GetAttributeValue("name");
   &subRecName = %This.isSubRecordField(&fldName);

   If All(&subRecName) Then
      &tmpRS = CreateRowset(Record.PSRECFIELD);
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &subRecName, &fldName);
   Else
      &tmpRS = CreateRowset(Record.PSRECFIELD);
      &tmpRS.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2", &RecordName, &fldName);
   End-If;

   &tmpStrFld = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value;
   If All(&tmpStrFld) Then
      &tmpStrRec = &tmpRS.GetRow(1).GetRecord(1).GetField(Field.DEFRECNAME).Value;

      If All(&tmpStrRec) And
            All(&tmpStrFld) Then
         &tmpRSprompt = CreateRowset(@("RECORD." | &tmpStrRec));
         &tmpRSprompt.Fill();
         &attrVal = &tmpRSprompt.GetRow(1).GetRecord(1).GetField(@("FIELD." | &tmpStrFld)).Value;
      Else
         If All(&tmpStrFld) Then
            &attrVal = &tmpStrFld;
         End-If;
         &attrVal = ""; /* no default value */
      End-If;

      If &attrVal <> "" Then
         &parmNode.AddAttribute("default_value", &attrVal);
      End-If;
   End-If;

end-method;

method addFieldTypeAndLengthToNode
   /+ &parmNode as XmlNode +/
   Local Rowset &tmpRS;
   Local string &tmpStr;

   &tmpRS = CreateRowset(Record.PSDBFIELD);
   &tmpRS.Fill("WHERE FIELDNAME = :1", &parmNode.GetAttributeValue("name"));
   Evaluate &tmpRS.GetRow(1).GetRecord(1).GetField(Field.FIELDTYPE).Value
   When 0
      &tmpStr = "Character";
   When 1
      &tmpStr = "LongCharacter";
   When 2
      &tmpStr = "Number";
   When 3
      &tmpStr = "SignedNumber";
   When 4
      &tmpStr = "Date";
   When 5
      &tmpStr = "Time";
   When 6
      &tmpStr = "DateTime";
   When 7
      &tmpStr = "NA";
   When 8
      &tmpStr = "ImageORAttachment";
   When 9
      &tmpStr = "ImageReference";
   When-Other;
      &tmpStr = "NA";
   End-Evaluate;
   &parmNode.AddAttribute("field_type", &tmpStr);
   &parmNode.AddAttribute("field_length", &tmpRS.GetRow(1).GetRecord(1).GetField(Field.LENGTH).Value);

end-method;

method addFieldAttributeToNode
   /+ &parmNode as XmlNode, +/
   /+ &parmBitArry as Array of Number +/

   Local number &x, &y, &z;
   Local Rowset &rs, &rsPSRECFLD, &rsPSKEYDEFN, &rsDefltRecVals;
   Local XmlNode &tmpNode;
   Local string &tmpStr;

   For &x = 1 To &parmBitArry.Len
      Evaluate &parmBitArry [&x]
      When 8 /* required */
         &parmNode.AddAttribute("required", "true");
      When 9 /* xlat */
         &rs = CreateRowset(Record.PSXLATITEM);
         &rs.Fill("WHERE FIELDNAME = :1 AND %EffDtCheck(PSXLATITEM B,FILL,%CurrentDateIn) AND EFF_STATUS = 'A'", &parmNode.GetAttributeValue("name"));
         For &y = 1 To &rs.ActiveRowCount
            &tmpNode = &parmNode.AddElement("xlatitem");
            &tmpNode.AddAttribute("long_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATLONGNAME).Value);
            &tmpNode.AddAttribute("short_description", &rs.GetRow(&y).GetRecord(1).GetField(Field.XLATSHORTNAME).Value);
            &tmpNode.AddAttribute("field_value", &rs.GetRow(&y).GetRecord(1).GetField(Field.FIELDVALUE).Value);
         End-For;
      When 13 /* Y/N edit */
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "Yes");
         &tmpNode.AddAttribute("field_value", "Y");
         &tmpNode = &parmNode.AddElement("edititem");
         &tmpNode.AddAttribute("description", "No");
         &tmpNode.AddAttribute("field_value", "N");

      When 14 /* prompt table edit */
         &rsPSRECFLD = CreateRowset(Record.PSRECFIELD);
         Local string &subRecName = %This.isSubRecordField(&parmNode.GetAttributeValue("name"));
         If All(&subRecName) Then
            &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &subRecName, &parmNode.GetAttributeValue("name"));
         Else
            &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &RecordName, &parmNode.GetAttributeValue("name"));
         End-If;
         &tmpStr = &rsPSRECFLD(1).GetRecord(1).GetField(Field.EDITTABLE).Value;

         &rs = CreateRowset(@("RECORD." | &tmpStr));

         /* check for effective date and effective status ??*/

         /* check the prompt table for default values for keys and use them for keys to fill */
         /* find key, then search against psrecfield */
         &rsPSKEYDEFN = CreateRowset(Record.PSKEYDEFN);
         &rsPSKEYDEFN.Fill("WHERE INDEXID='_' AND RECNAME = :1 ORDER BY KEYPOSN", &tmpStr);

         Local array of string &dfltVals = CreateArrayRept("", 0);
         Local string &WHEREstr;

         If &rsPSKEYDEFN.ActiveRowCount > 1 Then
            For &z = 1 To &rsPSKEYDEFN.ActiveRowCount - 1

               &rsPSRECFLD = CreateRowset(Record.PSRECFIELD);
               &rsPSRECFLD.Fill("WHERE RECNAME = :1 AND FIELDNAME = :2 ORDER BY FIELDNUM", &tmpStr, &rsPSKEYDEFN(&z).GetRecord(1).GetField(Field.FIELDNAME).Value);

               If All(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFRECNAME).Value) And
                     All(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value) Then
                  try
                     &rsDefltRecVals = CreateRowset(@("RECORD." | &rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFRECNAME).Value));
                     &rsDefltRecVals.Fill();
                     &dfltVals.Push(&rsDefltRecVals(1).GetRecord(1).GetField(@("FIELD." | &rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value)).Value);

                  catch Exception &e1

                  end-try;
               Else
                  If All(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value) Then
                     &dfltVals.Push(&rsPSRECFLD(1).GetRecord(1).GetField(Field.DEFFIELDNAME).Value);
                  End-If;
               End-If;

            End-For;

            rem if no default value try using VARIABLE ;
            rem if VARIABLE is not empty, then set defltval;
            For &z = 1 To &SubRecFieldPromptKeyVals.Len
               If (&parmNode.GetAttributeValue("name") | ":" | &rsPSKEYDEFN(1).GetRecord(1).GetField(Field.RECNAME).Value | "." | &rsPSKEYDEFN(1).GetRecord(1).GetField(Field.FIELDNAME).Value) = &SubRecFieldPromptKeyVals [&z] Then
                  rem      &dfltVals.Push("");
               End-If;
            End-For;
            rem debug hardcoded value;
            rem &dfltVals.Push("USA");
         End-If;

         If &dfltVals.Len > 0 Then
            rem check if dfltvals.len equals to pskeydefn number of rows?;

            For &z = 1 To &dfltVals.Len
               If All(&WHEREstr) Then
                  &WHEREstr = &WHEREstr | " AND " | &rsPSKEYDEFN(&z).GetRecord(1).GetField(Field.FIELDNAME).Value | " = '" | &dfltVals [&z] | "'";
               Else
                  &WHEREstr = "WHERE " | &rsPSKEYDEFN(&z).GetRecord(1).GetField(Field.FIELDNAME).Value | " = '" | &dfltVals [&z] | "'";
               End-If;
            End-For;
         End-If;

         &rs.Fill(&WHEREstr);

         If All(&rs(1).GetRecord(1).GetField(1).Value) Then
            For &y = 1 To &rs.ActiveRowCount
               &tmpNode = &parmNode.AddElement("promptitem");
               For &z = 1 To &rs(&y).GetRecord(1).FieldCount
                  &tmpNode.AddAttribute(&rs.GetRow(&y).GetRecord(1).GetField(&z).Name, &rs.GetRow(&y).GetRecord(1).GetField(&z).Value);
               End-For;
            End-For;
         End-If;

      When-Other;

      End-Evaluate;

   End-For;

end-method;

method isSubRecordField
   /+ &parmFieldName as String +/
   /+ Returns String +/

   Local number &x;
   Local string &RecName;

   &x = 1;
   While &SubRecDetails.Len >= &x
      If Substring(&SubRecDetails [&x], Find(":", &SubRecDetails [&x]) + 1, Len(&SubRecDetails [&x])) = &parmFieldName Then
         &RecName = Substring(&SubRecDetails [&x], 1, Find(":", &SubRecDetails [&x]) - 1);
         Return &RecName;
      End-If;
      &x = &x + 1;
   End-While;

   Return &RecName;

end-method;

method addSubRecFieldPrmtKeyVals
   /+ &NameValPair as String +/
   &SubRecFieldPromptKeyVals.Push(&NameValPair);
end-method;
