instance.peoplecode_text:
class ConfigRecordXML
   method ConfigRecordXML(&ObjParm As string);
   method addFieldAttributeToNode(&parmNode As XmlNode);

private
   instance string &ConfigKeyName;
   instance array of array of string &qStrParmPairs;
   instance array of string &qStrPair;

   instance array of array of string &keyValuePairs;
   instance array of string &keyValue;

   instance boolean &QStrParmExists;
   instance Rowset &ConfigRowSet;

   method initialize();
   method compareQStrKeys(&qstrparms As string) Returns boolean;
   method getNameValuePair(&queryString As string);

   Constant &CONFIGRECORDNAME = "UD_CONFIG_TBL";
   Constant &FIELDNAME = "FIELDNAME";
   Constant &CONFIGKEY1 = "CATEGORYID";

end-class;

method ConfigRecordXML
   /+ &ObjParm as String +/
   %This.ConfigKeyName = &ObjParm;
   %This.initialize();

end-method;

method initialize

   /* get query string parameters */
   Local number &x;
   Local array of string &tmpAryStr;

   &QStrParmExists = False;
   &tmpAryStr = %Request.GetParameterNames();
   If &tmpAryStr.Len > 0 Then
      &QStrParmExists = True;
      &qStrPair = CreateArrayRept("", 2);
      &qStrParmPairs = CreateArrayRept(&qStrPair, 0);
      For &x = 1 To &tmpAryStr.Len
         &qStrPair [1] = &tmpAryStr [&x];
         &qStrPair [2] = %Request.GetParameter(&tmpAryStr [&x]);
         &qStrParmPairs.Push(&qStrPair);
      End-For;
   End-If;

   /* get first record field that is a key */
   Local string &ConfigKeyFieldName = "";
   Local Record &tmpRec;

   &x = 1;
   While &ConfigKeyFieldName = ""
      &tmpRec = CreateRecord(@("Record." | &CONFIGRECORDNAME));
      If &tmpRec.GetField(&x).IsKey Then
         &ConfigKeyFieldName = &tmpRec.GetField(&x).Name;
      End-If;
      &x = &x + 1;
   End-While;

   &ConfigRowSet = CreateRowset(&tmpRec);
   &ConfigRowSet.Fill("WHERE " | &ConfigKeyFieldName | "=:1", %This.ConfigKeyName);

end-method;

method addFieldAttributeToNode
   /+ &parmNode as XmlNode +/

   Local array of Row &ConfigRow = CreateArrayRept(%This.ConfigRowSet.GetRow(1), 0);
   Local string &strFieldName = &parmNode.GetAttributeValue("name");
   Local number &x;

   If All(%This.ConfigRowSet.GetRow(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To %This.ConfigRowSet.ActiveRowCount
         If %This.ConfigRowSet.GetRow(&x).GetRecord(1).GetField(&FIELDNAME).Value = &strFieldName Then
            &ConfigRow.Push(&ConfigRowSet(&x));
         End-If;
      End-For;

      /* compare selected config rows to blank/query string keys */
      Evaluate &ConfigRow.Len
      When 0
         /* do nothing to xmlnode */
      When 1
         If None(&ConfigRow [1].GetRecord(1).GetField(&CONFIGKEY1).Value) Then
            /* apply attr to node */
         Else
            /* match qstrparms to keys of config record */
         End-If;
      When-Other
         If None(&ConfigRow [1].GetRecord(1).GetField(&CONFIGKEY1).Value) Then
            /* apply attr to node */
         Else
            For &x = 1 To &ConfigRow.Len
               /* match qstrparms to keys of config record */
               If %This.compareQStrKeys(&ConfigRow [&x].GetRecord(1).GetField(&CONFIGKEY1).Value) Then

               End-If;
            End-For;
         End-If;
      End-Evaluate;

   End-If;
   /* finally add attr to node xml */
   rem   &parmNode.AddAttribute("config-required", " ");
end-method;

method compareQStrKeys
   /+ &qstrparms as String +/
   /+ Returns Boolean +/

   &keyValue = CreateArrayRept("", 2);
   &keyValuePairs = CreateArrayRept(&keyValue, 0);

   /* break string into key value pairs */
   %This.getNameValuePair(&qstrparms);

   Local number &x, &y, &match;
   &match = 0;
   For &x = 1 To &keyValuePairs.Len
      For &y = 1 To &qStrParmPairs.Len
         If &qStrParmPairs [&y][1] = &keyValuePairs [&x][1] And
               &qStrParmPairs [&y][2] = &keyValuePairs [&x][2] Then
            &match = 1 + &match;
         End-If;
      End-For;
   End-For;

   Return (&qStrParmPairs.Len = &match);
end-method;

method getNameValuePair
   /+ &queryString as String +/

   If Find("&", &queryString) > 0 Then
      %This.getNameValuePair(Substring(&queryString, 1, Find("&", &queryString) - 1));

      If Len(Substring(&queryString, Find("&", &queryString) + 1, Len(&queryString))) > 0 Then
         %This.getNameValuePair(Substring(&queryString, Find("&", &queryString) + 1, Len(&queryString)));
      End-If;
   Else
      &keyValue [1] = Substring(&queryString, 1, Find("=", &queryString) - 1);
      &keyValue [2] = Substring(&queryString, Find("=", &queryString) + 1, Len(&queryString));
      &keyValuePairs.Push(&keyValue);
   End-If;

end-method;

import PTPPB_PAGELET:UTILITY:*;
import PTPPB_PAGELET:DataSource:*;
import PTPPB_PAGELET:EXCEPTION:*;


/** 
  *  The HTML DataSource allows for the storage and retrieval of text or HTML
  *    for a pagelet.
  */
/*class HTMLDataSource extends PTPPB_PAGELET:DataSource:DataSource*/
class PSRecordDataSource extends PTPPB_PAGELET:DataSource:DataSource
   property PTPPB_PAGELET:UTILITY:Setting HTML readonly;

   method PSRecordDataSource(&id_param As string);
   method execute() Returns string;
   method Clone() Returns object;
   method initializeSettings(&NewSettings As PTPPB_PAGELET:UTILITY:Collection);
   method processSettingsChange();

end-class;


rem  --- for RTE HTML Process;
Declare Function WrapRTEHTML PeopleCode WEBLIB_PTRTE.ISCRIPT1 FieldFormula;


/**
  * Constructor
  */
method PSRecordDataSource
   /+ &id_param as String +/
   %Super = create PTPPB_PAGELET:DataSource:DataSource(&id_param);
   %This.setObjectSubType("PSRecord");
   %This.setCanHaveParameters( True);
   %This.initializeSettings(%This.Settings);
   %This.allowGrouplet = True;
end-method;

/**
  * Returns the text or HTML for the pagelet
  *
  * @return string text or HTML
  */
method execute
   /+ Returns String +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.execute +/

   Local string &strHTML, &temp;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;
   Local XmlDoc &xmlDoc;

   &strPageletID = %This.PageletID;

   If %This.Settings <> Null Then
      &strHTML = %This.Settings.getItemByID("PSRecord").Value;
   End-If;

   If &strHTML = "" Then
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &strHTML = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If All(&strHTML) Then
      try
         &xmlDoc = CreateXmlDoc(&strHTML);
      catch Exception &ex
         /*
         &strHTML = "<?xml version='1.0' encoding='UTF-8'?><!DOCTYPE xsl:stylesheet [<!ENTITY nbsp '&#160;'>]>" | WrapRTEHTML(&strHTML);
         */
         &temp = "<?xml version='1.0' encoding='UTF-8'?>" | GetHTMLText(HTML.PTPPB_XHTML_SPECIAL_CHARACTERS) | WrapRTEHTML(&strHTML);
         try
            &xmlDoc = CreateXmlDoc(&temp);
            &strHTML = &temp;
         catch Exception &ex1
            &strHTML = WrapRTEHTML(&strHTML);
         end-try;
      end-try;
   End-If;

   Return &strHTML;

end-method;

/**
  * Initialize the HTML Setting and retrieve from the DataBase if appropriate for the pagelet.
  *
  * @param NewSettings if null, a new collection of settings is created.
  */
method initializeSettings
   /+ &NewSettings as PTPPB_PAGELET:UTILITY:Collection +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.initializeSettings +/
   Local PTPPB_PAGELET:UTILITY:Setting &thisSetting;
   Local string &strPageletID;
   Local Rowset &rsPageletHTML;

   If &NewSettings = Null Then
      &NewSettings = create PTPPB_PAGELET:UTILITY:Collection("PSRecordDataSourceSettings");
   End-If;

   %This.setSettings(&NewSettings);
   &thisSetting = %This.Settings.getItemByID("PSRecord");

   If &thisSetting = Null Then
      &HTML = %This.createSettingProperty("PSRecord", "");
   Else
      &HTML = &thisSetting;
   End-If;

   If &HTML.Value = "" Then
      &strPageletID = %This.PageletID;
      &rsPageletHTML = CreateRowset(Record.PTPPB_PGLT_HTML);
      &rsPageletHTML.Fill("WHERE PTPPB_PAGELET_ID =:1", &strPageletID);
      &HTML.Value = &rsPageletHTML(1).PTPPB_PGLT_HTML.HTMLAREA.Value;
   End-If;

   If &HTML.Value <> "" Then
      %This.setSettingsComplete( True);
   End-If;


   &HTML.FieldType = &HTML.FIELDTYPE_LONGCHARACTER;
   &HTML.EditType = &HTML.EDITTYPE_NOTABLEEDIT;
   &HTML.Enabled = True;
   &HTML.Visible = True;
   &HTML.Required = True;
   &HTML.LongName = MsgGetText(219, 602, "Message Not Found");

   &HTML.setObjectToRefreshOnValueChange(%This);
end-method;

/**
  * Determines if any text/HTML has been set.  If so, the settins are marked complete.
  */
method processSettingsChange
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.processSettingsChange +/;

   Local string &strHTMLText;
   Local PTPPB_PAGELET:UTILITY:Setting &HTMLSetting;

   /* do nothing if no settings */
   If %This.Settings = Null Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return;
   End-If;

   &HTMLSetting = %This.Settings.getItemByID("PSRecord");
   /* do nothing if no queryname setting */
   If &HTMLSetting = Null Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return
   End-If;

   &strHTMLText = &HTMLSetting.Value;
   /* do nothing if the HTML is blank */
   If &strHTMLText = "" Then
      /* Mark the settings as incomplete */
      %This.setSettingsComplete( False);
      Return;
   End-If;

   /* Mark the settings as complete */
   %This.setSettingsComplete( True);

end-method;

/**
  * Clones this datasource
  *
  * @return Object exact duplicate of this HTMLDataSource
  */
method Clone
   /+ Returns Object +/
   /+ Extends/implements PTPPB_PAGELET:DataSource:DataSource.Clone +/
   Local CIBER_UI_FORMS:DataSource:PSRecordDataSource &NewDS = create CIBER_UI_FORMS:DataSource:PSRecordDataSource(%This.ID);
   &NewDS.PageletID = %This.PageletID;
   &NewDS.initializeSettings(%This.Settings.Clone());
   Return &NewDS;
end-method;
