   If All(&RS(1).GetRecord(1).GetField(1).Value) Then
      For &x = 1 To &RS.ActiveRowCount
         &retStr = &retStr | "<tr><th scope='row'></th><td>" | &RS(&x).GetRecord(1).GetField(Field.ATTACHUSERFILE).Value | "</td>";
         &retStr = &retStr | "<td><input type='button' value='View' onclick=''></td>";
         &retStr = &retStr | "<td><button class='btn btn-default' data-id='" | &RS(&x).GetRecord(1).GetField(Field.ATTACHUSERFILE).Value;
         &retStr = &retStr | "' data-toggle='modal' data-target='#confirm-delete'>Delete</button></td></tr>";

         /*
