Function IScript_deleteattach
   Local string &strParm = %Request.GetParameter("fname");

   Local string &retStr;
   Local number &x;
   Local Rowset &RS = CreateRowset(Record.IG_FILE_ATTACH);
   &RS.Fill("WHERE OPRID = :1 AND ATTACHSYSFILENAME = :2", %OperatorId, "IG_" | &strParm);

   If &RS(1).GetRecord(1).Delete() Then
      %Response.WriteLine("delete success!");
      delete_attachment("RECORD://IG_FILE_ATTDET/", "IG_" | &strParm, &ATTACHUSERFILE, &MESSAGE_LVL, &RETCODE);
   End-If;

   rem   %Response.WriteLine(&ATTACHUSERFILE | " | " | &MESSAGE_LVL | " | " | &RETCODE);

End-Function;

