         success: function (data) {  
            // The file is uploaded successfully...  
            $('.msg').text('File is uploaded.').show(); alert(data);
//$('.msg').html(data);
         },  
         error: function (data) {  
         // there was an error.  
         $('.msg').text('Some error in the request.').show();  
      }  
      });  
   });  
});
</script>

