function refreshAttachList() {
		$.ajax({
			url: 'http://192.168.5.144/psc/ps/EMPLOYEE/EMPL/s/WEBLIB_IG.ISCRIPT1.FieldFormula.IScript_refresh', 
			success: function(result){
				$('#myattachfiles').html(result); 
			}});
}

function deleteAttachment(f_element) {
		$.ajax({
			url: 'http://192.168.5.144/psc/ps/EMPLOYEE/EMPL/s/WEBLIB_IG.ISCRIPT1.FieldFormula.IScript_deleteattach?fname='+$(f_element).attr('filename'), 
			success: function(result){
				refreshAttachList();
			}});
}

